import React, { useState, useEffect } from 'react';
import { FaGraduationCap, FaComments, FaEnvelope, FaCog } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import { getCourses, getTestimonials, getContactRequests, getAllContent } from '../../services/contentService';

const Overview = () => {
  const [stats, setStats] = useState({
    courses: 0,
    testimonials: 0,
    contacts: 0,
    content: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [courses, testimonials, contacts, content] = await Promise.all([
          getCourses(),
          getTestimonials(),
          getContactRequests(),
          getAllContent()
        ]);

        setStats({
          courses: courses?.length || 0,
          testimonials: testimonials?.length || 0,
          contacts: contacts?.length || 0,
          content: content?.length || 0
        });
      } catch (error) {
        toast.error(error.message || 'Failed to fetch dashboard statistics');
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const statCards = [
    {
      title: 'Courses',
      value: stats.courses,
      icon: <FaGraduationCap className="text-3xl" />,
      path: '/admin/courses'
    },
    {
      title: 'Testimonials',
      value: stats.testimonials,
      icon: <FaComments className="text-3xl" />,
      path: '/admin/testimonials'
    },
    {
      title: 'Contact Requests',
      value: stats.contacts,
      icon: <FaEnvelope className="text-3xl" />,
      path: '/admin/contacts'
    },
    {
      title: 'Content Sections',
      value: stats.content,
      icon: <FaCog className="text-3xl" />,
      path: '/admin/content'
    }
  ];

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-bold text-gray-800">Dashboard Overview</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {statCards.map((card) => (
          <Link
            key={card.title}
            to={card.path}
            className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow"
          >
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-500 text-sm">{card.title}</p>
                <p className="text-2xl font-bold text-gray-800">{card.value}</p>
              </div>
              <div className="text-blue-500">{card.icon}</div>
            </div>
          </Link>
        ))}
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Link
            to="/admin/courses/new"
            className="bg-blue-50 text-blue-600 p-4 rounded-lg hover:bg-blue-100 transition-colors"
          >
            Add New Course
          </Link>
          <Link
            to="/admin/testimonials/new"
            className="bg-green-50 text-green-600 p-4 rounded-lg hover:bg-green-100 transition-colors"
          >
            Add New Testimonial
          </Link>
          <Link
            to="/admin/content"
            className="bg-purple-50 text-purple-600 p-4 rounded-lg hover:bg-purple-100 transition-colors"
          >
            Edit Content
          </Link>
          <Link
            to="/admin/contacts"
            className="bg-yellow-50 text-yellow-600 p-4 rounded-lg hover:bg-yellow-100 transition-colors"
          >
            View Contact Requests
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Overview;
