import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaPhone, FaEnvelope, FaMapMarkerAlt, FaClock } from 'react-icons/fa';
import ContactForm from '../components/contact/ContactForm';

const ContactPage = () => {
  const [formSubmitted, setFormSubmitted] = useState(false);

  const contactData = {
    hero: {
      title: 'Contact STEPS Education Limited',
      description: 'Get in touch with our team for personalized guidance on your educational journey. We are here to answer your questions and provide the support you need.'
    },
    contactInfo: {
      phone: {
        title: 'Phone',
        value: '+44-475-63228537'
      },
      email: {
        title: 'Email',
        value: 'info@stepseeducation.co.uk'
      },
      hours: {
        title: 'Business Hours',
        value: 'Monday - Friday: 9:00 AM - 5:00 PM'
      }
    },
    form: {
      title: 'Send Us a Message'
    },
    location: {
      title: 'Our Location',
      address: {
        title: 'Address',
        line1: 'City Gate House',
        line2: 'Suite 504',
        line3: 'London E7 9HZ'
      },
      mapEmbedUrl: 'https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2481.715284002221!2d0.0295985!3d51.5451175!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x0!2zNTHCsDMyJzQyLjQiTiAwwrAwMSc0Ni41IkU!5e0!3m2!1sen!2suk!4v1620305089776!5m2!1sen!2suk'
    },
    services: [
      'International Student Recruitment',
      'Documents Assessment',
      'University Selection',
      'Admission Process',
      'Visa Application',
      'Local Student Admissions',
      'Employability Services'
    ]
  };

  return (
    <div className="pt-16">
      {/* Hero Section */}
      <motion.section 
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="relative py-20"
      >
        <div className="absolute inset-0 bg-blue-900/90"></div>
        <div className="container mx-auto px-4 relative z-10">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-6">
            {contactData.hero.title}
          </h1>
          <p className="text-xl text-gray-200 max-w-3xl">
            {contactData.hero.description}
          </p>
        </div>
      </motion.section>

      {/* Contact Information */}
      <section className="py-20">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16">
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.1 }}
              viewport={{ once: true }}
              className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow"
            >
              <div className="flex items-center">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mr-4">
                  <FaPhone className="text-blue-600" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-gray-800">{contactData.contactInfo.phone.title}</h3>
                  <p className="text-gray-600">{contactData.contactInfo.phone.value}</p>
                </div>
              </div>
            </motion.div>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
              viewport={{ once: true }}
              className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow"
            >
              <div className="flex items-center">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mr-4">
                  <FaEnvelope className="text-blue-600" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-gray-800">{contactData.contactInfo.email.title}</h3>
                  <a href={`mailto:${contactData.contactInfo.email.value}`} className="text-blue-600 hover:underline">
                    {contactData.contactInfo.email.value}
                  </a>
                </div>
              </div>
            </motion.div>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.3 }}
              viewport={{ once: true }}
              className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow"
            >
              <div className="flex items-center">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mr-4">
                  <FaClock className="text-blue-600" />
                </div>
                <div>
                  <h3 className="text-lg font-semibold text-gray-800">{contactData.contactInfo.hours.title}</h3>
                  <p className="text-gray-600">{contactData.contactInfo.hours.value}</p>
                </div>
              </div>
            </motion.div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            {/* Contact Form */}
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
            >
              <h2 className="text-2xl font-bold text-blue-600 mb-6">{contactData.form.title}</h2>
              {formSubmitted ? (
                <div className="bg-green-100 text-green-700 p-6 rounded-lg">
                  <h3 className="text-xl font-bold mb-2">Thank You!</h3>
                  <p>Your message has been sent successfully. We'll get back to you shortly.</p>
                </div>
              ) : (
                <ContactForm 
                  services={contactData.services} 
                  onSubmitSuccess={() => setFormSubmitted(true)} 
                />
              )}
            </motion.div>

            {/* Map and Address */}
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
            >
              <h2 className="text-2xl font-bold text-blue-600 mb-6">{contactData.location.title}</h2>
              <div className="bg-white p-6 rounded-lg shadow-md mb-6">
                <div className="flex items-start">
                  <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mr-4 mt-1 flex-shrink-0">
                    <FaMapMarkerAlt className="text-blue-600" />
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-gray-800 mb-2">{contactData.location.address.title}</h3>
                    <p className="text-gray-600">
                      {contactData.location.address.line1}<br />
                      {contactData.location.address.line2}<br />
                      {contactData.location.address.line3}
                    </p>
                  </div>
                </div>
              </div>

              {/* Google Maps iframe */}
              <div className="rounded-lg overflow-hidden shadow-md">
                <iframe
                  src={contactData.location.mapEmbedUrl}
                  width="100%"
                  height="400"
                  style={{ border: 0 }}
                  allowFullScreen=""
                  loading="lazy"
                  referrerPolicy="no-referrer-when-downgrade"
                  title="Google Maps"
                ></iframe>
              </div>

              <div className="mt-6 p-4 bg-blue-50 rounded-lg">
                <p className="text-blue-800 text-sm">
                  <strong>Note:</strong> We are available for in-person consultations by appointment only. 
                  Please contact us via phone or email to schedule a meeting.
                </p>
              </div>
            </motion.div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default ContactPage;
