import api from './api';

// Public routes
export const submitContactRequest = async (contactData) => {
  try {
    const response = await api.post('/contacts', contactData);
    return response.data.contact;
  } catch (error) {
    console.error('Error submitting contact request:', error);
    throw error;
  }
};

// Admin routes
export const getContactRequests = async () => {
  try {
    const response = await api.get('/admin/contacts');
    return response.data.contacts || [];
  } catch (error) {
    console.error('Error fetching contact requests:', error);
    return [];
  }
};

export const getContactRequestById = async (id) => {
  try {
    const response = await api.get(`/admin/contacts/${id}`);
    return response.data.contact || null;
  } catch (error) {
    console.error('Error fetching contact request:', error);
    return null;
  }
};

export const updateContactRequest = async (id, status) => {
  try {
    const response = await api.put(`/admin/contacts/${id}`, { status });
    return response.data.contact;
  } catch (error) {
    console.error('Error updating contact request:', error);
    throw error;
  }
};

export const deleteContactRequest = async (id) => {
  try {
    await api.delete(`/admin/contacts/${id}`);
  } catch (error) {
    console.error('Error deleting contact request:', error);
    throw error;
  }
};
