import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { Layout } from './components/layout';
import { ROUTES } from './constants/routes';
import { motion, AnimatePresence } from 'framer-motion';

// Page imports
import Home from './pages/Home';
import ContactPage from './pages/Contact';
import AdmissionProcessPage from './pages/AdmissionProcess';
import CVJobsPortalPage from './pages/CVJobsPortal';
import ScholarshipsPage from './pages/Scholarships';
import BlogPage from './pages/Blog';

// Preloader Component
const Preloader = () => (
  <motion.div
    initial={{ opacity: 1 }}
    exit={{ opacity: 0 }}
    className="fixed inset-0 z-50 flex items-center justify-center bg-white"
  >
    <div className="text-center">
      <motion.div
        animate={{ 
          scale: [1, 1.2, 1],
          rotate: [0, 360]
        }}
        transition={{ 
          duration: 2,
          repeat: Infinity,
          ease: "easeInOut"
        }}
        className="w-32 h-32 mb-8"
      >
        <span className="text-4xl font-bold">
          <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-blue-800">STEPS</span>
          <span className="text-gray-700 font-normal">Education</span>
        </span>
      </motion.div>
      <div className="w-16 h-16 border-4 border-blue-600 border-t-transparent rounded-full animate-spin mx-auto"></div>
    </div>
  </motion.div>
);

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time and initialize data
    const initializeApp = async () => {
      await Promise.all([
        // Set mock data for storage
        setMockData(),
        // Simulate minimum loading time for UX
        new Promise(resolve => setTimeout(resolve, 2000))
      ]);
      setLoading(false);
    };

    initializeApp();
  }, []);

  const setMockData = async () => {
    // Only set if not already present
    if (!localStorage.getItem('aboutDatabase')) {
      localStorage.setItem('aboutDatabase', JSON.stringify({
        title: 'About STEPS Education Limited',
        description1: 'Established in 2020, STEPS Education Limited is committed to providing exceptional educational services to international students. Our mission is guided by our core values: Education | Training | Excellence.',
        description2: 'We aim to help only 50 students per intake to maintain our service quality, ensuring personalized attention and support throughout your educational journey. Our team of dedicated specialists is led by Mr. Mohsin, who brings years of expertise in international student recruitment and visa processing.',
        image: 'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?ixlib=rb-4.0.3',
        stats: [
          { value: '50', label: 'Students Per Intake', description: 'Limited intake for personalized attention' },
          { value: '98%', label: 'Success Rate', description: 'Admission success rate for our students' },
          { value: '3', label: 'Days', description: 'Document assessment guarantee' }
        ]
      }));
    }

    if (!localStorage.getItem('footerDatabase')) {
      localStorage.setItem('footerDatabase', JSON.stringify({
        companyInfo: {
          name: 'STEPS Education Limited',
          description: 'Your Dream, Our Job! Education | Training | Excellence',
          socialLinks: [
            { platform: 'facebook', url: 'https://facebook.com/stepseducation' },
            { platform: 'twitter', url: 'https://twitter.com/stepseducation' },
            { platform: 'instagram', url: 'https://instagram.com/stepseducation' },
            { platform: 'linkedin', url: 'https://linkedin.com/company/stepseducation' }
          ]
        },
        quickLinks: [
          { name: 'Home', url: '/' },
          { name: 'Admission Process', url: '/admission-process' },
          { name: 'CV & Jobs Portal', url: '/cv-jobs' },
          { name: 'Scholarships', url: '/scholarships' },
          { name: 'Blog', url: '/blog' },
          { name: 'Contact', url: '/contact' }
        ],
        services: [
          { name: 'International Student Recruitment', url: '/admission-process' },
          { name: 'Documents Assessment', url: '/admission-process' },
          { name: 'University Selection', url: '/admission-process' },
          { name: 'Visa Application Guidance', url: '/admission-process' },
          { name: 'Local Student Admissions', url: '/admission-process' },
          { name: 'Employability Services', url: '/cv-jobs' }
        ],
        contactInfo: {
          address: 'City Gate House, 246-250 Romford Road, Stratford, London E7 9HZ, Suite 504',
          phone: '+44-475-63228537',
          email: 'info@stepseeducation.co.uk'
        },
        copyright: 'Â© {year} STEPS Education Limited. All rights reserved.'
      }));
    }
  };

  return (
    <>
      <AnimatePresence>
        {loading && <Preloader />}
      </AnimatePresence>

      <Routes>
        {/* Public Routes */}
        <Route element={<Layout />}>
          <Route index element={<Home />} />
          <Route path={ROUTES.CONTACT} element={<ContactPage />} />
          <Route path={ROUTES.ADMISSION_PROCESS} element={<AdmissionProcessPage />} />
          <Route path={ROUTES.CV_JOBS} element={<CVJobsPortalPage />} />
          <Route path={ROUTES.SCHOLARSHIPS} element={<ScholarshipsPage />} />
          <Route path={ROUTES.BLOG} element={<BlogPage />} />
        </Route>
      </Routes>
    </>
  );
}

export default App;
