// Content Service
const ContentService = {
  // Get all content
  getAllContent: async () => {
    return contentDatabase;
  },
  // Get content by slug
  getContentBySlug: async slug => {
    const content = contentDatabase.find(item => item.slug === slug);
    if (!content) {
      throw new Error('Content not found');
    }
    return content;
  },
  // Create content
  createContent: async contentData => {
    const newContent = {
      _id: 'content-' + Date.now(),
      ...contentData,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };
    contentDatabase.push(newContent);
    saveContentDatabase();
    toast.success('Content created successfully');
    return newContent;
  },
  // Update content
  updateContent: async (id, contentData) => {
    const index = contentDatabase.findIndex(item => item._id === id);
    if (index === -1) {
      throw new Error('Content not found');
    }
    const updatedContent = {
      ...contentDatabase[index],
      ...contentData,
      updatedAt: new Date().toISOString()
    };
    contentDatabase[index] = updatedContent;
    saveContentDatabase();
    toast.success('Content updated successfully');
    return updatedContent;
  },
  // Delete content
  deleteContent: async id => {
    const index = contentDatabase.findIndex(item => item._id === id);
    if (index === -1) {
      throw new Error('Content not found');
    }
    contentDatabase.splice(index, 1);
    saveContentDatabase();
    toast.success('Content deleted successfully');
    return {
      success: true
    };
  },
  // Update content section
  updateContentSection: async (id, sectionName, sectionData) => {
    const content = contentDatabase.find(item => item._id === id);
    if (!content) {
      throw new Error('Content not found');
    }
    const sectionIndex = content.sections.findIndex(section => section.name === sectionName);
    if (sectionIndex === -1) {
      throw new Error('Section not found');
    }
    content.sections[sectionIndex] = {
      ...content.sections[sectionIndex],
      ...sectionData
    };
    content.updatedAt = new Date().toISOString();
    saveContentDatabase();
    toast.success('Section updated successfully');
    return content;
  },
  // Add section item
  addSectionItem: async (id, sectionName, itemData) => {
    const content = contentDatabase.find(item => item._id === id);
    if (!content) {
      throw new Error('Content not found');
    }
    const sectionIndex = content.sections.findIndex(section => section.name === sectionName);
    if (sectionIndex === -1) {
      throw new Error('Section not found');
    }
    const section = content.sections[sectionIndex];
    if (!section.isRepeatable) {
      throw new Error('This section does not support multiple items');
    }
    const newItem = {
      ...itemData,
      order: section.items ? section.items.length : 0
    };
    if (!section.items) {
      section.items = [];
    }
    section.items.push(newItem);
    content.updatedAt = new Date().toISOString();
    saveContentDatabase();
    toast.success('Item added successfully');
    return content;
  }
};

