// Countries Service
const CountriesService = {
  // Get all countries
  getCountries: async () => {
    return countriesDatabase;
  },

  // Get country by ID
  getCountryById: async (id) => {
    return countriesDatabase.find(country => country._id === id);
  },

  // Add new country
  addCountry: async (countryData) => {
    const newCountry = {
      _id: 'country-' + Date.now(),
      ...countryData,
      order: countriesDatabase.length + 1,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };

    countriesDatabase.push(newCountry);
    saveCountriesDatabase();
    toast.success('Country added successfully');
    return newCountry;
  },

  // Update country
  updateCountry: async (id, countryData) => {
    const index = countriesDatabase.findIndex(country => country._id === id);

    if (index === -1) {
      toast.error('Country not found');
      return null;
    }

    countriesDatabase[index] = {
      ...countriesDatabase[index],
      ...countryData,
      updatedAt: new Date().toISOString()
    };

    saveCountriesDatabase();
    toast.success('Country updated successfully');
    return countriesDatabase[index];
  },

  // Delete country
  deleteCountry: async (id) => {
    const initialLength = countriesDatabase.length;
    countriesDatabase = countriesDatabase.filter(country => country._id !== id);

    if (countriesDatabase.length === initialLength) {
      toast.error('Country not found');
      return false;
    }

    // Reorder remaining countries
    countriesDatabase = countriesDatabase.map((country, index) => ({
      ...country,
      order: index + 1
    }));

    saveCountriesDatabase();
    toast.success('Country deleted successfully');
    return true;
  },

  // Reorder countries
  reorderCountries: async (orderedIds) => {
    // Update order based on the provided array of IDs
    orderedIds.forEach((id, index) => {
      const countryIndex = countriesDatabase.findIndex(country => country._id === id);
      if (countryIndex !== -1) {
        countriesDatabase[countryIndex].order = index + 1;
      }
    });

    // Sort the array by the new order
    countriesDatabase.sort((a, b) => a.order - b.order);

    saveCountriesDatabase();
    toast.success('Countries reordered successfully');
    return countriesDatabase;
  }
};

