import React, { useState, useEffect } from 'react';
import { FooterService } from './AdminDataServices';
import { toast } from 'react-toastify';
import { FaPlus, FaTrash, FaEdit, FaSave, FaTimes } from 'react-icons/fa';

const FooterManagement = () => {
  const [loading, setLoading] = useState(true);
  const [footerData, setFooterData] = useState(null);
  const [activeTab, setActiveTab] = useState('company');

  useEffect(() => {
    const fetchFooterData = async () => {
      try {
        setLoading(true);
        const data = await FooterService.getFooterData();
        setFooterData(data);
      } catch (error) {
        console.error('Error fetching footer data:', error);
        toast.error('Failed to load footer data');
      } finally {
        setLoading(false);
      }
    };

    fetchFooterData();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!footerData) {
    return (
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
        <p>Failed to load footer data. Please try again later.</p>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <h2 className="text-2xl font-bold mb-6">Footer Management</h2>

      {/* Tabs */}
      <div className="flex border-b mb-6">
        <button
          className={`py-2 px-4 ${activeTab === 'company' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-500'}`}
          onClick={() => setActiveTab('company')}
        >
          Company Info
        </button>
        <button
          className={`py-2 px-4 ${activeTab === 'quickLinks' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-500'}`}
          onClick={() => setActiveTab('quickLinks')}
        >
          Quick Links
        </button>
        <button
          className={`py-2 px-4 ${activeTab === 'services' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-500'}`}
          onClick={() => setActiveTab('services')}
        >
          Services
        </button>
        <button
          className={`py-2 px-4 ${activeTab === 'contact' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-500'}`}
          onClick={() => setActiveTab('contact')}
        >
          Contact Info
        </button>
        <button
          className={`py-2 px-4 ${activeTab === 'copyright' ? 'border-b-2 border-blue-500 text-blue-500' : 'text-gray-500'}`}
          onClick={() => setActiveTab('copyright')}
        >
          Copyright
        </button>
      </div>

      {/* Tab Content */}
      <div className="mt-4">
        {activeTab === 'company' && <CompanyInfoTab footerData={footerData} setFooterData={setFooterData} />}
        {activeTab === 'quickLinks' && <QuickLinksTab footerData={footerData} setFooterData={setFooterData} />}
        {activeTab === 'services' && <ServicesTab footerData={footerData} setFooterData={setFooterData} />}
        {activeTab === 'contact' && <ContactInfoTab footerData={footerData} setFooterData={setFooterData} />}
        {activeTab === 'copyright' && <CopyrightTab footerData={footerData} setFooterData={setFooterData} />}
      </div>
    </div>
  );
};

// Company Info Tab
const CompanyInfoTab = ({ footerData, setFooterData }) => {
  const [companyInfo, setCompanyInfo] = useState({ ...footerData.companyInfo });
  const [editingSocial, setEditingSocial] = useState(null);
  const [newSocial, setNewSocial] = useState({ platform: '', url: '' });
  const [showAddSocial, setShowAddSocial] = useState(false);

  const handleCompanyInfoChange = (e) => {
    const { name, value } = e.target;
    setCompanyInfo({ ...companyInfo, [name]: value });
  };

  const handleSocialChange = (index, field, value) => {
    const updatedSocialLinks = [...companyInfo.socialLinks];
    updatedSocialLinks[index] = { ...updatedSocialLinks[index], [field]: value };
    setCompanyInfo({ ...companyInfo, socialLinks: updatedSocialLinks });
  };

  const handleNewSocialChange = (field, value) => {
    setNewSocial({ ...newSocial, [field]: value });
  };

  const addSocialLink = () => {
    if (!newSocial.platform || !newSocial.url) {
      toast.error('Please fill in all fields');
      return;
    }

    const updatedSocialLinks = [...companyInfo.socialLinks, newSocial];
    setCompanyInfo({ ...companyInfo, socialLinks: updatedSocialLinks });
    setNewSocial({ platform: '', url: '' });
    setShowAddSocial(false);
  };

  const removeSocialLink = (index) => {
    const updatedSocialLinks = companyInfo.socialLinks.filter((_, i) => i !== index);
    setCompanyInfo({ ...companyInfo, socialLinks: updatedSocialLinks });
  };

  const saveCompanyInfo = async () => {
    try {
      await FooterService.updateCompanyInfo(companyInfo);
      setFooterData({ ...footerData, companyInfo });
      toast.success('Company information updated successfully');
    } catch (error) {
      console.error('Error updating company info:', error);
      toast.error('Failed to update company information');
    }
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Company Information</h3>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Company Name
        </label>
        <input
          type="text"
          name="name"
          value={companyInfo.name}
          onChange={handleCompanyInfoChange}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Description
        </label>
        <textarea
          name="description"
          value={companyInfo.description}
          onChange={handleCompanyInfoChange}
          rows="3"
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        ></textarea>
      </div>

      <div className="mb-4">
        <div className="flex justify-between items-center mb-2">
          <label className="block text-gray-700 text-sm font-bold">
            Social Media Links
          </label>
          <button
            onClick={() => setShowAddSocial(true)}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded text-xs flex items-center"
            disabled={showAddSocial}
          >
            <FaPlus className="mr-1" /> Add New
          </button>
        </div>

        {showAddSocial && (
          <div className="bg-gray-100 p-3 rounded mb-3">
            <div className="flex flex-wrap -mx-2">
              <div className="px-2 w-1/3">
                <select
                  value={newSocial.platform}
                  onChange={(e) => handleNewSocialChange('platform', e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                >
                  <option value="">Select Platform</option>
                  <option value="facebook">Facebook</option>
                  <option value="twitter">Twitter</option>
                  <option value="instagram">Instagram</option>
                  <option value="linkedin">LinkedIn</option>
                </select>
              </div>
              <div className="px-2 w-1/2">
                <input
                  type="text"
                  placeholder="URL"
                  value={newSocial.url}
                  onChange={(e) => handleNewSocialChange('url', e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <div className="px-2 w-1/6 flex">
                <button
                  onClick={addSocialLink}
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-2 rounded mr-1"
                >
                  <FaSave />
                </button>
                <button
                  onClick={() => setShowAddSocial(false)}
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-2 rounded"
                >
                  <FaTimes />
                </button>
              </div>
            </div>
          </div>
        )}

        <div className="bg-gray-50 rounded border">
          {companyInfo.socialLinks.map((social, index) => (
            <div key={index} className="border-b last:border-b-0 p-3">
              {editingSocial === index ? (
                <div className="flex flex-wrap -mx-2">
                  <div className="px-2 w-1/3">
                    <select
                      value={social.platform}
                      onChange={(e) => handleSocialChange(index, 'platform', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    >
                      <option value="facebook">Facebook</option>
                      <option value="twitter">Twitter</option>
                      <option value="instagram">Instagram</option>
                      <option value="linkedin">LinkedIn</option>
                    </select>
                  </div>
                  <div className="px-2 w-1/2">
                    <input
                      type="text"
                      value={social.url}
                      onChange={(e) => handleSocialChange(index, 'url', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>
                  <div className="px-2 w-1/6 flex">
                    <button
                      onClick={() => setEditingSocial(null)}
                      className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-2 rounded mr-1"
                    >
                      <FaSave />
                    </button>
                    <button
                      onClick={() => removeSocialLink(index)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-2 rounded"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex justify-between items-center">
                  <div>
                    <span className="font-semibold capitalize">{social.platform}</span>: {social.url}
                  </div>
                  <button
                    onClick={() => setEditingSocial(index)}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <FaEdit />
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      <div className="mt-6">
        <button
          onClick={saveCompanyInfo}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Save Changes
        </button>
      </div>
    </div>
  );
};

// Quick Links Tab
const QuickLinksTab = ({ footerData, setFooterData }) => {
  const [quickLinks, setQuickLinks] = useState([...footerData.quickLinks]);
  const [editingLink, setEditingLink] = useState(null);
  const [newLink, setNewLink] = useState({ name: '', url: '' });
  const [showAddLink, setShowAddLink] = useState(false);

  const handleLinkChange = (index, field, value) => {
    const updatedLinks = [...quickLinks];
    updatedLinks[index] = { ...updatedLinks[index], [field]: value };
    setQuickLinks(updatedLinks);
  };

  const handleNewLinkChange = (field, value) => {
    setNewLink({ ...newLink, [field]: value });
  };

  const addLink = () => {
    if (!newLink.name || !newLink.url) {
      toast.error('Please fill in all fields');
      return;
    }

    setQuickLinks([...quickLinks, newLink]);
    setNewLink({ name: '', url: '' });
    setShowAddLink(false);
  };

  const removeLink = (index) => {
    const updatedLinks = quickLinks.filter((_, i) => i !== index);
    setQuickLinks(updatedLinks);
  };

  const saveQuickLinks = async () => {
    try {
      await FooterService.updateQuickLinks(quickLinks);
      setFooterData({ ...footerData, quickLinks });
      toast.success('Quick links updated successfully');
    } catch (error) {
      console.error('Error updating quick links:', error);
      toast.error('Failed to update quick links');
    }
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Quick Links</h3>

      <div className="mb-4">
        <div className="flex justify-between items-center mb-2">
          <label className="block text-gray-700 text-sm font-bold">
            Navigation Links
          </label>
          <button
            onClick={() => setShowAddLink(true)}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded text-xs flex items-center"
            disabled={showAddLink}
          >
            <FaPlus className="mr-1" /> Add New
          </button>
        </div>

        {showAddLink && (
          <div className="bg-gray-100 p-3 rounded mb-3">
            <div className="flex flex-wrap -mx-2">
              <div className="px-2 w-1/3">
                <input
                  type="text"
                  placeholder="Link Name"
                  value={newLink.name}
                  onChange={(e) => handleNewLinkChange('name', e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <div className="px-2 w-1/2">
                <input
                  type="text"
                  placeholder="URL"
                  value={newLink.url}
                  onChange={(e) => handleNewLinkChange('url', e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <div className="px-2 w-1/6 flex">
                <button
                  onClick={addLink}
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-2 rounded mr-1"
                >
                  <FaSave />
                </button>
                <button
                  onClick={() => setShowAddLink(false)}
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-2 rounded"
                >
                  <FaTimes />
                </button>
              </div>
            </div>
          </div>
        )}

        <div className="bg-gray-50 rounded border">
          {quickLinks.map((link, index) => (
            <div key={index} className="border-b last:border-b-0 p-3">
              {editingLink === index ? (
                <div className="flex flex-wrap -mx-2">
                  <div className="px-2 w-1/3">
                    <input
                      type="text"
                      value={link.name}
                      onChange={(e) => handleLinkChange(index, 'name', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>
                  <div className="px-2 w-1/2">
                    <input
                      type="text"
                      value={link.url}
                      onChange={(e) => handleLinkChange(index, 'url', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>
                  <div className="px-2 w-1/6 flex">
                    <button
                      onClick={() => setEditingLink(null)}
                      className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-2 rounded mr-1"
                    >
                      <FaSave />
                    </button>
                    <button
                      onClick={() => removeLink(index)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-2 rounded"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex justify-between items-center">
                  <div>
                    <span className="font-semibold">{link.name}</span>: {link.url}
                  </div>
                  <button
                    onClick={() => setEditingLink(index)}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <FaEdit />
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      <div className="mt-6">
        <button
          onClick={saveQuickLinks}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Save Changes
        </button>
      </div>
    </div>
  );
};

// Services Tab
const ServicesTab = ({ footerData, setFooterData }) => {
  const [services, setServices] = useState([...footerData.services]);
  const [editingService, setEditingService] = useState(null);
  const [newService, setNewService] = useState({ name: '', url: '' });
  const [showAddService, setShowAddService] = useState(false);

  const handleServiceChange = (index, field, value) => {
    const updatedServices = [...services];
    updatedServices[index] = { ...updatedServices[index], [field]: value };
    setServices(updatedServices);
  };

  const handleNewServiceChange = (field, value) => {
    setNewService({ ...newService, [field]: value });
  };

  const addService = () => {
    if (!newService.name || !newService.url) {
      toast.error('Please fill in all fields');
      return;
    }

    setServices([...services, newService]);
    setNewService({ name: '', url: '' });
    setShowAddService(false);
  };

  const removeService = (index) => {
    const updatedServices = services.filter((_, i) => i !== index);
    setServices(updatedServices);
  };

  const saveServices = async () => {
    try {
      await FooterService.updateServices(services);
      setFooterData({ ...footerData, services });
      toast.success('Services updated successfully');
    } catch (error) {
      console.error('Error updating services:', error);
      toast.error('Failed to update services');
    }
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Footer Services</h3>

      <div className="mb-4">
        <div className="flex justify-between items-center mb-2">
          <label className="block text-gray-700 text-sm font-bold">
            Service Links
          </label>
          <button
            onClick={() => setShowAddService(true)}
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded text-xs flex items-center"
            disabled={showAddService}
          >
            <FaPlus className="mr-1" /> Add New
          </button>
        </div>

        {showAddService && (
          <div className="bg-gray-100 p-3 rounded mb-3">
            <div className="flex flex-wrap -mx-2">
              <div className="px-2 w-1/3">
                <input
                  type="text"
                  placeholder="Service Name"
                  value={newService.name}
                  onChange={(e) => handleNewServiceChange('name', e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <div className="px-2 w-1/2">
                <input
                  type="text"
                  placeholder="URL"
                  value={newService.url}
                  onChange={(e) => handleNewServiceChange('url', e.target.value)}
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                />
              </div>
              <div className="px-2 w-1/6 flex">
                <button
                  onClick={addService}
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-2 rounded mr-1"
                >
                  <FaSave />
                </button>
                <button
                  onClick={() => setShowAddService(false)}
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-2 rounded"
                >
                  <FaTimes />
                </button>
              </div>
            </div>
          </div>
        )}

        <div className="bg-gray-50 rounded border">
          {services.map((service, index) => (
            <div key={index} className="border-b last:border-b-0 p-3">
              {editingService === index ? (
                <div className="flex flex-wrap -mx-2">
                  <div className="px-2 w-1/3">
                    <input
                      type="text"
                      value={service.name}
                      onChange={(e) => handleServiceChange(index, 'name', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>
                  <div className="px-2 w-1/2">
                    <input
                      type="text"
                      value={service.url}
                      onChange={(e) => handleServiceChange(index, 'url', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    />
                  </div>
                  <div className="px-2 w-1/6 flex">
                    <button
                      onClick={() => setEditingService(null)}
                      className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-2 rounded mr-1"
                    >
                      <FaSave />
                    </button>
                    <button
                      onClick={() => removeService(index)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-2 rounded"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </div>
              ) : (
                <div className="flex justify-between items-center">
                  <div>
                    <span className="font-semibold">{service.name}</span>: {service.url}
                  </div>
                  <button
                    onClick={() => setEditingService(index)}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <FaEdit />
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      <div className="mt-6">
        <button
          onClick={saveServices}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Save Changes
        </button>
      </div>
    </div>
  );
};

// Contact Info Tab
const ContactInfoTab = ({ footerData, setFooterData }) => {
  const [contactInfo, setContactInfo] = useState({ ...footerData.contactInfo });

  const handleContactInfoChange = (e) => {
    const { name, value } = e.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };

  const saveContactInfo = async () => {
    try {
      await FooterService.updateContactInfo(contactInfo);
      setFooterData({ ...footerData, contactInfo });
      toast.success('Contact information updated successfully');
    } catch (error) {
      console.error('Error updating contact info:', error);
      toast.error('Failed to update contact information');
    }
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Contact Information</h3>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Address
        </label>
        <input
          type="text"
          name="address"
          value={contactInfo.address}
          onChange={handleContactInfoChange}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Phone
        </label>
        <input
          type="text"
          name="phone"
          value={contactInfo.phone}
          onChange={handleContactInfoChange}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Email
        </label>
        <input
          type="email"
          name="email"
          value={contactInfo.email}
          onChange={handleContactInfoChange}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      <div className="mt-6">
        <button
          onClick={saveContactInfo}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Save Changes
        </button>
      </div>
    </div>
  );
};

// Copyright Tab
const CopyrightTab = ({ footerData, setFooterData }) => {
  const [copyright, setCopyright] = useState(footerData.copyright);

  const handleCopyrightChange = (e) => {
    setCopyright(e.target.value);
  };

  const saveCopyright = async () => {
    try {
      await FooterService.updateCopyright(copyright);
      setFooterData({ ...footerData, copyright });
      toast.success('Copyright updated successfully');
    } catch (error) {
      console.error('Error updating copyright:', error);
      toast.error('Failed to update copyright');
    }
  };

  return (
    <div>
      <h3 className="text-xl font-semibold mb-4">Copyright Information</h3>

      <div className="mb-4">
        <label className="block text-gray-700 text-sm font-bold mb-2">
          Copyright Text
        </label>
        <input
          type="text"
          value={copyright}
          onChange={handleCopyrightChange}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
        <p className="text-sm text-gray-500 mt-1">Use {'{year}'} to include the current year dynamically.</p>
      </div>

      <div className="mt-6">
        <button
          onClick={saveCopyright}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        >
          Save Changes
        </button>
      </div>
    </div>
  );
};

export default FooterManagement;
