import React, { useEffect, useState } from 'react';

const Preloader = ({ onLoadingChange }) => {
  const [isLoading, setIsLoading] = useState(true);

  // Notify parent component immediately when mounted
  useEffect(() => {
    onLoadingChange?.(true);
  }, []);

  useEffect(() => {
    // Simulate loading time
    const timer = setTimeout(() => {
      setIsLoading(false);
      onLoadingChange?.(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  if (!isLoading) return null;

  return (
    <div className="fixed inset-0 z-[9999] flex items-center justify-center bg-white">
      <div className="relative">
        {/* Logo */}
        <div className="text-4xl font-bold bg-gradient-to-r from-navy to-blue-500 bg-clip-text text-transparent mb-4 text-center">
          GradWay
        </div>

        {/* Loading Bar */}
        <div className="w-48 h-1 bg-gray-100 rounded-full overflow-hidden">
          <div className="w-full h-full bg-gradient-to-r from-navy via-blue-500 to-navy animate-gradient-x"></div>
        </div>
      </div>
    </div>
  );
};

export default Preloader;
