import React from 'react';
import { FaGraduationCap, FaPassport, FaUserTie, FaLanguage, FaHome, FaHandshake } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const services = [
  {
    icon: <FaGraduationCap className="w-8 h-8" />,
    title: "University Admissions",
    description: "Expert guidance in selecting and applying to top universities worldwide. We help you navigate the entire application process."
  },
  {
    icon: <FaPassport className="w-8 h-8" />,
    title: "Visa Guidance",
    description: "Comprehensive assistance with student visa applications, documentation, and interview preparation."
  },
  {
    icon: <FaUserTie className="w-8 h-8" />,
    title: "Career Counselling",
    description: "Professional guidance to help you make informed decisions about your academic and career path."
  },
  {
    icon: <FaLanguage className="w-8 h-8" />,
    title: "Language Training",
    description: "Specialized coaching for IELTS, TOEFL, and other language proficiency tests required for international admissions."
  },
  {
    icon: <FaHome className="w-8 h-8" />,
    title: "Accommodation Help",
    description: "Assistance in finding suitable accommodation options near your chosen university."
  },
  {
    icon: <FaHandshake className="w-8 h-8" />,
    title: "Post-Arrival Support",
    description: "Ongoing support and guidance after you arrive at your destination country."
  }
];

const Services = () => {
  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Our Services
          </h2>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Comprehensive support for your international education journey
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white p-8 rounded-lg shadow-sm hover:shadow-md transition-shadow duration-300"
            >
              <div className="text-navy mb-4">
                {service.icon}
              </div>
              <h3 className="text-xl font-semibold text-navy mb-3">
                {service.title}
              </h3>
              <p className="text-gray-600 mb-4">
                {service.description}
              </p>
              <Link
                to="/services"
                className="text-navy font-medium inline-flex items-center hover:text-navy-light transition-colors duration-300"
              >
                Learn More
                <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Services;
