import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { FaGraduationCap, FaPassport, FaUniversity, FaLanguage, FaCheck } from 'react-icons/fa';
import useTheme from '../hooks/useTheme';

// Map icon names to actual icon components
const iconMap = {
  FaGraduationCap: <FaGraduationCap className="w-12 h-12 text-indigo-600" />,
  FaPassport: <FaPassport className="w-12 h-12 text-indigo-600" />,
  FaUniversity: <FaUniversity className="w-12 h-12 text-indigo-600" />,
  FaLanguage: <FaLanguage className="w-12 h-12 text-indigo-600" />
};

// Default services data if nothing is in localStorage
const defaultServices = [
  {
    _id: 'service-1',
    id: "study-abroad",
    iconName: "FaGraduationCap",
    title: "Study Abroad Consultation",
    description: "Expert guidance on choosing the right country, university, and program for your academic goals.",
    features: [
      "Country and university selection",
      "Program matching based on your profile",
      "Scholarship and funding guidance",
      "Cultural adaptation support"
    ],
    order: 1
  },
  {
    _id: 'service-2',
    id: "visa",
    iconName: "FaPassport",
    title: "Visa Application Guidance",
    description: "Comprehensive support for visa applications, documentation, and interview preparation.",
    features: [
      "Document checklist and preparation",
      "Application form assistance",
      "Interview preparation",
      "Post-approval guidance"
    ],
    order: 2
  },
  {
    _id: 'service-3',
    id: "admission",
    iconName: "FaUniversity",
    title: "University Admission Support",
    description: "Personalized assistance with university applications, essays, and admission requirements.",
    features: [
      "Application strategy development",
      "Personal statement review",
      "Recommendation letter guidance",
      "Portfolio preparation"
    ],
    order: 3
  },
  {
    _id: 'service-4',
    id: "language",
    iconName: "FaLanguage",
    title: "Language Training",
    description: "Specialized language courses to help you meet the language requirements of your chosen program.",
    features: [
      "IELTS/TOEFL preparation",
      "Academic writing skills",
      "Speaking and listening practice",
      "Test-taking strategies"
    ],
    order: 4
  }
];

const ServicesPage = () => {
  const theme = useTheme();
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.6 } }
  };

  // Fetch services data from localStorage
  useEffect(() => {
    const fetchServicesPageData = () => {
      try {
        setLoading(true);
        // Get services page data from localStorage
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));

        if (storedServicesPage) {
          setServices(storedServicesPage.services || []);
        } else {
          // Use default data if nothing is in localStorage
          setServices(defaultServices);
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching services page data:', err);
        setError('Failed to load services page data');
        setServices(defaultServices);
        setLoading(false);
      }
    };

    // Initial fetch
    fetchServicesPageData();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        console.log('Services page database updated, refreshing...');
        fetchServicesPageData();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  // Sort services by order property
  const sortedServices = [...services].sort((a, b) => (a.order || 0) - (b.order || 0));

  // Get hero section data from localStorage
  const [heroData, setHeroData] = useState({
    title: "Our Services",
    description: "Comprehensive support for your educational journey, from initial consultation to successful admission."
  });

  // Fetch hero data
  useEffect(() => {
    const fetchHeroData = () => {
      try {
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));
        if (storedServicesPage && storedServicesPage.hero) {
          setHeroData(storedServicesPage.hero);
        }
      } catch (err) {
        console.error('Error fetching hero data:', err);
      }
    };

    fetchHeroData();

    // Listen for changes
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        fetchHeroData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Get process section data from localStorage
  const [processData, setProcessData] = useState({
    title: "Our Process",
    description: "We follow a structured approach to ensure your success at every step.",
    steps: [
      { number: 1, title: "Initial Consultation", description: "Understanding your goals and requirements" },
      { number: 2, title: "Profile Assessment", description: "Evaluating your academic background and preferences" },
      { number: 3, title: "Application Support", description: "Guiding you through the application process" },
      { number: 4, title: "Post-Admission", description: "Supporting your transition to university life" }
    ]
  });

  // Fetch process data
  useEffect(() => {
    const fetchProcessData = () => {
      try {
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));
        if (storedServicesPage && storedServicesPage.process) {
          setProcessData(storedServicesPage.process);
        }
      } catch (err) {
        console.error('Error fetching process data:', err);
      }
    };

    fetchProcessData();

    // Listen for changes
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        fetchProcessData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Get CTA section data from localStorage
  const [ctaData, setCtaData] = useState({
    title: "Ready to Start Your Journey?",
    description: "Contact us today to learn more about our services and how we can help you achieve your academic goals.",
    buttonText: "Get in Touch",
    buttonLink: "/contact"
  });

  // Fetch CTA data
  useEffect(() => {
    const fetchCtaData = () => {
      try {
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));
        if (storedServicesPage && storedServicesPage.cta) {
          setCtaData(storedServicesPage.cta);
        }
      } catch (err) {
        console.error('Error fetching CTA data:', err);
      }
    };

    fetchCtaData();

    // Listen for changes
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        fetchCtaData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  if (loading) {
    return (
      <div className="pt-16 flex justify-center items-center min-h-screen">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="pt-16 flex justify-center items-center min-h-screen">
        <div className="text-center">
          <p className="text-red-500 text-xl">{error}</p>
          <p className="mt-4">Please try refreshing the page.</p>
        </div>
      </div>
    );
  }

  return (
    <motion.div 
      className="pt-16"
      initial="hidden"
      animate="visible"
      variants={containerVariants}
    >
      {/* Hero Section */}
      <section className="relative py-28 overflow-hidden">
        {/* Background Gradient & Decoration */}
        <div className="absolute inset-0 bg-gradient-to-br from-indigo-900/90 via-blue-800/80 to-purple-800/70"></div>

        {/* Decorative Elements */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute -right-24 top-1/4 w-64 h-64 rounded-full bg-indigo-400 opacity-20 blur-3xl"></div>
          <div className="absolute left-1/4 bottom-0 w-80 h-80 rounded-full bg-blue-300 opacity-20 blur-3xl"></div>
          <div className="pattern-dots opacity-10 absolute inset-0"></div>
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <motion.div 
            className="max-w-3xl mx-auto text-center"
            variants={itemVariants}
          >
            <span className="inline-block px-4 py-1.5 bg-white/10 backdrop-blur-sm rounded-full text-white text-sm font-medium mb-6">
              Expert Education Services
            </span>

            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-6 leading-tight">
              <span className="text-gradient-light">
                {heroData.title}
              </span>
            </h1>

            <p className="text-xl text-white/90 max-w-3xl mx-auto">
              {heroData.description}
            </p>
          </motion.div>
        </div>
      </section>

      {/* Services Section */}
      <section className="py-24 relative overflow-hidden">
        {/* Background decoration */}
        <div className="absolute inset-0 opacity-5 pointer-events-none overflow-hidden">
          <div className="absolute -left-32 -top-32 w-96 h-96 rounded-full bg-indigo-300"></div>
          <div className="absolute right-0 top-1/3 w-64 h-64 rounded-full bg-blue-300"></div>
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <motion.div 
            className="text-center mb-16"
            variants={itemVariants}
          >
            <span className="inline-block px-3 py-1.5 bg-indigo-100 text-indigo-700 rounded-full text-sm font-medium mb-4">
              What We Offer
            </span>
            <h2 className="text-3xl md:text-4xl font-bold text-gradient mb-4">
              Our Services
            </h2>
            <div className="w-20 h-1.5 bg-gradient-to-r from-indigo-600 to-blue-500 rounded-full mx-auto mb-6"></div>
            <p className="text-gray-600 max-w-2xl mx-auto text-lg">
              We provide comprehensive support for international students at every step of their educational journey
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-10">
            {sortedServices.map((service, index) => (
              <motion.div
                key={service._id || index}
                id={service.id}
                className="bg-white p-8 rounded-2xl shadow-xl border border-gray-100 hover:shadow-2xl hover:border-indigo-100 transition-all duration-300 transform hover:-translate-y-2"
                variants={itemVariants}
              >
                <div className="w-16 h-16 rounded-xl bg-indigo-100 flex items-center justify-center mb-6">
                  {iconMap[service.iconName] || <FaGraduationCap className="w-8 h-8 text-indigo-600" />}
                </div>
                <h3 className="text-2xl font-bold text-indigo-700 mb-4">{service.title}</h3>
                <p className="text-gray-600 mb-6">{service.description}</p>
                <div className="space-y-3">
                  {service.features && service.features.map((feature, i) => (
                    <div key={i} className="flex items-start">
                      <div className="mr-3 mt-1 bg-indigo-100 p-1 rounded-full">
                        <FaCheck className="w-3 h-3 text-indigo-600" />
                      </div>
                      <p className="text-gray-700">{feature}</p>
                    </div>
                  ))}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Process Section */}
      <section className="py-24 bg-gradient-to-b from-indigo-50 to-white relative overflow-hidden">
        <div className="absolute inset-0 pattern-dots opacity-5"></div>

        <div className="container mx-auto px-4 relative z-10">
          <motion.div 
            className="text-center mb-16"
            variants={itemVariants}
          >
            <span className="inline-block px-3 py-1.5 bg-indigo-100 text-indigo-700 rounded-full text-sm font-medium mb-4">
              How We Work
            </span>
            <h2 className="text-3xl md:text-4xl font-bold text-gradient mb-4">
              {processData.title}
            </h2>
            <div className="w-20 h-1.5 bg-gradient-to-r from-indigo-600 to-blue-500 rounded-full mx-auto mb-6"></div>
            <p className="text-gray-600 max-w-2xl mx-auto text-lg">
              {processData.description}
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {processData.steps.map((step, index) => (
              <motion.div 
                key={index}
                className="bg-white p-8 rounded-2xl shadow-xl border border-gray-100 hover:shadow-2xl hover:border-indigo-100 transition-all duration-300 relative"
                variants={itemVariants}
              >
                <div className="absolute -top-5 -left-5 w-16 h-16 rounded-full bg-gradient-to-r from-indigo-600 to-blue-500 flex items-center justify-center shadow-lg">
                  <span className="text-white text-xl font-bold">{step.number}</span>
                </div>
                <div className="pt-8">
                  <h3 className="text-xl font-bold text-indigo-700 mb-3">{step.title}</h3>
                  <p className="text-gray-600">{step.description}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-24 relative overflow-hidden">
        {/* Background */}
        <div className="absolute inset-0 bg-gradient-to-r from-indigo-600 to-blue-500"></div>

        {/* Decorative elements */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute -right-40 -top-40 w-96 h-96 rounded-full bg-purple-400 opacity-20 blur-3xl"></div>
          <div className="absolute -left-20 top-1/3 w-72 h-72 rounded-full bg-blue-300 opacity-20 blur-3xl"></div>
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <div className="max-w-4xl mx-auto bg-white/10 backdrop-blur-lg rounded-3xl p-12 border border-white/20 shadow-2xl">
            <motion.div 
              className="text-center"
              variants={itemVariants}
            >
              <h2 className="text-3xl md:text-4xl font-bold text-white mb-6">
                {ctaData.title}
              </h2>
              <p className="text-xl text-white/90 mb-10 max-w-2xl mx-auto">
                {ctaData.description}
              </p>
              <Link
                to={ctaData.buttonLink}
                className="px-8 py-4 bg-white text-indigo-600 font-semibold rounded-full hover:bg-gray-100 transition-all duration-300 shadow-xl shadow-indigo-900/20 transform hover:-translate-y-1 text-center"
              >
                {ctaData.buttonText}
              </Link>
            </motion.div>
          </div>
        </div>
      </section>
    </motion.div>
  );
};

export default ServicesPage;
