import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Hero = ({ content }) => {
  // Find title, subtitle and description fields from content
  const getFieldValue = (fieldName) => {
    if (!content || !content.fields) return '';
    const field = content.fields.find(f => f.name === fieldName);
    return field ? field.value : '';
  };

  const title = getFieldValue('title');
  const subtitle = getFieldValue('subtitle');
  const description = getFieldValue('description');
  const backgroundImage = getFieldValue('backgroundImage');

  return (
    <section className="relative min-h-screen flex items-center overflow-hidden pt-32 pb-20">
      {/* Background with parallax effect */}
      <motion.div 
        initial={{ scale: 1.1 }}
        animate={{ scale: 1 }}
        transition={{ duration: 1.5 }}
        className="absolute inset-0"
      >
        <img
          src={backgroundImage || "https://images.unsplash.com/photo-1522202176988-66273c2fd55f?ixlib=rb-4.0.3"}
          alt="International students"
          className="w-full h-full object-cover"
          loading="eager"
        />
        <div className="absolute inset-0 bg-gradient-to-br from-indigo-900/90 via-blue-800/70 to-purple-800/60"></div>
      </motion.div>

      {/* Animated particles/shapes */}
      <div className="absolute inset-0 overflow-hidden">
        {[...Array(5)].map((_, i) => (
          <motion.div 
            key={i}
            className="absolute rounded-full bg-white/10 backdrop-blur-sm"
            style={{
              width: Math.random() * 200 + 50,
              height: Math.random() * 200 + 50,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
            }}
            animate={{
              x: [0, Math.random() * 40 - 20],
              y: [0, Math.random() * 40 - 20],
            }}
            transition={{
              repeat: Infinity,
              repeatType: "reverse",
              duration: Math.random() * 10 + 10,
            }}
          />
        ))}
      </div>

      {/* Content */}
      <div className="container mx-auto px-4 relative z-10">
        {/* Main content */}
        <motion.div 
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          className="max-w-3xl"
        >
          <motion.span 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3, duration: 0.6 }}
            className="inline-block px-4 py-1 bg-white/10 backdrop-blur-md rounded-full text-indigo-100 text-sm font-medium mb-6"
          >
            Trusted Education Consultancy Since 2020
          </motion.span>

          <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold text-white mb-4 md:mb-6 leading-tight">
            <motion.span 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.4, duration: 0.6 }}
              className="block bg-clip-text text-transparent bg-gradient-to-r from-white via-blue-200 to-indigo-100"
            >
              WE EXIST TO
            </motion.span>
            <motion.span 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.6, duration: 0.6 }}
              className="block bg-clip-text text-transparent bg-gradient-to-r from-white via-blue-200 to-indigo-100"
            >
              HELP YOU
            </motion.span>
            <motion.span 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.8, duration: 0.6 }}
              className="block bg-clip-text text-transparent bg-gradient-to-r from-white via-blue-200 to-indigo-100"
            >
              ACHIEVE MORE
            </motion.span>
          </h1>

          <motion.p 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1, duration: 0.6 }}
            className="text-xl sm:text-2xl md:text-3xl text-indigo-200 mb-2 md:mb-3 font-semibold"
          >
            YOUR DREAM OUR JOB!
          </motion.p>

          <motion.p 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.2, duration: 0.6 }}
            className="text-base sm:text-lg text-gray-200 mb-8 md:mb-10 max-w-2xl"
          >
            {description || "STEPS Education Limited provides expert guidance for international education since 2020. We focus on quality service with a limited intake of 50 students per batch."}
          </motion.p>

          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 1.4, duration: 0.6 }}
            className="flex flex-wrap gap-4"
          >
            <Link
              to="/contact"
              className="inline-block px-6 sm:px-8 py-4 bg-gradient-to-r from-indigo-600 to-blue-500 text-white font-semibold rounded-full hover:from-indigo-700 hover:to-blue-600 shadow-lg shadow-indigo-500/30 transition-all duration-300 text-center transform hover:-translate-y-1"
            >
              Book a Consultation
            </Link>

            <Link
              to="/admission-process"
              className="inline-block px-6 sm:px-8 py-4 bg-white/10 backdrop-blur-md border border-white/30 text-white font-semibold rounded-full hover:bg-white/20 transition-all duration-300 text-center transform hover:-translate-y-1"
            >
              Our Process
            </Link>
          </motion.div>
        </motion.div>

        {/* Features */}
        <motion.div 
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 1.8, duration: 0.8 }}
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4 md:gap-6 lg:gap-8 mt-12 md:mt-16 lg:mt-20"
        >
          <Feature 
            icon="🎓" 
            title="50 Students Per Intake" 
            description="Limited intake to ensure quality education"
          />
          <Feature 
            icon="✈️" 
            title="Tier 4 Student Visa" 
            description="Specialists in student visa applications"
          />
          <Feature 
            icon="⏱️" 
            title="3-Day Assessment" 
            description="Quick document evaluation process"
          />
          <Feature 
            icon="🏆" 
            title="Best Course Selection" 
            description="Personalized education planning"
          />
        </motion.div>
      </div>
    </section>
  );
};

const Feature = ({ icon, title, description }) => (
  <motion.div 
    whileHover={{ scale: 1.05, y: -5 }}
    className="bg-white/10 backdrop-blur-md p-6 rounded-2xl border border-white/20 transition-all duration-300 hover:bg-white/20 hover:shadow-lg hover:shadow-indigo-500/20"
  >
    <div className="text-3xl mb-4">{icon}</div>
    <h3 className="text-white font-semibold text-lg mb-2">{title}</h3>
    <p className="text-indigo-200 text-sm">{description}</p>
  </motion.div>
);

export default Hero;
