import React, { useState, useEffect } from 'react';
import { FaEdit } from 'react-icons/fa';

const Hero = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [heroData, setHeroData] = useState({
    title: '',
    subtitle: '',
    description: '',
    primaryButtonText: '',
    primaryButtonLink: '',
    secondaryButtonText: '',
    secondaryButtonLink: '',
    backgroundImage: ''
  });

  useEffect(() => {
    fetchHeroData();
  }, []);

  const fetchHeroData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/admin/hero', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (data.success) {
        setHeroData(data.hero);
      }
    } catch (error) {
      console.error('Error fetching hero data:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/admin/hero', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(heroData)
      });

      const data = await response.json();
      if (data.success) {
        setIsModalOpen(false);
        fetchHeroData();
      }
    } catch (error) {
      console.error('Error updating hero section:', error);
    }
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-semibold text-gray-900">Hero Section Management</h2>
        <button
          onClick={() => setIsModalOpen(true)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg flex items-center space-x-2 hover:bg-blue-700"
        >
          <FaEdit />
          <span>Edit Hero Section</span>
        </button>
      </div>

      {/* Hero Preview */}
      <div className="bg-white rounded-lg shadow overflow-hidden p-6">
        <div className="relative h-64 mb-6">
          <img
            src={heroData.backgroundImage || 'https://via.placeholder.com/1920x1080'}
            alt="Hero Background"
            className="w-full h-full object-cover rounded-lg"
          />
        </div>
        <div className="space-y-4">
          <div>
            <h3 className="text-lg font-medium text-gray-900">Title</h3>
            <p className="mt-1 text-gray-600">{heroData.title}</p>
          </div>
          <div>
            <h3 className="text-lg font-medium text-gray-900">Subtitle</h3>
            <p className="mt-1 text-gray-600">{heroData.subtitle}</p>
          </div>
          <div>
            <h3 className="text-lg font-medium text-gray-900">Description</h3>
            <p className="mt-1 text-gray-600">{heroData.description}</p>
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <h3 className="text-lg font-medium text-gray-900">Primary Button</h3>
              <p className="mt-1 text-gray-600">{heroData.primaryButtonText}</p>
              <p className="text-sm text-gray-500">Links to: {heroData.primaryButtonLink}</p>
            </div>
            <div>
              <h3 className="text-lg font-medium text-gray-900">Secondary Button</h3>
              <p className="mt-1 text-gray-600">{heroData.secondaryButtonText}</p>
              <p className="text-sm text-gray-500">Links to: {heroData.secondaryButtonLink}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Edit Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-[600px] shadow-lg rounded-md bg-white">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium">Edit Hero Section</h3>
              <button
                onClick={() => setIsModalOpen(false)}
                className="text-gray-400 hover:text-gray-500"
              >
                ×
              </button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Title</label>
                  <input
                    type="text"
                    value={heroData.title}
                    onChange={(e) => setHeroData({ ...heroData, title: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Subtitle</label>
                  <input
                    type="text"
                    value={heroData.subtitle}
                    onChange={(e) => setHeroData({ ...heroData, subtitle: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Description</label>
                  <textarea
                    value={heroData.description}
                    onChange={(e) => setHeroData({ ...heroData, description: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    rows="4"
                    required
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Primary Button Text</label>
                    <input
                      type="text"
                      value={heroData.primaryButtonText}
                      onChange={(e) => setHeroData({ ...heroData, primaryButtonText: e.target.value })}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Primary Button Link</label>
                    <input
                      type="text"
                      value={heroData.primaryButtonLink}
                      onChange={(e) => setHeroData({ ...heroData, primaryButtonLink: e.target.value })}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                      required
                    />
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Secondary Button Text</label>
                    <input
                      type="text"
                      value={heroData.secondaryButtonText}
                      onChange={(e) => setHeroData({ ...heroData, secondaryButtonText: e.target.value })}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Secondary Button Link</label>
                    <input
                      type="text"
                      value={heroData.secondaryButtonLink}
                      onChange={(e) => setHeroData({ ...heroData, secondaryButtonLink: e.target.value })}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                      required
                    />
                  </div>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Background Image URL</label>
                  <input
                    type="url"
                    value={heroData.backgroundImage}
                    onChange={(e) => setHeroData({ ...heroData, backgroundImage: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    required
                  />
                </div>
              </div>
              <div className="mt-5">
                <button
                  type="submit"
                  className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-blue-600 text-base font-medium text-white hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  Update Hero Section
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Hero;
