import React from "react";
import { motion } from "framer-motion";
import { FaBriefcase, FaFileAlt, FaUserTie, FaSearch, FaRegLightbulb, FaRegClipboard, FaRegHandshake, FaRegCheckCircle } from "react-icons/fa";
import { useTheme } from "../context/ThemeContext";

const CVJobsPortal = () => {
  const { isDarkMode } = useTheme();

  const pageInfo = {
    title: "CV & Jobs Portal",
    description: "Elevate your career with our professional CV writing and job placement services. Our expert team helps you craft standout CVs and connect with top employers in your field.",
  };

  const services = [
    {
      icon: <FaFileAlt />,
      title: "Professional CV Writing",
      description:
        "Our team of experienced CV writers will create a professionally designed CV that highlights your skills and experience to make you stand out from the competition.",
    },
    {
      icon: <FaUserTie />,
      title: "Academic CV Service",
      description:
        "Specialized CV development for academic positions, highlighting research accomplishments, publications, and educational credentials in the format preferred by universities and research institutions.",
    },
    {
      icon: <FaRegLightbulb />,
      title: "Interview Preparation",
      description:
        "We provide comprehensive interview coaching sessions to help you confidently navigate common questions and present yourself as the ideal candidate.",
    },
    {
      icon: <FaSearch />,
      title: "Job Search Assistance",
      description:
        "Access our extensive network of employer connections and job opportunities. We'll help match your skills with appropriate positions and guide you through the application process.",
    },
  ];

  const stats = [
    { value: "94%", label: "Success Rate" },
    { value: "1,200+", label: "Jobs Secured" },
    { value: "500+", label: "Partner Companies" },
    { value: "4.8/5", label: "Client Satisfaction" },
  ];

  const processSteps = [
    {
      icon: <FaRegClipboard />,
      title: "Initial Consultation",
      description:
        "We begin with a detailed consultation to understand your career objectives, experience, and the positions you're targeting. This helps us tailor our approach to your specific needs.",
    },
    {
      icon: <FaFileAlt />,
      title: "CV Development",
      description:
        "Our experts craft a compelling CV that highlights your strengths and achievements, formatted according to industry standards and optimized with keywords for applicant tracking systems.",
    },
    {
      icon: <FaRegHandshake />,
      title: "Job Matching",
      description:
        "We leverage our extensive employer network to identify suitable positions that align with your skills and career goals, providing personalized job recommendations.",
    },
    {
      icon: <FaRegCheckCircle />,
      title: "Interview Success",
      description:
        "With our interview preparation services, we equip you with strategies to effectively communicate your value proposition and navigate challenging questions with confidence.",
    },
  ];

  const testimonials = [
    {
      quote:
        "The CV services team transformed my resume completely. Within two weeks of using my new CV, I secured three interviews and received a job offer with a 15% higher salary than my previous position.",
      author: "Sarah J., Marketing Professional",
    },
    {
      quote:
        "As an international student, I struggled to format my CV for the UK job market. The academic CV service helped me highlight my research experience appropriately, and I've now secured a position at a leading university.",
      author: "Dr. Rahul P., Research Fellow",
    },
    {
      quote:
        "The interview coaching was invaluable. I felt so much more confident and prepared, and it definitely showed during my interviews. I received offers from two top companies in my field.",
      author: "Michael T., Software Engineer",
    },
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
      },
    },
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.5 },
    },
  };

  return (
    <div className={`min-h-screen pt-24 ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gradient-to-b from-blue-50 to-white text-gray-800'}`}>
      {/* Hero Section */}
      <section className="container mx-auto px-4 py-12">
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="text-center max-w-4xl mx-auto"
        >
          <h1 className={`text-4xl md:text-5xl font-bold mb-6 ${isDarkMode ? 'text-white' : 'bg-gradient-to-r from-blue-700 to-purple-600 bg-clip-text text-transparent'}`}>
            {pageInfo.title}
