            <a
              href="#contact"
              className={`px-8 py-3 rounded-lg font-medium transition-all ${
                isDarkMode 
                  ? 'bg-gray-700 hover:bg-gray-600 text-white' 
                  : 'bg-white hover:bg-gray-100 text-gray-800 border border-gray-300'
              }`}
            >
              Get Started
            </a>
          </div>
        </motion.div>
      </section>

