              <div className="mb-4">
                <svg className={`w-8 h-8 ${isDarkMode ? 'text-blue-400' : 'text-blue-500'}`} fill="currentColor" viewBox="0 0 24 24">
                  <path d="M14.017 21v-7.391c0-5.704 3.731-9.57 8.983-10.609l.995 2.151c-2.432.917-3.995 3.638-3.995 5.849h4v10h-9.983zm-14.017 0v-7.391c0-5.704 3.748-9.57 9-10.609l.996 2.151c-2.433.917-3.996 3.638-3.996 5.849h3.983v10h-9.983z" />
                </svg>
              </div>
              <p className={`mb-4 ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                {testimonial.quote}
              </p>
              <p className={`font-semibold ${isDarkMode ? 'text-blue-400' : 'text-blue-600'}`}>
                {testimonial.author}
              </p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* CTA Section */}
      <section className={`py-16 ${isDarkMode ? 'bg-gray-800' : 'bg-gradient-to-r from-blue-500 to-purple-600'}`}>
        <div className="container mx-auto px-4 text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5 }}
            className="max-w-3xl mx-auto"
          >
            <h2 className={`text-3xl md:text-4xl font-bold mb-6 ${isDarkMode ? 'text-white' : 'text-white'}`}>
              Ready to Advance Your Career?
            </h2>
            <p className={`text-lg mb-8 ${isDarkMode ? 'text-gray-300' : 'text-white opacity-90'}`}>
              Take the first step toward your next career opportunity. Our expert team is ready to help you succeed.
            </p>
            <div className="flex flex-wrap justify-center gap-4">
              <a
                href="/contact"
                className={`px-8 py-3 rounded-lg font-medium transition-all ${
                  isDarkMode 
                    ? 'bg-blue-600 hover:bg-blue-700 text-white' 
                    : 'bg-white hover:bg-gray-100 text-gray-800'
                }`}
              >
                Get Started
              </a>
              <a
                href="#samples"
                className={`px-8 py-3 rounded-lg font-medium transition-all ${
                  isDarkMode 
                    ? 'bg-gray-700 hover:bg-gray-600 text-white border border-gray-600' 
                    : 'bg-transparent hover:bg-white/10 text-white border border-white'
                }`}
              >
                View Sample CVs
              </a>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default CVJobsPortal;
