import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import AdmissionProcess from '../components/services/AdmissionProcess';
import { FaFileAlt, FaCheckCircle, FaCalendarCheck } from 'react-icons/fa';
import useTheme from '../hooks/useTheme';

const AdmissionProcessPage = () => {
  const { isDarkMode } = useTheme();
  
  const pageInfo = {
    title: 'Our Admission Process',
    description: 'At STEPS Education Limited, we provide a streamlined 6-step admission process to ensure your educational journey is smooth and successful. From document assessment to enrollment, we offer comprehensive support at every stage.'
  };

  const additionalInfo = [
    {
      title: 'Required Documents',
      description: 'Passport, academic transcripts/certificates, English language proficiency test results, personal statement/SOP, references/recommendation letters, and CV',
      icon: <FaFileAlt className="text-indigo-600 text-2xl" />,
      emoji: 'üìÑ'
    },
    {
      title: 'Fast Turnaround Times',
      description: 'Document assessment within 3 days, university application processing within 2 weeks, CAS issuance typically within 2-4 weeks after university acceptance',
      icon: <FaCheckCircle className="text-indigo-600 text-2xl" />,
      emoji: '‚è±Ô∏è'
    },
    {
      title: 'Application Deadlines',
      description: 'For September intake: June 30 | For January intake: November 15 | For May intake: March 30 (deadlines may vary by university)',
      icon: <FaCalendarCheck className="text-indigo-600 text-2xl" />,
      emoji: 'üìÖ'
    }
  ];

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };
  
  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.6 } }
  };

  return (
    <motion.div 
      className="pt-20 lg:pt-24 min-h-screen bg-white dark:bg-gray-900" // Added padding to fix navbar overlap and dark mode support
      initial="hidden"
      animate="visible"
      variants={containerVariants}
    >
      {/* Hero Section */}
      <section className="relative py-28 overflow-hidden">
        {/* Background Gradient & Decoration */}
        <div className="absolute inset-0 bg-gradient-to-br from-indigo-900/90 via-blue-800/80 to-purple-800/70"></div>
        
        {/* Decorative Elements */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute -right-24 top-1/4 w-64 h-64 rounded-full bg-indigo-400 opacity-20 blur-3xl"></div>
          <div className="absolute left-1/4 bottom-0 w-80 h-80 rounded-full bg-blue-300 opacity-20 blur-3xl"></div>
          <div className="pattern-dots opacity-10 absolute inset-0"></div>
        </div>
        
        <div className="container mx-auto px-4 relative z-10">
          <motion.div 
            className="max-w-3xl mx-auto text-center"
            variants={itemVariants}
          >
            <span className="inline-block px-4 py-1.5 bg-white/10 backdrop-blur-sm rounded-full text-white text-sm font-medium mb-6">
              Step-by-Step Guide
            </span>
            
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-6 leading-tight">
              <span className="text-gradient-light">
                {pageInfo.title}
              </span>
            </h1>
            
            <p className="text-xl text-white/90 max-w-3xl mx-auto">
              {pageInfo.description}
            </p>
          </motion.div>
        </div>
      </section>

      {/* Main Content */}
      <AdmissionProcess />

      {/* Additional Information */}
      <section className="py-24 bg-gradient-to-b from-indigo-50 to-white dark:from-indigo-900/20 dark:to-gray-900 relative overflow-hidden">
        <div className="absolute inset-0 pattern-dots opacity-5"></div>
        
        <div className="container mx-auto px-4 relative z-10">
          <motion.div 
            className="text-center mb-16"
            variants={itemVariants}
          >
            <span className="inline-block px-3 py-1.5 bg-indigo-100 dark:bg-indigo-900/50 text-indigo-700 dark:text-indigo-300 rounded-full text-sm font-medium mb-4">
              What You Need to Know
            </span>
            <h2 className="text-3xl md:text-4xl font-bold text-gradient mb-4">
              Important Information
            </h2>
            <div className="w-20 h-1.5 bg-gradient-to-r from-indigo-600 to-blue-500 rounded-full mx-auto mb-6"></div>
            <p className="text-gray-600 dark:text-gray-300 max-w-2xl mx-auto text-lg">
              Essential details to help you prepare for your educational journey abroad
            </p>
          </motion.div>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {additionalInfo.map((info, index) => (
              <motion.div 
                key={index}
                variants={itemVariants}
                className="bg-white dark:bg-gray-800 p-8 rounded-2xl shadow-xl hover:shadow-2xl border border-gray-100 dark:border-gray-700 hover:border-indigo-100 dark:hover:border-indigo-700/50 transition-all duration-300 transform hover:-translate-y-2"
              >
                <div className="w-16 h-16 rounded-xl bg-indigo-100 dark:bg-indigo-900/50 flex items-center justify-center mb-6">
                  <span className="text-3xl">{info.emoji}</span>
                </div>
                <h3 className="text-2xl font-bold text-indigo-700 dark:text-indigo-400 mb-4">{info.title}</h3>
                <p className="text-gray-600 dark:text-gray-300">{info.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-24 relative overflow-hidden">
        {/* Background */}
        <div className="absolute inset-0 bg-gradient-to-r from-indigo-600 to-blue-500"></div>
        
        {/* Decorative elements */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute -right-40 -top-40 w-96 h-96 rounded-full bg-purple-400 opacity-20 blur-3xl"></div>
          <div className="absolute -left-20 top-1/3 w-72 h-72 rounded-full bg-blue-300 opacity-20 blur-3xl"></div>
        </div>
        
        <div className="container mx-auto px-4 relative z-10">
          <div className="max-w-4xl mx-auto bg-white/10 backdrop-blur-lg rounded-3xl p-12 border border-white/20 shadow-2xl">
            <motion.div 
              className="text-center"
              variants={itemVariants}
            >
              <h2 className="text-3xl md:text-4xl font-bold text-white mb-6">
                Ready to Start Your Educational Journey?
              </h2>
              <p className="text-xl text-white/90 mb-10 max-w-2xl mx-auto">
                Contact us today to begin your application process. Our team of experts is ready to guide you through every step.
              </p>
              <div className="flex flex-wrap gap-6 justify-center">
                <a
                  href="mailto:info@stepseeducation.co.uk"
                  className="px-8 py-4 bg-white text-indigo-600 font-semibold rounded-full hover:bg-gray-100 transition-all duration-300 shadow-xl shadow-indigo-900/20 transform hover:-translate-y-1 text-center"
                >
                  Email: info@stepseeducation.co.uk
                </a>
                <a
                  href="tel:+4447563228537"
                  className="px-8 py-4 bg-transparent border border-white/60 text-white font-semibold rounded-full hover:bg-white/10 transition-all duration-300 transform hover:-translate-y-1 text-center"
                >
                  Call: +44-475-63228537
                </a>
              </div>
            </motion.div>
          </div>
        </div>
      </section>
    </motion.div>
  );
};

export default AdmissionProcessPage;
