import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Hero = ({ content }) => {
  // Find title, subtitle and description fields from content
  const getFieldValue = (fieldName) => {
    if (!content || !content.fields) return '';
    const field = content.fields.find(f => f.name === fieldName);
    return field ? field.value : '';
  };

  const title = getFieldValue('title');
  const subtitle = getFieldValue('subtitle');
  const description = getFieldValue('description');
  const backgroundImage = getFieldValue('backgroundImage');

  return (
    <section className="relative min-h-[90vh] md:min-h-screen flex items-center py-16 md:py-0">
      {/* Background Image with Overlay */}
      <div className="absolute inset-0">
        <img
          src={backgroundImage || "https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3"}
          alt="International students"
          className="w-full h-full object-cover"
          loading="eager"
        />
        <div className="absolute inset-0 bg-gradient-to-r from-blue-900/80 to-blue-700/60"></div>
      </div>

      {/* Content */}
      <div className="container mx-auto px-4 relative z-10">
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="max-w-3xl"
        >
          <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-4 md:mb-6 leading-tight">
            WE EXIST TO HELP YOU ACHIEVE MORE
          </h1>

          <p className="text-xl sm:text-2xl md:text-3xl text-blue-200 mb-2 md:mb-3 font-semibold">
            YOUR DREAM OUR JOB!
          </p>

          <p className="text-base sm:text-lg md:text-xl text-gray-200 mb-6 md:mb-8 max-w-2xl">
            {description || "STEPS Education Limited provides expert guidance for international education since 2020. We focus on quality service with a limited intake of 50 students per batch."}
          </p>

          <div className="flex flex-wrap gap-4">
            <Link
              to="/contact"
              className="inline-block px-6 sm:px-8 py-3 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 transition-colors duration-300 text-center"
            >
              Book a Consultation
            </Link>

            <Link
              to="/services"
              className="inline-block px-6 sm:px-8 py-3 bg-transparent border-2 border-white text-white font-semibold rounded-md hover:bg-white/10 transition-colors duration-300 text-center"
            >
              Our Services
            </Link>
          </div>
        </motion.div>

        {/* Features */}
        <motion.div 
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.6 }}
          className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4 md:gap-6 lg:gap-8 mt-12 md:mt-16 lg:mt-20"
        >
          <Feature icon="✓" title="50 Students Per Intake" />
          <Feature icon="✓" title="Tier 4 Student Visa Specialists" />
          <Feature icon="✓" title="3-Day Document Assessment" />
          <Feature icon="✓" title="BEST COURSE FOR YOU" />
        </motion.div>
      </div>
    </section>
  );
};

const Feature = ({ icon, title }) => (
  <motion.div 
    whileHover={{ scale: 1.03 }}
    className="bg-white/10 backdrop-blur-sm p-4 md:p-6 rounded-lg transition-all duration-300 hover:bg-white/20 flex items-start"
  >
    <div className="text-xl md:text-2xl text-white mr-3 bg-blue-600/60 rounded-full w-8 h-8 flex items-center justify-center flex-shrink-0">{icon}</div>
    <h3 className="text-white font-medium text-sm md:text-base">{title}</h3>
  </motion.div>
);

export default Hero;
