import React from 'react';
import { FaGraduationCap, FaPassport, FaUserTie, FaLanguage, FaHome, FaHandshake } from 'react-icons/fa';

const ServiceCard = ({ title, description, icon }) => (
  <div className="bg-white p-8 rounded-lg shadow-sm hover:shadow-md transition-shadow duration-300">
    <div className="text-4xl mb-4">{icon}</div>
    <h3 className="text-xl font-semibold text-navy mb-3">{title}</h3>
    <p className="text-gray-600 mb-4">{description}</p>
  </div>
);

const services = [
  {
    icon: <FaGraduationCap className="w-8 h-8" />,
    title: "University Admissions",
    description: "Expert guidance in selecting and applying to top universities worldwide. We help you navigate the entire application process."
  },
  {
    icon: <FaPassport className="w-8 h-8" />,
    title: "Visa Guidance",
    description: "Comprehensive assistance with student visa applications, documentation, and interview preparation."
  },
  {
    icon: <FaUserTie className="w-8 h-8" />,
    title: "Career Counselling",
    description: "Professional guidance to help you make informed decisions about your academic and career path."
  },
  {
    icon: <FaLanguage className="w-8 h-8" />,
    title: "Language Training",
    description: "Specialized coaching for IELTS, TOEFL, and other language proficiency tests required for international admissions."
  },
  {
    icon: <FaHome className="w-8 h-8" />,
    title: "Home",
    description: "Home"
  },
];

const Services = ({ data = services }) => {
  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Our Services
          </h2>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Comprehensive support for your international education journey
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {data && data.length > 0 ? (
            data.map((service) => (
              <ServiceCard
                key={service._id}
                title={service.title}
                description={service.description}
                icon={service.icon || '🎓'}
              />
            ))
          ) : (
            // Fallback content
            <>
              <ServiceCard
                title="Study Abroad Consultation"
                description="Get expert guidance on choosing the right country and university for your studies."
                icon="🎓"
              />
              <ServiceCard
                title="Visa Application Support"
                description="Navigate the visa application process with our experienced team."
                icon="✈️"
              />
              <ServiceCard
                title="University Admission Support"
                description="Complete assistance with university applications and documentation."
                icon="📚"
              />
            </>
          )}
        </div>
      </div>
    </section>
  );
};

export default Services;
