import React, { useState, useEffect } from 'react';
import { FaPlus, FaEdit, FaTrash, FaArrowUp, FaArrowDown, FaFlag } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { CountriesService } from './AdminDataServices';

const CountriesManagement = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [formMode, setFormMode] = useState('add'); // 'add' or 'edit'
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    image: '',
    flag: ''
  });

  // Fetch countries on component mount
  useEffect(() => {
    fetchCountries();
  }, []);

  // Fetch countries from service
  const fetchCountries = async () => {
    try {
      setLoading(true);
      const data = await CountriesService.getCountries();
      // Sort by order
      const sortedData = [...data].sort((a, b) => (a.order || 0) - (b.order || 0));
      setCountries(sortedData);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching countries:', error);
      toast.error('Failed to load countries');
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Reset form
  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      image: '',
      flag: ''
    });
    setSelectedCountry(null);
    setFormMode('add');
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (formMode === 'add') {
        await CountriesService.addCountry(formData);
      } else {
        await CountriesService.updateCountry(selectedCountry._id, formData);
      }

      resetForm();
      fetchCountries();
    } catch (error) {
      console.error('Error saving country:', error);
      toast.error('Failed to save country');
    }
  };

  // Edit country
  const handleEdit = (country) => {
    setSelectedCountry(country);
    setFormData({
      name: country.name,
      description: country.description,
      image: country.image,
      flag: country.flag
    });
    setFormMode('edit');
  };

  // Delete country
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this country?')) {
      try {
        await CountriesService.deleteCountry(id);
        fetchCountries();
      } catch (error) {
        console.error('Error deleting country:', error);
        toast.error('Failed to delete country');
      }
    }
  };

  // Move country up in order
  const handleMoveUp = async (index) => {
    if (index === 0) return; // Already at the top

    try {
      const newCountries = [...countries];
      [newCountries[index - 1], newCountries[index]] = [newCountries[index], newCountries[index - 1]];

      // Update order property
      newCountries.forEach((country, idx) => {
        country.order = idx + 1;
      });

      // Get array of IDs in the new order
      const orderedIds = newCountries.map(country => country._id);

      await CountriesService.reorderCountries(orderedIds);
      fetchCountries();
    } catch (error) {
      console.error('Error reordering countries:', error);
      toast.error('Failed to reorder countries');
    }
  };

  // Move country down in order
  const handleMoveDown = async (index) => {
    if (index === countries.length - 1) return; // Already at the bottom

    try {
      const newCountries = [...countries];
      [newCountries[index], newCountries[index + 1]] = [newCountries[index + 1], newCountries[index]];

      // Update order property
      newCountries.forEach((country, idx) => {
        country.order = idx + 1;
      });

      // Get array of IDs in the new order
      const orderedIds = newCountries.map(country => country._id);

      await CountriesService.reorderCountries(orderedIds);
      fetchCountries();
    } catch (error) {
      console.error('Error reordering countries:', error);
      toast.error('Failed to reorder countries');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-navy mb-8">Countries Management</h1>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Form Section */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">
            {formMode === 'add' ? 'Add New Country' : 'Edit Country'}
          </h2>

          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
                Country Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                placeholder="e.g. United Kingdom"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                placeholder="Brief description of education opportunities"
                rows="3"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="image">
                Image URL
              </label>
              <input
                type="url"
                id="image"
                name="image"
                value={formData.image}
                onChange={handleInputChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                placeholder="https://example.com/image.jpg"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="flag">
                Flag Emoji
              </label>
              <input
                type="text"
                id="flag"
                name="flag"
                value={formData.flag}
                onChange={handleInputChange}
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                placeholder="🇬🇧"
                required
              />
              <p className="text-sm text-gray-500 mt-1">
                Use country flag emoji (e.g. 🇬🇧, 🇺🇸, 🇨🇦, 🇦🇺)
              </p>
            </div>

            {formData.image && (
              <div className="mb-4">
                <p className="block text-gray-700 text-sm font-bold mb-2">Image Preview</p>
                <div className="border rounded overflow-hidden h-40">
                  <img 
                    src={formData.image} 
                    alt="Preview" 
                    className="w-full h-full object-cover"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = 'https://via.placeholder.com/400x200?text=Invalid+Image+URL';
                    }}
                  />
                </div>
              </div>
            )}

            <div className="flex items-center justify-between">
              <button
                type="submit"
                className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
              >
                {formMode === 'add' ? 'Add Country' : 'Update Country'}
              </button>

              {formMode === 'edit' && (
                <button
                  type="button"
                  onClick={resetForm}
                  className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  Cancel
                </button>
              )}
            </div>
          </form>
        </div>

        {/* Countries List Section */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h2 className="text-xl font-semibold mb-4">Countries List</h2>

          {loading ? (
            <p className="text-center py-4">Loading countries...</p>
          ) : countries.length === 0 ? (
            <p className="text-center py-4">No countries found. Add your first country!</p>
          ) : (
            <div className="space-y-4">
              {countries.map((country, index) => (
                <div 
                  key={country._id} 
                  className="border rounded-lg p-4 flex items-center justify-between bg-gray-50 hover:bg-gray-100"
                >
                  <div className="flex items-center space-x-4">
                    <div className="w-12 h-12 rounded-full overflow-hidden bg-gray-200 flex-shrink-0">
                      <img 
                        src={country.image} 
                        alt={country.name} 
                        className="w-full h-full object-cover"
                        onError={(e) => {
                          e.target.onerror = null;
                          e.target.src = 'https://via.placeholder.com/100?text=No+Image';
                        }}
                      />
                    </div>
                    <div>
                      <h3 className="font-semibold">
                        <span className="mr-2">{country.flag}</span>
                        {country.name}
                      </h3>
                      <p className="text-sm text-gray-600 truncate max-w-xs">
                        {country.description}
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => handleMoveUp(index)}
                      disabled={index === 0}
                      className={`p-2 rounded-full ${index === 0 ? 'text-gray-300 cursor-not-allowed' : 'text-gray-600 hover:bg-gray-200'}`}
                      title="Move Up"
                    >
                      <FaArrowUp />
                    </button>
                    <button
                      onClick={() => handleMoveDown(index)}
                      disabled={index === countries.length - 1}
                      className={`p-2 rounded-full ${index === countries.length - 1 ? 'text-gray-300 cursor-not-allowed' : 'text-gray-600 hover:bg-gray-200'}`}
                      title="Move Down"
                    >
                      <FaArrowDown />
                    </button>
                    <button
                      onClick={() => handleEdit(country)}
                      className="p-2 rounded-full text-blue-600 hover:bg-blue-100"
                      title="Edit"
                    >
                      <FaEdit />
                    </button>
                    <button
                      onClick={() => handleDelete(country._id)}
                      className="p-2 rounded-full text-red-600 hover:bg-red-100"
                      title="Delete"
                    >
                      <FaTrash />
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Preview Section */}
      <div className="mt-12 bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-semibold mb-4">Preview</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {countries.map((country) => (
            <div
              key={country._id}
              className="group relative overflow-hidden rounded-lg shadow-sm hover:shadow-lg transition-shadow duration-300"
            >
              <div className="aspect-w-16 aspect-h-9">
                <img
                  src={country.image}
                  alt={country.name}
                  className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-300"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = 'https://via.placeholder.com/600x400?text=Image+Not+Available';
                  }}
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent"></div>
              </div>
              <div className="absolute bottom-0 left-0 right-0 p-6">
                <h3 className="text-xl font-bold text-white mb-1">
                  {country.flag} {country.name}
                </h3>
                <p className="text-gray-200 text-sm">
                  {country.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CountriesManagement;
