import React, { useState, useEffect } from 'react';
import {
  Hero,
  AboutSection,
  AdmissionsPathway,
  PopularCourses,
  CountriesWeOffer,
  Testimonials
} from '../components/home';
import Services from '../components/services/Services';
import { servicesApi, coursesApi, testimonialsApi } from '../services/api';
import { ROUTES } from '../constants/routes';

const Home = () => {
  const [services, setServices] = useState([]);
  const [courses, setCourses] = useState([]);
  const [testimonials, setTestimonials] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [servicesRes, coursesRes, testimonialsRes] = await Promise.all([
          servicesApi.getAll(),
          coursesApi.getAll(),
          testimonialsApi.getAll()
        ]);

        setServices(servicesRes.data);
        setCourses(coursesRes.data);
        setTestimonials(testimonialsRes.data);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('Failed to load content. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return null; // Layout component handles loading state

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-red-600 mb-2">Oops!</h2>
          <p className="text-gray-600">{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="pt-16">
      <Hero />
      <Services data={services} />
      <AboutSection />
      <AdmissionsPathway />
      <PopularCourses data={courses} />
      <CountriesWeOffer />
      <Testimonials data={testimonials} />

      {/* Contact CTA */}
      <section className="py-20 bg-navy text-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl md:text-4xl font-bold mb-6 text-white">
            Start Your Educational Journey Today
          </h2>
          <p className="text-xl text-gray-200 mb-8 max-w-2xl mx-auto">
            Contact us for a free consultation and take the first step towards your academic success.
          </p>
          <a
            href={ROUTES.CONTACT}
            className="inline-block px-8 py-3 bg-navy text-white font-semibold rounded-md border-2 border-white hover:bg-white hover:text-navy transition-colors duration-300"
          >
            Get in Touch
          </a>
        </div>
      </section>
    </div>
  );
};

export default Home;
