import React from 'react';

const courses = [
  {
    title: "Computer Tech",
    subtitle: "Computing Technology",
    image: "https://images.unsplash.com/photo-1517694712202-14dd9538aa97?ixlib=rb-4.0.3"
  },
  {
    title: "Business",
    subtitle: "HND In Business",
    image: "https://images.unsplash.com/photo-1507679799987-c73779587ccf?ixlib=rb-4.0.3"
  },
  {
    title: "Business & Marketing",
    subtitle: "Business Management & Marketing",
    image: "https://images.unsplash.com/photo-1552664730-d307ca884978?ixlib=rb-4.0.3"
  },
  {
    title: "Accounting & Finance",
    subtitle: "BSC(Hons) in Accounting and Finance",
    image: "https://images.unsplash.com/photo-1554224155-6726b3ff858f?ixlib=rb-4.0.3"
  },
  {
    title: "Construction",
    subtitle: "Construction Project Management",
    image: "https://images.unsplash.com/photo-1581094794329-c8112c4e0e0a?ixlib=rb-4.0.3"
  },
  {
    title: "Health & Social",
    subtitle: "HND In Health & Social Care",
    image: "https://images.unsplash.com/photo-1576091160550-2173dba999ef?ixlib=rb-4.0.3"
  },
  {
    title: "Digital Technology",
    subtitle: "HND In Digital Technology",
    image: "https://images.unsplash.com/photo-1516321318423-f06f69e0b1c8?ixlib=rb-4.0.3"
  },
  {
    title: "BSC (Hons)",
    subtitle: "BSC (Hons) In Criminology & Psychology",
    image: "https://images.unsplash.com/photo-1507413245164-6160d8298b31?ixlib=rb-4.0.3"
  }
];

const PopularCourses = () => {
  return (
    <section className="py-20">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Top Courses Await Your Selection
          </h2>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {courses.map((course, index) => (
            <div
              key={index}
              className="group relative overflow-hidden rounded-lg shadow-sm hover:shadow-lg transition-shadow duration-300"
            >
              <div className="aspect-w-16 aspect-h-9">
                <img
                  src={course.image}
                  alt={course.title}
                  className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent"></div>
              </div>
              <div className="absolute bottom-0 left-0 right-0 p-6">
                <h3 className="text-xl font-bold text-white mb-1">
                  {course.title}
                </h3>
                <p className="text-gray-200 text-sm">
                  {course.subtitle}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default PopularCourses;
