import React, { useState, useEffect } from 'react';
import LoadingSpinner from '../shared/LoadingSpinner';
import ErrorMessage from '../shared/ErrorMessage';

const PopularCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = () => {
      try {
        setLoading(true);
        // Get courses from localStorage
        const storedCourses = JSON.parse(localStorage.getItem('coursesDatabase')) || [];

        // Sort by featured and limit to 8 courses
        const sortedCourses = storedCourses
          .sort((a, b) => (b.featured === a.featured) ? 0 : b.featured ? 1 : -1)
          .slice(0, 8);

        setCourses(sortedCourses);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching courses:', err);
        setError('Failed to load courses');
        setLoading(false);
      }
    };

    // Initial fetch
    fetchCourses();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'coursesDatabase') {
        console.log('Courses database updated, refreshing...');
        fetchCourses();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Poll for changes every 2 seconds (as a backup for changes in the same tab)
    const intervalId = setInterval(fetchCourses, 2000);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      clearInterval(intervalId);
    };
  }, []);

  if (loading) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4 text-center">
          <LoadingSpinner />
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4 text-center">
          <ErrorMessage message={error} />
        </div>
      </section>
    );
  }

  // If no courses are available, show a message
  if (courses.length === 0) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            No Courses Available
          </h2>
          <p className="text-gray-600">Please check back later or contact us for more information.</p>
        </div>
      </section>
    );
  }
  return (
    <section className="py-20">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Top Courses Await Your Selection
          </h2>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {courses.map((course, index) => (
            <div
              key={index}
              className="group relative overflow-hidden rounded-lg shadow-sm hover:shadow-lg transition-shadow duration-300"
            >
              <div className="aspect-w-16 aspect-h-9">
                <img
                  src={course.image}
                  alt={course.title}
                  className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent"></div>
              </div>
              <div className="absolute bottom-0 left-0 right-0 p-6">
                <h3 className="text-xl font-bold text-white mb-1">
                  {course.title}
                </h3>
                <p className="text-gray-200 text-sm">
                  {course.university || course.subtitle || ''}
                </p>
                {course.featured && (
                  <span className="absolute top-4 right-4 bg-gold text-navy text-xs font-bold px-2 py-1 rounded">
                    Featured
                  </span>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default PopularCourses;
