import React from 'react';
import { motion } from 'framer-motion';
import { FaFileAlt, FaSearch, FaUniversity, FaCheckCircle, FaPassport, FaUserGraduate } from 'react-icons/fa';
import useTheme from '../../hooks/useTheme';

const AdmissionProcess = () => {
  const theme = useTheme();
  
  const steps = [
    {
      id: 1,
      title: 'Documents Submission',
      description: 'Submit your academic and personal documents to info@stepseeducation.co.uk',
      icon: <FaFileAlt className="text-3xl" />,
      emoji: 'üìÑ'
    },
    {
      id: 2,
      title: 'Assessment',
      description: 'Our team will assess your documents within 3 days',
      icon: <FaSearch className="text-3xl" />,
      emoji: 'üîç'
    },
    {
      id: 3,
      title: 'Course & University Selection',
      description: 'We\'ll recommend the best courses and universities based on your profile',
      icon: <FaUniversity className="text-3xl" />,
      emoji: 'üèõÔ∏è'
    },
    {
      id: 4,
      title: 'Offer Letter',
      description: 'Receive your offer letter within 2 weeks of application',
      icon: <FaCheckCircle className="text-3xl" />,
      emoji: 'üìù'
    },
    {
      id: 5,
      title: 'CAS/Approval',
      description: 'Get your Confirmation of Acceptance for Studies (CAS) for visa application',
      icon: <FaPassport className="text-3xl" />,
      emoji: 'üõÇ'
    },
    {
      id: 6,
      title: 'Enrollment',
      description: 'Complete enrollment process with full support from our team',
      icon: <FaUserGraduate className="text-3xl" />,
      emoji: 'üéì'
    },
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.15,
        delayChildren: 0.1
      }
    }
  };
  
  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0, 
      transition: { 
        type: "spring",
        stiffness: 100,
        damping: 12
      } 
    }
  };

  const floatingAnimation = {
    y: [-10, 10],
    transition: {
      y: {
        duration: 2,
        repeat: Infinity,
        repeatType: "reverse",
        ease: "easeInOut"
      }
    }
  };

  return (
    <section className="py-32 relative overflow-hidden bg-gradient-to-b from-white via-indigo-50/30 to-white">
      {/* Decorative Elements */}
      <div className="absolute top-0 inset-x-0 h-40 bg-gradient-to-b from-indigo-500/5 to-transparent"></div>
      <div className="absolute bottom-0 inset-x-0 h-40 bg-gradient-to-t from-indigo-500/5 to-transparent"></div>
      <div className="absolute inset-0 pattern-dots opacity-5"></div>
      <motion.div 
        className="absolute top-20 left-10 w-64 h-64 bg-gradient-to-r from-blue-400/20 to-indigo-400/20 rounded-full blur-3xl"
        animate={floatingAnimation}
      />
      <motion.div 
        className="absolute bottom-20 right-10 w-64 h-64 bg-gradient-to-r from-indigo-400/20 to-purple-400/20 rounded-full blur-3xl"
        animate={floatingAnimation}
      />
      
      <div className="container mx-auto px-4 relative z-10">
        <motion.div
          variants={itemVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="text-center mb-20"
        >
          <span className="inline-block px-4 py-2 bg-gradient-to-r from-indigo-500/10 to-blue-500/10 text-indigo-700 rounded-full text-sm font-medium mb-4 border border-indigo-100">
            Simple 6-Step Process
          </span>
          <h2 className="text-4xl md:text-5xl font-bold bg-gradient-to-r from-indigo-600 to-blue-600 bg-clip-text text-transparent mb-6">
            Your Journey Starts Here
          </h2>
          <div className="w-24 h-2 bg-gradient-to-r from-indigo-600 to-blue-500 rounded-full mx-auto mb-8"></div>
          <p className="text-gray-600 max-w-3xl mx-auto text-lg md:text-xl leading-relaxed">
            Our streamlined admission process is designed to make your educational journey smooth and stress-free.
            Expert guidance at every step ensures your success.
          </p>
        </motion.div>

        <motion.div 
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 lg:gap-10"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, margin: "-100px" }}
        >
          {steps.map((step) => (
            <motion.div
              key={step.id}
              variants={itemVariants}
              whileHover={{ 
                y: -12, 
                transition: { type: "spring", stiffness: 200, damping: 10 }
              }}
              className="group bg-white/80 backdrop-blur-sm p-8 rounded-3xl shadow-xl hover:shadow-2xl border border-gray-100/50 hover:border-indigo-200 transition-all duration-300"
            >
              <div className="relative mb-8">
                <div className="w-20 h-20 rounded-2xl bg-gradient-to-br from-indigo-50 to-blue-50 flex items-center justify-center group-hover:scale-110 transition-transform duration-300">
                  <span className="text-4xl transform group-hover:scale-110 transition-transform duration-300">{step.emoji}</span>
                </div>
                <div className="absolute -top-3 -left-3 bg-gradient-to-r from-indigo-600 to-blue-600 text-white rounded-2xl w-10 h-10 flex items-center justify-center font-bold text-lg shadow-lg group-hover:scale-110 transition-transform duration-300">
                  {step.id}
                </div>
              </div>
              
              <h3 className="text-2xl font-bold bg-gradient-to-r from-indigo-700 to-blue-700 bg-clip-text text-transparent mb-4">
                {step.title}
              </h3>
              <p className="text-gray-600 text-lg leading-relaxed group-hover:text-gray-700 transition-colors duration-300">
                {step.description}
              </p>
            </motion.div>
          ))}
        </motion.div>

        <motion.div
          variants={itemVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="mt-20"
        >
          <div className="bg-gradient-to-r from-indigo-50 to-blue-50 p-10 rounded-3xl shadow-xl border border-indigo-100/50 max-w-3xl mx-auto relative overflow-hidden">
            <div className="absolute inset-0 bg-white/40 backdrop-blur-sm"></div>
            <div className="relative z-10">
              <h4 className="text-2xl font-bold text-indigo-700 mb-6 text-center">
                Ready to Begin Your Educational Journey?
              </h4>
              <p className="text-lg text-gray-700 text-center mb-8">
                Our admission experts are here to guide you through every step of the process.
              </p>
              <div className="flex flex-col sm:flex-row items-center justify-center gap-4">
                <a
                  href="mailto:info@stepseeducation.co.uk"
                  className="inline-flex items-center px-8 py-4 bg-gradient-to-r from-indigo-600 to-blue-600 text-white font-semibold rounded-2xl hover:shadow-lg shadow-xl shadow-indigo-900/20 transition-all duration-300 transform hover:-translate-y-1 group"
                >
                  <span className="mr-2">Contact Us</span>
                  <span className="group-hover:translate-x-1 transition-transform duration-300">‚Üí</span>
                </a>
                <span className="text-gray-500">or</span>
                <a
                  href="tel:+44123456789"
                  className="inline-flex items-center px-8 py-4 bg-white text-indigo-700 font-semibold rounded-2xl hover:shadow-lg shadow-xl shadow-indigo-900/10 transition-all duration-300 transform hover:-translate-y-1 border border-indigo-100 group"
                >
                  <span className="mr-2">Call Now</span>
                  <span className="group-hover:translate-x-1 transition-transform duration-300">‚Üí</span>
                </a>
              </div>
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default AdmissionProcess;
