import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContentBySlug } from '../features/content/contentSlice';
import Hero from '../components/home/Hero';
import ServicesPreview from '../components/home/ServicesPreview';
import Testimonials from '../components/home/Testimonials';
import LoadingSpinner from '../components/shared/LoadingSpinner';
import ErrorMessage from '../components/shared/ErrorMessage';
import {
  AboutSection,
  AdmissionsPathway,
  PopularCourses,
  CountriesWeOffer
} from '../components/home';
import Services from '../components/services/Services';
import CallToAction from '../components/home/CallToAction';
import { ROUTES } from '../constants';
import Debug from '../components/Debug';

const Home = () => {
  const dispatch = useDispatch();
  const { content, loading, error } = useSelector((state) => state.content);

  useEffect(() => {
    dispatch(fetchContentBySlug('home'));
  }, [dispatch]);

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="min-h-screen">
      {/* Sections arranged in the specified order */}

      {/* 1. Hero Section */}
      {content && content.sections?.find(section => section.name === 'hero') ? (
        <Hero key="hero" content={content.sections.find(section => section.name === 'hero')} />
      ) : (
        <Hero key="hero" content={{}} />
      )}

      {/* 2. About Section */}
      <AboutSection />

      {/* 3. Services Section */}
      <Services />

      {/* 4. Countries We Offer */}
      <CountriesWeOffer />

      {/* 5. Top Courses */}
      <PopularCourses />

      {/* 6. Testimonials */}
      <Testimonials />

      {/* 7. CAT (Common Admission Test) */}
      <AdmissionsPathway />

      {/* 8. Footer is typically outside this component, in the App layout */}
      {/* The Footer component should be in your main App.jsx or layout component */}
    </div>
  );
};

export default Home;
