import React from 'react';
import Hero from '../components/home/Hero';
import { AboutSection } from '../components/home';
import AdmissionProcess from '../components/services/AdmissionProcess';
import Services from '../components/services/Services';
import CallToAction from '../components/home/CallToAction';
import ScholarshipsSection from '../components/home/ScholarshipsSection';
import BlogSection from '../components/home/BlogSection';
import { motion } from 'framer-motion';

const Home = () => {
  // Mock content for Hero
  const heroContent = {
    fields: [
      { name: 'title', value: 'WE EXIST TO HELP YOU ACHIEVE MORE' },
      { name: 'subtitle', value: 'YOUR DREAM OUR JOB!' },
      { name: 'description', value: 'STEPS Education Limited provides expert guidance for international education since 2020. We focus on quality service with a limited intake of 50 students per batch.' },
      { name: 'backgroundImage', value: 'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?ixlib=rb-4.0.3' }
    ]
  };

  // Animation variants for staggered animations
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.3,
        delayChildren: 0.2
      }
    }
  };

  return (
    <motion.div 
      className="min-h-screen overflow-hidden pt-20 lg:pt-24"
      initial="hidden"
      animate="visible"
      variants={containerVariants}
    >
      {/* Floating dots animation (purely decorative) */}
      <div className="fixed inset-0 pointer-events-none z-0">
        {[...Array(20)].map((_, i) => (
          <div 
            key={i}
            className="absolute rounded-full bg-indigo-500 opacity-10"
            style={{
              width: Math.random() * 10 + 5,
              height: Math.random() * 10 + 5,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animation: `float ${Math.random() * 10 + 10}s infinite ease-in-out`,
              animationDelay: `${Math.random() * 5}s`,
            }}
          />
        ))}
      </div>

      {/* 1. Hero Section */}
      <motion.section variants={containerVariants}>
        <Hero key="hero" content={heroContent} />
      </motion.section>

      {/* 2. About Section */}
      <motion.section variants={containerVariants}>
        <AboutSection />
      </motion.section>

      {/* 3. Services Section */}
      <motion.section variants={containerVariants}>
        <Services />
      </motion.section>

      {/* 4. Admission Process Section */}
      <motion.section variants={containerVariants}>
        <AdmissionProcess />
      </motion.section>

      {/* 5. Scholarships Section */}
      <motion.section variants={containerVariants}>
        <ScholarshipsSection />
      </motion.section>

      {/* 6. Blog Section */}
      <motion.section variants={containerVariants}>
        <BlogSection />
      </motion.section>

      {/* 7. Call to Action Section */}
      <motion.section variants={containerVariants}>
        <CallToAction />
      </motion.section>

      {/* Add global CSS for floating animation */}
      <style jsx global>{`
        @keyframes float {
          0%, 100% {
            transform: translateY(0);
          }
          50% {
            transform: translateY(-20px);
          }
        }
      `}</style>
    </motion.div>
  );
};

export default Home;
