import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL ? `${process.env.REACT_APP_API_URL}/content` : 'http://localhost:5000/api/content';

const handleError = (error) => {
  if (error.response) {
    throw new Error(error.response.data.message || 'Server error');
  } else if (error.request) {
    throw new Error('Could not connect to server. Please check if the server is running.');
  } else {
    throw new Error('Error setting up the request');
  }
};

const getAuthConfig = (token) => ({
  headers: {
    Authorization: `Bearer ${token}`,
  },
});

// Get all content with filters
export const getAllContent = async (filters = {}) => {
  try {
    const params = new URLSearchParams(filters);
    const response = await axios.get(`${API_URL}?${params}`);
    return response.data;
  } catch (error) {
    handleError(error);
  }
};

// Get content by slug
export const getContentBySlug = async (slug) => {
  try {
    // For 'home' slug, check if we have hero data in localStorage first
    if (slug === 'home') {
      // Try to construct content from localStorage data
      const heroData = JSON.parse(localStorage.getItem('heroDatabase'));

      if (heroData) {
        // Format the hero data to match the expected content structure
        return {
          slug: 'home',
          page: 'home',
          title: 'Welcome to GradWay',
          description: 'Your path to academic success',
          sections: [
            {
              name: 'hero',
              title: 'Hero Section',
              order: 0,
              fields: [
                {
                  name: 'title',
                  type: 'text',
                  value: heroData.title,
                  label: 'Title'
                },
                {
                  name: 'subtitle',
                  type: 'text',
                  value: heroData.subtitle,
                  label: 'Subtitle'
                },
                {
                  name: 'description',
                  type: 'text',
                  value: heroData.description,
                  label: 'Description'
                }
              ]
            }
          ]
        };
      }
    }

    // If we don't have localStorage data or it's not the home page, fetch from API
    const response = await axios.get(`${API_URL}/${slug}`);
    return response.data;
  } catch (error) {
    // If API call fails, check if we can return mock data for home page
    if (slug === 'home') {
      const contentDatabase = JSON.parse(localStorage.getItem('contentDatabase')) || [];
      const homeContent = contentDatabase.find(item => item.slug === 'home');
      if (homeContent) return homeContent;
    }
    handleError(error);
  }
};

// Create content (admin only)
export const createContent = async (contentData, token) => {
  try {
    const response = await axios.post(API_URL, contentData, getAuthConfig(token));
    return response.data;
  } catch (error) {
    handleError(error);
  }
};

// Update content (admin only)
export const updateContent = async (contentId, contentData, token) => {
  try {
    const response = await axios.put(
      `${API_URL}/${contentId}`,
      contentData,
      getAuthConfig(token)
    );
    return response.data;
  } catch (error) {
    handleError(error);
  }
};

// Delete content (admin only)
export const deleteContent = async (contentId, token) => {
  try {
    const response = await axios.delete(
      `${API_URL}/${contentId}`,
      getAuthConfig(token)
    );
    return response.data;
  } catch (error) {
    handleError(error);
  }
};

// Update content section (admin only)
export const updateContentSection = async (contentId, sectionName, sectionData, token) => {
  try {
    const response = await axios.put(
      `${API_URL}/${contentId}/sections/${sectionName}`,
      sectionData,
      getAuthConfig(token)
    );
    return response.data;
  } catch (error) {
    handleError(error);
  }
};

// Add item to repeatable section (admin only)
export const addSectionItem = async (contentId, sectionName, itemData, token) => {
  try {
    const response = await axios.post(
      `${API_URL}/${contentId}/sections/${sectionName}/items`,
      itemData,
      getAuthConfig(token)
    );
    return response.data;
  } catch (error) {
    handleError(error);
  }
};

// Upload file (admin only)
export const uploadFile = async (file, token) => {
  try {
    const formData = new FormData();
    formData.append('file', file);

    const response = await axios.post(
      `${API_URL}/upload`,
      formData,
      {
        ...getAuthConfig(token),
        headers: {
          ...getAuthConfig(token).headers,
          'Content-Type': 'multipart/form-data',
        },
      }
    );
    return response.data;
  } catch (error) {
    handleError(error);
  }
};

const contentService = {
  getAllContent,
  getContentBySlug,
  createContent,
  updateContent,
  deleteContent,
  updateContentSection,
  addSectionItem,
  uploadFile,
};

export default contentService;
