import React, { useState, useEffect } from 'react';
import { getContentByPage, updateContent } from '../../services/contentService';
import { toast } from 'react-toastify';

const ContentManagement = () => {
  const [content, setContent] = useState({
    about: null,
    admissions: null,
    countries: null,
  });
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('about');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: '',
    features: [''],
    steps: [{ title: '', description: '' }],
    countries: [{
      name: '',
      description: '',
      flag: '',
      features: [''],
    }],
  });

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const [about, admissions, countries] = await Promise.all([
          getContentByPage('about'),
          getContentByPage('admissions'),
          getContentByPage('countries'),
        ]);

        setContent({
          about,
          admissions,
          countries,
        });

        if (about) {
          setFormData({
            title: about.title,
            description: about.description,
            image: about.image,
            features: about.features,
          });
        }
      } catch (error) {
        toast.error('Failed to load content');
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFeatureChange = (index, value) => {
    const newFeatures = [...formData.features];
    newFeatures[index] = value;
    setFormData((prev) => ({
      ...prev,
      features: newFeatures,
    }));
  };

  const addFeature = () => {
    setFormData((prev) => ({
      ...prev,
      features: [...prev.features, ''],
    }));
  };

  const removeFeature = (index) => {
    setFormData((prev) => ({
      ...prev,
      features: prev.features.filter((_, i) => i !== index),
    }));
  };

  const handleStepChange = (index, field, value) => {
    const newSteps = [...formData.steps];
    newSteps[index] = {
      ...newSteps[index],
      [field]: value,
    };
    setFormData((prev) => ({
      ...prev,
      steps: newSteps,
    }));
  };

  const addStep = () => {
    setFormData((prev) => ({
      ...prev,
      steps: [...prev.steps, { title: '', description: '' }],
    }));
  };

  const removeStep = (index) => {
    setFormData((prev) => ({
      ...prev,
      steps: prev.steps.filter((_, i) => i !== index),
    }));
  };

  const handleCountryChange = (index, field, value) => {
    const newCountries = [...formData.countries];
    newCountries[index] = {
      ...newCountries[index],
      [field]: value,
    };
    setFormData((prev) => ({
      ...prev,
      countries: newCountries,
    }));
  };

  const addCountry = () => {
    setFormData((prev) => ({
      ...prev,
      countries: [
        ...prev.countries,
        {
          name: '',
          description: '',
          flag: '',
          features: [''],
        },
      ],
    }));
  };

  const removeCountry = (index) => {
    setFormData((prev) => ({
      ...prev,
      countries: prev.countries.filter((_, i) => i !== index),
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await updateContent(content[activeTab]._id, formData);
      toast.success('Content updated successfully');
    } catch (error) {
      toast.error('Failed to update content');
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Content Management</h1>
        <div className="flex space-x-4">
          <button
            className={`px-4 py-2 rounded ${
              activeTab === 'about'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-700'
            }`}
            onClick={() => setActiveTab('about')}
          >
            About Section
          </button>
          <button
            className={`px-4 py-2 rounded ${
              activeTab === 'admissions'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-700'
            }`}
            onClick={() => setActiveTab('admissions')}
          >
            Admissions Pathway
          </button>
          <button
            className={`px-4 py-2 rounded ${
              activeTab === 'countries'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-700'
            }`}
            onClick={() => setActiveTab('countries')}
          >
            Countries
          </button>
        </div>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-sm font-medium text-gray-700">Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Description
          </label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            rows={4}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Image URL
          </label>
          <input
            type="text"
            name="image"
            value={formData.image}
            onChange={handleInputChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
        </div>

        {activeTab === 'about' && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Features
            </label>
            {formData.features.map((feature, index) => (
              <div key={index} className="flex items-center space-x-2 mb-2">
                <input
                  type="text"
                  value={feature}
                  onChange={(e) => handleFeatureChange(index, e.target.value)}
                  className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
                <button
                  type="button"
                  onClick={() => removeFeature(index)}
                  className="text-red-600 hover:text-red-800"
                >
                  Remove
                </button>
              </div>
            ))}
            <button
              type="button"
              onClick={addFeature}
              className="mt-2 text-blue-600 hover:text-blue-800"
            >
              Add Feature
            </button>
          </div>
        )}

        {activeTab === 'admissions' && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Steps
            </label>
            {formData.steps.map((step, index) => (
              <div key={index} className="space-y-2 mb-4 p-4 border rounded">
                <input
                  type="text"
                  placeholder="Step Title"
                  value={step.title}
                  onChange={(e) =>
                    handleStepChange(index, 'title', e.target.value)
                  }
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
                <textarea
                  placeholder="Step Description"
                  value={step.description}
                  onChange={(e) =>
                    handleStepChange(index, 'description', e.target.value)
                  }
                  rows={2}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
                <button
                  type="button"
                  onClick={() => removeStep(index)}
                  className="text-red-600 hover:text-red-800"
                >
                  Remove Step
                </button>
              </div>
            ))}
            <button
              type="button"
              onClick={addStep}
              className="mt-2 text-blue-600 hover:text-blue-800"
            >
              Add Step
            </button>
          </div>
        )}

        {activeTab === 'countries' && (
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Countries
            </label>
            {formData.countries.map((country, index) => (
              <div key={index} className="space-y-2 mb-4 p-4 border rounded">
                <input
                  type="text"
                  placeholder="Country Name"
                  value={country.name}
                  onChange={(e) =>
                    handleCountryChange(index, 'name', e.target.value)
                  }
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
                <textarea
                  placeholder="Country Description"
                  value={country.description}
                  onChange={(e) =>
                    handleCountryChange(index, 'description', e.target.value)
                  }
                  rows={2}
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
                <input
                  type="text"
                  placeholder="Flag Image URL"
                  value={country.flag}
                  onChange={(e) =>
                    handleCountryChange(index, 'flag', e.target.value)
                  }
                  className="block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
                <div className="space-y-2">
                  {country.features.map((feature, featureIndex) => (
                    <div key={featureIndex} className="flex items-center space-x-2">
                      <input
                        type="text"
                        placeholder="Feature"
                        value={feature}
                        onChange={(e) => {
                          const newFeatures = [...country.features];
                          newFeatures[featureIndex] = e.target.value;
                          handleCountryChange(index, 'features', newFeatures);
                        }}
                        className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                      />
                      <button
                        type="button"
                        onClick={() => {
                          const newFeatures = country.features.filter(
                            (_, i) => i !== featureIndex
                          );
                          handleCountryChange(index, 'features', newFeatures);
                        }}
                        className="text-red-600 hover:text-red-800"
                      >
                        Remove
                      </button>
                    </div>
                  ))}
                  <button
                    type="button"
                    onClick={() => {
                      const newFeatures = [...country.features, ''];
                      handleCountryChange(index, 'features', newFeatures);
                    }}
                    className="text-blue-600 hover:text-blue-800"
                  >
                    Add Feature
                  </button>
                </div>
                <button
                  type="button"
                  onClick={() => removeCountry(index)}
                  className="text-red-600 hover:text-red-800"
                >
                  Remove Country
                </button>
              </div>
            ))}
            <button
              type="button"
              onClick={addCountry}
              className="mt-2 text-blue-600 hover:text-blue-800"
            >
              Add Country
            </button>
          </div>
        )}

        <div className="flex justify-end">
          <button
            type="submit"
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
          >
            Save Changes
          </button>
        </div>
      </form>
    </div>
  );
};

export default ContentManagement;
