import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, Outlet, useLocation } from 'react-router-dom';
import { FaGraduationCap, FaHandshake, FaComments, FaEnvelope, FaSignOutAlt, FaHome, FaUsers, FaBlog, FaCog, FaBook, FaBars, FaTimes } from 'react-icons/fa';
import Courses from './Courses';
import Services from './Services';
import Testimonials from './Testimonials';
import Contacts from './Contacts';
import Overview from './Overview';
import Blog from './Blog';
import Team from './Team';
import Hero from './Hero';

const Dashboard = () => {
  const location = useLocation();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [stats, setStats] = useState({
    courses: 0,
    services: 0,
    testimonials: 0,
    contacts: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/admin/stats', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (data.success) {
        setStats(data.stats);
      }
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/admin/login');
  };

  const menuItems = [
    { title: 'Overview', path: '/admin/dashboard', icon: <FaHome /> },
    { title: 'Content Management', path: '/admin/dashboard/content', icon: <FaCog /> },
    { title: 'Courses', path: '/admin/dashboard/courses', icon: <FaBook /> },
    { title: 'Testimonials', path: '/admin/dashboard/testimonials', icon: <FaComments /> },
    { title: 'Contact Requests', path: '/admin/dashboard/contact-requests', icon: <FaEnvelope /> },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={`${
          sidebarOpen ? 'w-64' : 'w-20'
        } bg-white shadow-lg transition-all duration-300 ease-in-out`}
      >
        <div className="flex items-center justify-between p-4">
          {sidebarOpen && <h2 className="text-xl font-bold">Admin Dashboard</h2>}
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="p-2 rounded-lg hover:bg-gray-100"
          >
            {sidebarOpen ? <FaTimes /> : <FaBars />}
          </button>
        </div>
        <nav className="mt-8">
          {menuItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`flex items-center p-4 ${
                location.pathname === item.path
                  ? 'bg-blue-50 text-blue-600'
                  : 'text-gray-600 hover:bg-gray-50'
              }`}
            >
              <span className="text-xl">{item.icon}</span>
              {sidebarOpen && <span className="ml-4">{item.title}</span>}
            </Link>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        <div className="p-8">
          <Outlet />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
