import React from 'react';
import { Link } from 'react-router-dom';

const Hero = ({ content }) => {
  // Find title, subtitle and description fields from content
  const getFieldValue = (fieldName) => {
    if (!content || !content.fields) return '';
    const field = content.fields.find(f => f.name === fieldName);
    return field ? field.value : '';
  };

  const title = getFieldValue('title');
  const subtitle = getFieldValue('subtitle');
  const description = getFieldValue('description');
  const backgroundImage = getFieldValue('backgroundImage');
  return (
    <section className="relative min-h-[90vh] md:min-h-screen flex items-center py-16 md:py-0">
      {/* Background Image with Overlay */}
      <div className="absolute inset-0">
        <img
          src={backgroundImage || "https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3"}
          alt="Students studying"
          className="w-full h-full object-cover"
          loading="eager"
        />
        <div className="absolute inset-0 bg-gradient-to-r from-black/80 to-black/60"></div>
      </div>

      {/* Content */}
      <div className="container mx-auto px-4 relative z-10">
        <div className="max-w-3xl">
          <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-4 md:mb-6 leading-tight">
            {title || 'Empowering Your Education Abroad'}
          </h1>

          <p className="text-base sm:text-lg md:text-xl text-gray-200 mb-2 md:mb-3">
            {subtitle || 'Expert guidance for international education'}
          </p>

          <p className="text-base sm:text-lg md:text-xl text-gray-200 mb-6 md:mb-8 max-w-2xl">
            {description || "Expert guidance and support for your international education journey. From university selection to visa processing, we're here to help you achieve your academic goals."}
          </p>

          <div className="flex flex-wrap gap-4">
            <Link
              to="/contact"
              className="inline-block px-6 sm:px-8 py-3 bg-navy text-white font-semibold rounded-md hover:bg-navy-light transition-colors duration-300 text-center"
            >
              Get in Touch
            </Link>

          </div>
        </div>

        {/* Features */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4 md:gap-6 lg:gap-8 mt-12 md:mt-16 lg:mt-20">
          <Feature icon="✓" title="Licensed and Qualified staff" />
          <Feature icon="✓" title="100% Free Counselling and Assessment" />
          <Feature icon="✓" title="Free Application and Admission" />
          <Feature icon="✓" title="24/7 Student Support Guidance" />
        </div>
      </div>
    </section>
  );
};

const Feature = ({ icon, title }) => (
  <div className="bg-white/10 backdrop-blur-sm p-4 md:p-6 rounded-lg transition-all duration-300 hover:bg-white/20 flex items-start">
    <div className="text-xl md:text-2xl text-white mr-3 bg-navy/40 rounded-full w-8 h-8 flex items-center justify-center flex-shrink-0">{icon}</div>
    <h3 className="text-white font-medium text-sm md:text-base">{title}</h3>
  </div>
);

export default Hero;
