import React, { useState, useEffect } from 'react';
import { FaGraduationCap, FaComments, FaEnvelope, FaCog } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { getContentByPage } from '../../services/contentService';
import { getCourses } from '../../services/courseService';
import { getTestimonials } from '../../services/testimonialService';
import { getContactRequests } from '../../services/contactService';

const Overview = () => {
  const [stats, setStats] = useState({
    courses: 0,
    testimonials: 0,
    contacts: 0,
    content: 0
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [courses, testimonials, contacts, content] = await Promise.all([
          getCourses(),
          getTestimonials(),
          getContactRequests(),
          getContentByPage('about')
        ]);

        setStats({
          courses: courses.length,
          testimonials: testimonials.length,
          contacts: contacts.length,
          content: content ? 1 : 0
        });
      } catch (error) {
        toast.error('Failed to fetch dashboard statistics');
      }
    };

    fetchStats();
  }, []);

  const statCards = [
    {
      title: 'Courses',
      value: stats.courses,
      icon: <FaGraduationCap className="text-3xl" />,
      path: '/admin/dashboard/courses'
    },
    {
      title: 'Testimonials',
      value: stats.testimonials,
      icon: <FaComments className="text-3xl" />,
      path: '/admin/dashboard/testimonials'
    },
    {
      title: 'Contact Requests',
      value: stats.contacts,
      icon: <FaEnvelope className="text-3xl" />,
      path: '/admin/dashboard/contact-requests'
    },
    {
      title: 'Content Sections',
      value: stats.content,
      icon: <FaCog className="text-3xl" />,
      path: '/admin/dashboard/content'
    }
  ];

  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-bold text-gray-800">Dashboard Overview</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {statCards.map((card) => (
          <Link
            key={card.title}
            to={card.path}
            className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow"
          >
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-500 text-sm">{card.title}</p>
                <p className="text-2xl font-bold text-gray-800">{card.value}</p>
              </div>
              <div className="text-blue-500">{card.icon}</div>
            </div>
          </Link>
        ))}
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Link
            to="/admin/dashboard/courses/new"
            className="bg-blue-50 text-blue-600 p-4 rounded-lg hover:bg-blue-100 transition-colors"
          >
            Add New Course
          </Link>
          <Link
            to="/admin/dashboard/testimonials/new"
            className="bg-green-50 text-green-600 p-4 rounded-lg hover:bg-green-100 transition-colors"
          >
            Add New Testimonial
          </Link>
          <Link
            to="/admin/dashboard/content"
            className="bg-purple-50 text-purple-600 p-4 rounded-lg hover:bg-purple-100 transition-colors"
          >
            Edit Content
          </Link>
          <Link
            to="/admin/dashboard/contact-requests"
            className="bg-yellow-50 text-yellow-600 p-4 rounded-lg hover:bg-yellow-100 transition-colors"
          >
            View Contact Requests
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Overview;
