import React from 'react';
import { FaQuoteLeft } from 'react-icons/fa';

const testimonials = [
  {
    name: "Sarah Johnson",
    university: "University of Oxford",
    image: "https://images.unsplash.com/photo-1494790108377-be9c29b29330?ixlib=rb-4.0.3",
    quote: "GradWay made my dream of studying at Oxford a reality. Their guidance was invaluable throughout the application process."
  },
  {
    name: "Michael Chen",
    university: "Harvard University",
    image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3",
    quote: "The team's expertise in visa applications and university admissions was crucial to my success. Highly recommended!"
  },
  {
    name: "Emma Wilson",
    university: "University of Toronto",
    image: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3",
    quote: "Thanks to GradWay, I'm now pursuing my master's degree in Canada. Their support was exceptional at every step."
  }
];

const Testimonials = () => {
  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            What Our Students Say
          </h2>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Hear from students who have successfully achieved their academic goals with our support.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {testimonials.map((testimonial, index) => (
            <div
              key={index}
              className="bg-white p-8 rounded-lg shadow-md hover:shadow-lg transition-shadow"
            >
              <div className="flex items-center mb-6">
                <div className="w-16 h-16 rounded-full overflow-hidden mr-4">
                  <img
                    src={testimonial.image}
                    alt={testimonial.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                <div>
                  <h3 className="font-semibold text-navy">{testimonial.name}</h3>
                  <p className="text-gray-600 text-sm">{testimonial.university}</p>
                </div>
              </div>
              <FaQuoteLeft className="text-navy/20 text-4xl mb-4" />
              <p className="text-gray-600 italic">
                "{testimonial.quote}"
              </p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
