import React from 'react';
import { motion } from 'framer-motion';
import { FaFileUpload, FaCheckCircle, FaBookOpen, FaEnvelope, FaPassport, FaUserGraduate } from 'react-icons/fa';
import { useTheme } from '../../context/ThemeContext';

const AdmissionProcess = () => {
  const { isDarkMode } = useTheme();
  
  const steps = [
    {
      icon: <FaFileUpload />,
      title: "Document Submission",
      description:
        "Submit your academic transcripts, certificates, English language proficiency test results, and a personal statement through our secure online portal.",
    },
    {
      icon: <FaCheckCircle />,
      title: "Assessment",
      description:
        "Our academic team reviews your application to ensure it meets the entry requirements for your chosen course and institution.",
    },
    {
      icon: <FaBookOpen />,
      title: "Course Selection",
      description:
        "Based on your qualifications and preferences, we'll help you select the most suitable courses and institutions aligned with your career goals.",
    },
    {
      icon: <FaEnvelope />,
      title: "Offer Letter",
      description:
        "Once your application is accepted, you'll receive a conditional or unconditional offer letter from the institution.",
    },
    {
      icon: <FaPassport />,
      title: "CAS Approval",
      description:
        "After accepting your offer and paying the deposit, the institution will issue a Confirmation of Acceptance for Studies (CAS) required for your visa application.",
    },
    {
      icon: <FaUserGraduate />,
      title: "Enrollment",
      description:
        "With your student visa approved, we'll guide you through the final steps of enrollment, pre-departure preparation, and arrival in the UK.",
    },
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };
  
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.5 },
    },
  };

  const floatingAnimation = {
    y: [-10, 10],
    transition: {
      y: {
        duration: 2,
        repeat: Infinity,
        repeatType: "reverse",
        ease: "easeInOut"
      }
    }
  };

  return (
    <section className="py-32 relative overflow-hidden bg-gradient-to-b from-white via-indigo-50/30 to-white">
      {/* Decorative Elements */}
      <div className="absolute top-0 inset-x-0 h-40 bg-gradient-to-b from-indigo-500/5 to-transparent"></div>
      <div className="absolute bottom-0 inset-x-0 h-40 bg-gradient-to-t from-indigo-500/5 to-transparent"></div>
      <div className="absolute inset-0 pattern-dots opacity-5"></div>
      <motion.div 
        className="absolute top-20 left-10 w-64 h-64 bg-gradient-to-r from-blue-400/20 to-indigo-400/20 rounded-full blur-3xl"
        animate={floatingAnimation}
      />
      <motion.div 
        className="absolute bottom-20 right-10 w-64 h-64 bg-gradient-to-r from-indigo-400/20 to-purple-400/20 rounded-full blur-3xl"
        animate={floatingAnimation}
      />
      
      <div className="container mx-auto px-4 relative z-10">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center max-w-3xl mx-auto mb-12"
        >
          <h2 className={`text-3xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
            Our 6-Step Admission Process
          </h2>
          <p className={`text-lg ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            We've simplified the university application process to help you navigate each stage with ease and confidence.
          </p>
        </motion.div>

        <motion.div
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
          className="grid md:grid-cols-2 lg:grid-cols-3 gap-8"
        >
          {steps.map((step, index) => (
            <motion.div
              key={index}
              variants={itemVariants}
              className={`relative p-6 rounded-xl ${
                isDarkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white shadow-lg'
              }`}
            >
              <div className="flex items-start">
                <div className={`p-4 rounded-full mr-4 ${
                  isDarkMode ? 'bg-blue-900 text-blue-300' : 'bg-blue-100 text-blue-600'
                }`}>
                  {step.icon}
                </div>
                <div className={`absolute top-5 right-5 w-8 h-8 rounded-full flex items-center justify-center font-bold ${
                  isDarkMode ? 'bg-gray-700 text-white' : 'bg-blue-500 text-white'
                }`}>
                  {index + 1}
                </div>
              </div>
              <h3 className={`text-xl font-bold mt-6 mb-3 ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
                {step.title}
              </h3>
              <p className={isDarkMode ? 'text-gray-300' : 'text-gray-600'}>
                {step.description}
              </p>
            </motion.div>
          ))}
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.6 }}
          className={`mt-16 p-8 rounded-xl text-center ${
            isDarkMode ? 'bg-gray-800 border border-gray-700' : 'bg-blue-50'
          }`}
        >
          <h3 className={`text-2xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
            Need Assistance?
          </h3>
          <p className={`text-lg mb-6 max-w-2xl mx-auto ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
            Our dedicated advisors are here to guide you through each step of the admission process. Get personalized support for your application journey.
          </p>
          <a
            href="/contact"
            className={`inline-block px-8 py-3 rounded-lg font-medium ${
              isDarkMode 
                ? 'bg-blue-600 hover:bg-blue-700 text-white' 
                : 'bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white'
            } transition-all`}
          >
            Contact an Advisor
          </a>
        </motion.div>
      </div>
    </section>
  );
};

export default AdmissionProcess;
