import React, { useState, useEffect } from 'react';
import { FaBook, FaComments, FaEnvelope, FaCog } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const DashboardCard = ({ title, count, icon: Icon, color }) => (
  <div className="bg-white rounded-lg shadow-md p-6">
    <div className="flex items-center justify-between">
      <div>
        <p className="text-gray-500 text-sm">{title}</p>
        <h3 className="text-2xl font-bold mt-2">{count}</h3>
      </div>
      <div className={`p-3 rounded-full ${color}`}>
        <Icon className="text-white text-xl" />
      </div>
    </div>
  </div>
);

const Dashboard = () => {
  const [stats, setStats] = useState({
    courses: 0,
    testimonials: 0,
    contacts: 0,
    content: 4,
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/admin/login');
          return;
        }

        const headers = { Authorization: `Bearer ${token}` };

        const [courses, testimonials, contacts] = await Promise.all([
          axios.get('http://localhost:5000/api/courses', { headers }),
          axios.get('http://localhost:5000/api/testimonials', { headers }),
          axios.get('http://localhost:5000/api/contacts', { headers }),
        ]);

        setStats({
          courses: courses.data.length,
          testimonials: testimonials.data.length,
          contacts: contacts.data.length,
          content: 4, // Static content sections
        });
      } catch (error) {
        console.error('Error fetching dashboard stats:', error);
        if (error.response?.status === 401 || error.response?.status === 403) {
          navigate('/admin/login');
        }
      }
    };

    fetchStats();
  }, [navigate]);

  const cards = [
    {
      title: 'Total Courses',
      count: stats.courses,
      icon: FaBook,
      color: 'bg-blue-500',
    },
    {
      title: 'Total Testimonials',
      count: stats.testimonials,
      icon: FaComments,
      color: 'bg-green-500',
    },
    {
      title: 'Contact Requests',
      count: stats.contacts,
      icon: FaEnvelope,
      color: 'bg-yellow-500',
    },
    {
      title: 'Content Sections',
      count: stats.content,
      icon: FaCog,
      color: 'bg-purple-500',
    },
  ];

  return (
    <div>
      <h1 className="text-2xl font-bold text-gray-800 mb-6">Dashboard Overview</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {cards.map((card) => (
          <DashboardCard key={card.title} {...card} />
        ))}
      </div>

      <div className="mt-8">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <a
            href="/admin/dashboard/courses/new"
            className="block p-4 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow"
          >
            <h3 className="font-semibold text-blue-600">Add New Course</h3>
            <p className="text-gray-600 mt-1">Create and publish a new course</p>
          </a>
          <a
            href="/admin/dashboard/testimonials/new"
            className="block p-4 bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow"
          >
            <h3 className="font-semibold text-green-600">Add New Testimonial</h3>
            <p className="text-gray-600 mt-1">Add a new student testimonial</p>
          </a>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
