import React, { useState, useEffect } from 'react';
import { FaGraduationCap, FaHandshake, FaComments, FaEnvelope, FaChartLine, FaGlobeAmericas, FaCalendarAlt, FaEllipsisH, FaArrowUp, FaArrowDown, FaCog, FaExternalLinkAlt } from 'react-icons/fa';
import { Link } from 'react-router-dom';

const StatCard = ({ icon, title, value, bgColor, change, link }) => (
  <div className={`${bgColor} rounded-lg shadow-lg p-6 text-white transition-transform duration-300 hover:scale-105 hover:shadow-xl`}>
    <div className="flex items-center justify-between">
      <div className="flex items-center">
        <div className="p-3 bg-white bg-opacity-30 rounded-full">
          {icon}
        </div>
        <div className="ml-5">
          <h4 className="text-lg font-semibold">{title}</h4>
          <p className="text-3xl font-bold mt-2">{value}</p>

          {change && (
            <div className="flex items-center mt-2">
              {change > 0 ? (
                <>
                  <FaArrowUp className="text-green-300 mr-1" />
                  <span className="text-green-300 text-sm">{change}% from last month</span>
                </>
              ) : (
                <>
                  <FaArrowDown className="text-red-300 mr-1" />
                  <span className="text-red-300 text-sm">{Math.abs(change)}% from last month</span>
                </>
              )}
            </div>
          )}
        </div>
      </div>
      {link && (
        <Link to={link} className="text-white hover:text-gray-200">
          <FaEllipsisH />
        </Link>
      )}
    </div>
  </div>
);

const ActivityItem = ({ icon, iconBg, title, time, status }) => (
  <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors duration-200">
    <div className="flex items-center">
      <div className={`${iconBg} p-2 rounded-full`}>
        {icon}
      </div>
      <div className="ml-4">
        <p className="text-sm font-medium text-gray-900">{title}</p>
        <p className="text-sm text-gray-500">{time}</p>
      </div>
    </div>
    {status && (
      <span className={`px-3 py-1 text-xs rounded-full ${status === 'new' ? 'bg-green-100 text-green-800' : status === 'pending' ? 'bg-yellow-100 text-yellow-800' : 'bg-blue-100 text-blue-800'}`}>
        {status === 'new' ? 'New' : status === 'pending' ? 'Pending' : 'Completed'}
      </span>
    )}
  </div>
);

const QuickAction = ({ icon, title, description, link, bgColor }) => (
  <Link to={link} className="block">
    <div className={`${bgColor} p-4 rounded-lg text-white transition-transform duration-300 hover:scale-105 hover:shadow-lg`}>
      <div className="flex items-center mb-2">
        {icon}
        <h4 className="text-lg font-semibold ml-2">{title}</h4>
      </div>
      <p className="text-sm text-white/80">{description}</p>
    </div>
  </Link>
);

const Overview = ({ stats }) => {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [activities, setActivities] = useState([]);

  // Update current time every minute
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000);

    return () => clearInterval(timer);
  }, []);

  // Simulate fetching recent activities
  useEffect(() => {
    // In a real app, you would fetch this data from an API
    const mockActivities = [
      {
        id: 1,
        icon: <FaGraduationCap className="w-5 h-5 text-blue-600" />,
        iconBg: 'bg-blue-100',
        title: 'New course added',
        time: '2 hours ago',
        status: 'new'
      },
      {
        id: 2,
        icon: <FaHandshake className="w-5 h-5 text-green-600" />,
        iconBg: 'bg-green-100',
        title: 'Service updated',
        time: '5 hours ago',
        status: 'completed'
      },
      {
        id: 3,
        icon: <FaEnvelope className="w-5 h-5 text-purple-600" />,
        iconBg: 'bg-purple-100',
        title: 'New contact request',
        time: '1 day ago',
        status: 'pending'
      },
      {
        id: 4,
        icon: <FaComments className="w-5 h-5 text-yellow-600" />,
        iconBg: 'bg-yellow-100',
        title: 'New testimonial submitted',
        time: '2 days ago',
        status: 'new'
      }
    ];

    setActivities(mockActivities);
  }, []);

  const statCards = [
    {
      icon: <FaGraduationCap className="w-8 h-8 text-white" />,
      title: 'Total Courses',
      value: stats.courses,
      bgColor: 'bg-gradient-to-r from-blue-500 to-blue-700',
      change: 12,
      link: '/admin/dashboard/courses'
    },
    {
      icon: <FaHandshake className="w-8 h-8 text-white" />,
      title: 'Services',
      value: stats.services,
      bgColor: 'bg-gradient-to-r from-green-500 to-green-700',
      change: 8,
      link: '/admin/dashboard/services'
    },
    {
      icon: <FaComments className="w-8 h-8 text-white" />,
      title: 'Testimonials',
      value: stats.testimonials,
      bgColor: 'bg-gradient-to-r from-yellow-500 to-yellow-700',
      change: -3,
      link: '/admin/dashboard/testimonials'
    },
    {
      icon: <FaEnvelope className="w-8 h-8 text-white" />,
      title: 'Contact Requests',
      value: stats.contacts,
      bgColor: 'bg-gradient-to-r from-purple-500 to-purple-700',
      change: 15,
      link: '/admin/dashboard/contacts'
    }
  ];

  const quickActions = [
    {
      icon: <FaGraduationCap className="w-5 h-5" />,
      title: 'Add Course',
      description: 'Create a new course offering',
      link: '/admin/dashboard/courses',
      bgColor: 'bg-blue-600'
    },
    {
      icon: <FaHandshake className="w-5 h-5" />,
      title: 'Add Service',
      description: 'Create a new service',
      link: '/admin/dashboard/services',
      bgColor: 'bg-green-600'
    },
    {
      icon: <FaCog className="w-5 h-5" />,
      title: 'Edit Hero',
      description: 'Update hero section',
      link: '/admin/dashboard/hero',
      bgColor: 'bg-purple-600'
    },
    {
      icon: <FaGlobeAmericas className="w-5 h-5" />,
      title: 'Countries',
      description: 'Manage country offerings',
      link: '/admin/dashboard/countries',
      bgColor: 'bg-red-600'
    }
  ];

  // Format date for dashboard
  const formatDate = (date) => {
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return date.toLocaleDateString('en-US', options);
  };

  return (
    <div>
      {/* Welcome Section */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
        <div className="flex flex-col md:flex-row md:items-center md:justify-between">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">Welcome back, Admin</h2>
            <p className="text-gray-600 mt-1">{formatDate(currentTime)}</p>
          </div>
          <div className="mt-4 md:mt-0 flex space-x-2">
            <button className="flex items-center px-4 py-2 bg-navy text-white rounded-md hover:bg-navy-dark transition-colors">
              <FaBell className="mr-2" /> Notifications
            </button>
            <button className="flex items-center px-4 py-2 bg-gray-200 text-gray-700 rounded-md hover:bg-gray-300 transition-colors">
              <FaCalendarAlt className="mr-2" /> Calendar
            </button>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <h3 className="text-xl font-semibold text-gray-900 mb-4">Key Metrics</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {statCards.map((stat, index) => (
          <StatCard key={index} {...stat} />
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        {/* Recent Activity */}
        <div className="lg:col-span-2 bg-white rounded-lg shadow-lg p-6">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-xl font-semibold text-gray-900">Recent Activity</h3>
            <Link to="#" className="text-navy hover:text-navy-dark text-sm font-medium">View All</Link>
          </div>
          <div className="space-y-4">
            {activities.map(activity => (
              <ActivityItem key={activity.id} {...activity} />
            ))}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-xl font-semibold text-gray-900 mb-4">Quick Actions</h3>
          <div className="grid grid-cols-1 gap-4">
            {quickActions.map((action, index) => (
              <QuickAction key={index} {...action} />
            ))}
          </div>
        </div>
      </div>

      {/* System Status */}
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h3 className="text-xl font-semibold text-gray-900 mb-4">System Status</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="p-4 bg-gray-50 rounded-lg">
            <div className="flex items-center justify-between mb-2">
              <h4 className="font-medium text-gray-700">Storage</h4>
              <span className="text-sm text-green-600 font-medium">Healthy</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
              <div className="bg-green-600 h-2.5 rounded-full" style={{ width: '25%' }}></div>
            </div>
            <p className="text-sm text-gray-500 mt-2">25% used of 1GB</p>
          </div>
          <div className="p-4 bg-gray-50 rounded-lg">
            <div className="flex items-center justify-between mb-2">
              <h4 className="font-medium text-gray-700">Database</h4>
              <span className="text-sm text-green-600 font-medium">Healthy</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
              <div className="bg-green-600 h-2.5 rounded-full" style={{ width: '40%' }}></div>
            </div>
            <p className="text-sm text-gray-500 mt-2">40% used of 10GB</p>
          </div>
          <div className="p-4 bg-gray-50 rounded-lg">
            <div className="flex items-center justify-between mb-2">
              <h4 className="font-medium text-gray-700">Server Load</h4>
              <span className="text-sm text-green-600 font-medium">Optimal</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
              <div className="bg-green-600 h-2.5 rounded-full" style={{ width: '15%' }}></div>
            </div>
            <p className="text-sm text-gray-500 mt-2">15% CPU usage</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Overview;
