import React from 'react';
import { motion } from 'framer-motion';
import CVJobsPortal from '../components/services/CVJobsPortal';
import { FaChartLine, FaBriefcase, FaUserGraduate } from 'react-icons/fa';

const CVJobsPortalPage = () => {
  const pageInfo = {
    title: 'CV & Jobs Portal',
    description: 'Submit your CV, track job applications, and access valuable employability resources. Our dedicated team will help connect you with the right opportunities to advance your career.'
  };

  const stats = [
    {
      value: '85%',
      label: 'Success Rate',
      description: 'Of our students find employment within 6 months of graduation',
      icon: <FaChartLine className="text-blue-600 text-3xl" />
    },
    {
      value: '500+',
      label: 'Job Opportunities',
      description: 'Available through our network of trusted employers and partners',
      icon: <FaBriefcase className="text-blue-600 text-3xl" />
    },
    {
      value: '1000+',
      label: 'Students Placed',
      description: 'In their dream jobs across various industries and sectors',
      icon: <FaUserGraduate className="text-blue-600 text-3xl" />
    }
  ];

  return (
    <div className="pt-16">
      {/* Hero Section */}
      <motion.section 
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="relative py-20 bg-blue-900 text-white"
      >
        <div className="container mx-auto px-4">
          <h1 className="text-4xl md:text-5xl font-bold mb-6">
            {pageInfo.title}
          </h1>
          <p className="text-xl max-w-3xl">
            {pageInfo.description}
          </p>
        </div>
      </motion.section>

      {/* Stats Section */}
      <section className="py-16 bg-white">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {stats.map((stat, index) => (
              <motion.div 
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.1 * index, duration: 0.5 }}
                viewport={{ once: true }}
                className="bg-gray-50 p-6 rounded-lg shadow-md text-center"
              >
                <div className="flex justify-center mb-4">
                  {stat.icon}
                </div>
                <h3 className="text-3xl font-bold text-blue-600 mb-1">{stat.value}</h3>
                <h4 className="text-lg font-semibold text-gray-800 mb-2">{stat.label}</h4>
                <p className="text-gray-600">{stat.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Main Content */}
      <CVJobsPortal />

      {/* Benefits Section */}
      <section className="py-16 bg-gray-50">
        <div className="container mx-auto px-4">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
            className="max-w-3xl mx-auto text-center mb-12"
          >
            <h2 className="text-3xl font-bold text-blue-600 mb-4">How We Can Help You</h2>
            <p className="text-gray-700">
              At STEPS Education Limited, we provide comprehensive career support services to help you achieve your professional goals.
            </p>
          </motion.div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-4xl mx-auto">
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
              viewport={{ once: true }}
              className="bg-white p-6 rounded-lg shadow-md"
            >
              <h3 className="text-xl font-semibold text-gray-800 mb-3">For Students & Graduates</h3>
              <ul className="space-y-2 text-gray-600">
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  CV building and optimization for UK job market
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Interview preparation and mock interviews
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Access to exclusive job opportunities
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Career counseling and guidance
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Networking events with industry professionals
                </li>
              </ul>
            </motion.div>
            
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
              viewport={{ once: true }}
              className="bg-white p-6 rounded-lg shadow-md"
            >
              <h3 className="text-xl font-semibold text-gray-800 mb-3">For Employers</h3>
              <ul className="space-y-2 text-gray-600">
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Access to a pool of qualified candidates
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Targeted recruitment for specific roles
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Reduced recruitment time and costs
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Participation in career fairs and events
                </li>
                <li className="flex items-start">
                  <span className="text-blue-600 mr-2">✓</span>
                  Partnership opportunities for training programs
                </li>
              </ul>
            </motion.div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-16 bg-blue-600 text-white">
        <div className="container mx-auto px-4 text-center">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
          >
            <h2 className="text-3xl font-bold mb-4">Ready to Advance Your Career?</h2>
            <p className="text-xl mb-8 max-w-3xl mx-auto">
              Submit your CV today or contact us to learn more about our employability services. Let us help you take the next step in your career journey.
            </p>
            <a
              href="#cv-form"
              className="px-8 py-3 bg-white text-blue-600 font-semibold rounded-md hover:bg-gray-100 transition-colors"
            >
              Submit Your CV Now
            </a>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default CVJobsPortalPage;
