import React from 'react';
import { motion } from 'framer-motion';
import { FaFileAlt, FaSearch, FaUniversity, FaCheckCircle, FaPassport, FaUserGraduate } from 'react-icons/fa';

const AdmissionProcess = () => {
  const steps = [
    {
      id: 1,
      title: 'Documents Submission',
      description: 'Submit your academic and personal documents to info@stepseeducation.co.uk',
      icon: <FaFileAlt className="text-3xl text-blue-600" />,
    },
    {
      id: 2,
      title: 'Assessment',
      description: 'Our team will assess your documents within 3 days',
      icon: <FaSearch className="text-3xl text-blue-600" />,
    },
    {
      id: 3,
      title: 'Course & University Selection',
      description: 'We\'ll recommend the best courses and universities based on your profile',
      icon: <FaUniversity className="text-3xl text-blue-600" />,
    },
    {
      id: 4,
      title: 'Offer Letter',
      description: 'Receive your offer letter within 2 weeks of application',
      icon: <FaCheckCircle className="text-3xl text-blue-600" />,
    },
    {
      id: 5,
      title: 'CAS/Approval',
      description: 'Get your Confirmation of Acceptance for Studies (CAS) for visa application',
      icon: <FaPassport className="text-3xl text-blue-600" />,
    },
    {
      id: 6,
      title: 'Enrollment',
      description: 'Complete enrollment process with full support from our team',
      icon: <FaUserGraduate className="text-3xl text-blue-600" />,
    },
  ];

  return (
    <section className="py-20 bg-white">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-bold text-blue-600 mb-4">Admission Process</h2>
          <p className="text-gray-600 max-w-3xl mx-auto">
            Our streamlined 6-step admission process is designed to make your educational journey smooth and stress-free.
            From document submission to enrollment, we provide comprehensive support at every stage.
          </p>
        </motion.div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {steps.map((step, index) => (
            <motion.div
              key={step.id}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.1 * index, duration: 0.5 }}
              viewport={{ once: true }}
              whileHover={{ y: -5 }}
              className="bg-white border border-gray-200 rounded-lg shadow-lg p-6 relative"
            >
              <div className="absolute top-0 left-0 transform -translate-y-1/2 -translate-x-1/2 bg-blue-600 text-white rounded-full w-10 h-10 flex items-center justify-center font-bold">
                {step.id}
              </div>
              <div className="flex flex-col items-center text-center">
                <div className="mb-4">{step.icon}</div>
                <h3 className="text-xl font-bold text-gray-800 mb-2">{step.title}</h3>
                <p className="text-gray-600">{step.description}</p>
              </div>
            </motion.div>
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.6, duration: 0.5 }}
          viewport={{ once: true }}
          className="mt-12 text-center"
        >
          <p className="text-blue-600 font-semibold">
            Need assistance with the admission process? Contact us for detailed guidance.
          </p>
          <div className="mt-4">
            <a
              href="mailto:info@stepseeducation.co.uk"
              className="inline-block px-6 py-3 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 transition-colors"
            >
              Email: info@stepseeducation.co.uk
            </a>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default AdmissionProcess;
