import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import { FaGraduationCap, FaHandshake, FaComments, FaEnvelope, FaSignOutAlt, FaHome, FaUsers, FaBlog, FaCog, FaEdit, FaInfoCircle, FaGlobeAmericas } from 'react-icons/fa';
import Overview from './Overview';
import ContentManagement from './ContentManagement';
import CoursesManagement from './CoursesManagement';
import ServicesManagement from './ServicesManagement';
import TestimonialsManagement from './TestimonialsManagement';
import HeroSectionManagement from './HeroSectionManagement';
import AboutSectionManagement from './AboutSectionManagement';
import CountriesManagement from './CountriesManagement';

const Dashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [stats, setStats] = useState({
    courses: 0,
    services: 0,
    testimonials: 0,
    contacts: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/admin/stats', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (data.success) {
        setStats(data.stats);
      }
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/admin/login');
  };

  const menuItems = [
    { icon: <FaHome className="w-5 h-5" />, text: 'Overview', link: '/admin/dashboard' },
    { icon: <FaEdit className="w-5 h-5" />, text: 'Content Management', link: '/admin/dashboard/content' },
    { icon: <FaGraduationCap className="w-5 h-5" />, text: 'Courses', link: '/admin/dashboard/courses' },
    { icon: <FaHandshake className="w-5 h-5" />, text: 'Services', link: '/admin/dashboard/services' },
    { icon: <FaComments className="w-5 h-5" />, text: 'Testimonials', link: '/admin/dashboard/testimonials' },
    { icon: <FaEnvelope className="w-5 h-5" />, text: 'Contact Requests', link: '/admin/dashboard/contacts' },
    { icon: <FaBlog className="w-5 h-5" />, text: 'Blog Posts', link: '/admin/dashboard/blog' },
    { icon: <FaUsers className="w-5 h-5" />, text: 'Team Members', link: '/admin/dashboard/team' },
    { icon: <FaCog className="w-5 h-5" />, text: 'Hero Section', link: '/admin/dashboard/hero' },
    { icon: <FaInfoCircle className="w-5 h-5" />, text: 'About Section', link: '/admin/dashboard/about' },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={`bg-navy text-white w-64 space-y-6 py-7 px-2 absolute inset-y-0 left-0 transform ${
          isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
        } md:relative md:translate-x-0 transition duration-200 ease-in-out z-30`}
      >
        <div className="flex items-center justify-between px-4">
          <span className="text-2xl font-extrabold">Admin Panel</span>
          <button
            className="md:hidden"
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <nav className="flex flex-col space-y-2">
          {menuItems.map((item) => (
            <Link
              key={item.text}
              to={item.link}
              className="flex items-center space-x-2 py-2.5 px-4 rounded transition duration-200 hover:bg-navy-light"
            >
              {item.icon}
              <span>{item.text}</span>
            </Link>
          ))}
          <button
            onClick={handleLogout}
            className="flex items-center space-x-2 py-2.5 px-4 rounded transition duration-200 hover:bg-navy-light text-red-300 hover:text-red-100 w-full text-left mt-4"
          >
            <FaSignOutAlt className="w-5 h-5" />
            <span>Logout</span>
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Top bar */}
        <header className="bg-white shadow-sm z-20">
          <div className="flex items-center justify-between p-4">
            <button
              className="md:hidden"
              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            </button>
            <div className="flex items-center space-x-4">
              <span className="text-gray-700">Welcome, Admin</span>
            </div>
          </div>
        </header>

        {/* Content */}
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-100">
          <div className="container mx-auto px-6 py-8">
            <Routes>
              <Route index element={<Overview stats={stats} />} />
              <Route path="content/*" element={<ContentManagement />} />
              <Route path="courses/*" element={<CoursesManagement />} />
              <Route path="services/*" element={<ServicesManagement />} />
              <Route path="testimonials/*" element={<TestimonialsManagement />} />
              <Route path="hero/*" element={<HeroSectionManagement />} />
              <Route path="about/*" element={<AboutSectionManagement />} />
            </Routes>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
