import React, { useState, useEffect } from 'react';
import { FaPlus, FaEdit, FaTrash, FaSave } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { ContactPageService } from './AdminDataServices';

const ContactPageManagement = () => {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('hero'); // 'hero', 'contactInfo', 'location', 'services'

  // State for hero section
  const [heroData, setHeroData] = useState({
    title: '',
    description: ''
  });

  // State for contact info
  const [contactInfoData, setContactInfoData] = useState({
    phone: { title: '', value: '' },
    email: { title: '', value: '' },
    hours: { title: '', value: '' }
  });

  // State for form section
  const [formData, setFormData] = useState({
    title: ''
  });

  // State for location section
  const [locationData, setLocationData] = useState({
    title: '',
    address: {
      title: '',
      line1: '',
      line2: '',
      line3: ''
    },
    mapEmbedUrl: ''
  });

  // State for services
  const [services, setServices] = useState([]);
  const [serviceFormMode, setServiceFormMode] = useState('add'); // 'add' or 'edit'
  const [selectedServiceIndex, setSelectedServiceIndex] = useState(null);
  const [serviceFormData, setServiceFormData] = useState({
    value: '',
    label: ''
  });

  // Fetch contact page data on component mount
  useEffect(() => {
    fetchContactPageData();
  }, []);

  // Fetch contact page data
  const fetchContactPageData = async () => {
    try {
      setLoading(true);
      const data = await ContactPageService.getContactPage();

      // Set hero data
      setHeroData(data.hero);

      // Set contact info data
      setContactInfoData(data.contactInfo);

      // Set form data
      setFormData(data.form);

      // Set location data
      setLocationData(data.location);

      // Set services
      setServices(data.services);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching contact page data:', error);
      toast.error('Failed to load contact page data');
      setLoading(false);
    }
  };

  // Handle hero data input changes
  const handleHeroInputChange = (e) => {
    const { name, value } = e.target;
    setHeroData({
      ...heroData,
      [name]: value
    });
  };

  // Save hero data
  const handleSaveHero = async (e) => {
    e.preventDefault();

    try {
      await ContactPageService.updateHero(heroData);
      toast.success('Hero section updated successfully');
    } catch (error) {
      console.error('Error saving hero data:', error);
      toast.error('Failed to save hero data');
    }
  };

  // Handle contact info input changes
  const handleContactInfoChange = (section, field, value) => {
    setContactInfoData({
      ...contactInfoData,
      [section]: {
        ...contactInfoData[section],
        [field]: value
      }
    });
  };

  // Save contact info
  const handleSaveContactInfo = async (e) => {
    e.preventDefault();

    try {
      await ContactPageService.updateContactInfo(contactInfoData);
      toast.success('Contact information updated successfully');
    } catch (error) {
      console.error('Error saving contact info:', error);
      toast.error('Failed to save contact information');
    }
  };

  // Handle location data input changes
  const handleLocationChange = (field, value) => {
    if (field.startsWith('address.')) {
      const addressField = field.split('.')[1];
      setLocationData({
        ...locationData,
        address: {
          ...locationData.address,
          [addressField]: value
        }
      });
    } else {
      setLocationData({
        ...locationData,
        [field]: value
      });
    }
  };

  // Save location data
  const handleSaveLocation = async (e) => {
    e.preventDefault();

    try {
      await ContactPageService.updateLocation(locationData);
      toast.success('Location information updated successfully');
    } catch (error) {
      console.error('Error saving location data:', error);
      toast.error('Failed to save location information');
    }
  };

  // Handle service form input changes
  const handleServiceInputChange = (e) => {
    const { name, value } = e.target;
    setServiceFormData({
      ...serviceFormData,
      [name]: value
    });
  };

  // Reset service form
  const resetServiceForm = () => {
    setServiceFormData({
      value: '',
      label: ''
    });
    setSelectedServiceIndex(null);
    setServiceFormMode('add');
  };

  // Handle service form submission
  const handleServiceSubmit = async (e) => {
    e.preventDefault();

    try {
      if (serviceFormMode === 'add') {
        await ContactPageService.addService(serviceFormData);
      } else {
        await ContactPageService.updateService(selectedServiceIndex, serviceFormData);
      }

      resetServiceForm();
      fetchContactPageData();
    } catch (error) {
      console.error('Error saving service:', error);
      toast.error('Failed to save service');
    }
  };

  // Edit service
  const handleEditService = (index) => {
    const service = services[index];
    setSelectedServiceIndex(index);
    setServiceFormData({
      value: service.value,
      label: service.label
    });
    setServiceFormMode('edit');
  };

  // Delete service
  const handleDeleteService = async (index) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      try {
        await ContactPageService.deleteService(index);
        fetchContactPageData();
      } catch (error) {
        console.error('Error deleting service:', error);
        toast.error('Failed to delete service');
      }
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-navy mb-8">Contact Page Management</h1>

      {/* Tabs */}
      <div className="flex border-b mb-8 overflow-x-auto">
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'hero' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('hero')}
        >
          Hero Section
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'contactInfo' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('contactInfo')}
        >
          Contact Information
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'location' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('location')}
        >
          Location
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'services' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('services')}
        >
          Services
        </button>
      </div>

      {loading ? (
        <div className="text-center py-8">
          <p>Loading...</p>
        </div>
      ) : (
        <>
          {/* Hero Section Tab */}
          {activeTab === 'hero' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Hero Section</h2>

              <form onSubmit={handleSaveHero}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="heroTitle">
                    Title
                  </label>
                  <input
                    type="text"
                    id="heroTitle"
                    name="title"
                    value={heroData.title}
                    onChange={handleHeroInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="heroDescription">
                    Description
                  </label>
                  <textarea
                    id="heroDescription"
                    name="description"
                    value={heroData.description}
                    onChange={handleHeroInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    rows="3"
                    required
                  />
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save Hero Section
                </button>
              </form>
            </div>
          )}

          {/* Contact Information Tab */}
          {activeTab === 'contactInfo' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Contact Information</h2>

              <form onSubmit={handleSaveContactInfo}>
                {/* Phone Section */}
                <div className="mb-6">
                  <h3 className="text-lg font-medium text-navy mb-3">Phone</h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="phoneTitle">
                        Title
                      </label>
                      <input
                        type="text"
                        id="phoneTitle"
                        value={contactInfoData.phone.title}
                        onChange={(e) => handleContactInfoChange('phone', 'title', e.target.value)}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="phoneValue">
                        Value
                      </label>
                      <input
                        type="text"
                        id="phoneValue"
                        value={contactInfoData.phone.value}
                        onChange={(e) => handleContactInfoChange('phone', 'value', e.target.value)}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                      />
                    </div>
                  </div>
                </div>

                {/* Email Section */}
                <div className="mb-6">
                  <h3 className="text-lg font-medium text-navy mb-3">Email</h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="emailTitle">
                        Title
                      </label>
                      <input
                        type="text"
                        id="emailTitle"
                        value={contactInfoData.email.title}
                        onChange={(e) => handleContactInfoChange('email', 'title', e.target.value)}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="emailValue">
                        Value
                      </label>
                      <input
                        type="email"
                        id="emailValue"
                        value={contactInfoData.email.value}
                        onChange={(e) => handleContactInfoChange('email', 'value', e.target.value)}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                      />
                    </div>
                  </div>
                </div>

                {/* Working Hours Section */}
                <div className="mb-6">
                  <h3 className="text-lg font-medium text-navy mb-3">Working Hours</h3>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="hoursTitle">
                        Title
                      </label>
                      <input
                        type="text"
                        id="hoursTitle"
                        value={contactInfoData.hours.title}
                        onChange={(e) => handleContactInfoChange('hours', 'title', e.target.value)}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="hoursValue">
                        Value
                      </label>
                      <input
                        type="text"
                        id="hoursValue"
                        value={contactInfoData.hours.value}
                        onChange={(e) => handleContactInfoChange('hours', 'value', e.target.value)}
                        className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                        required
                      />
                    </div>
                  </div>
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save Contact Information
                </button>
              </form>
            </div>
          )}

          {/* Location Tab */}
          {activeTab === 'location' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Location</h2>

              <form onSubmit={handleSaveLocation}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="locationTitle">
                    Section Title
                  </label>
                  <input
                    type="text"
                    id="locationTitle"
                    value={locationData.title}
                    onChange={(e) => handleLocationChange('title', e.target.value)}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-6">
                  <h3 className="text-lg font-medium text-navy mb-3">Address</h3>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="addressTitle">
                      Address Title
                    </label>
                    <input
                      type="text"
                      id="addressTitle"
                      value={locationData.address.title}
                      onChange={(e) => handleLocationChange('address.title', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="addressLine1">
                      Address Line 1
                    </label>
                    <input
                      type="text"
                      id="addressLine1"
                      value={locationData.address.line1}
                      onChange={(e) => handleLocationChange('address.line1', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="addressLine2">
                      Address Line 2
                    </label>
                    <input
                      type="text"
                      id="addressLine2"
                      value={locationData.address.line2}
                      onChange={(e) => handleLocationChange('address.line2', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="addressLine3">
                      Address Line 3
                    </label>
                    <input
                      type="text"
                      id="addressLine3"
                      value={locationData.address.line3}
                      onChange={(e) => handleLocationChange('address.line3', e.target.value)}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="mapEmbedUrl">
                    Google Maps Embed URL
                  </label>
                  <input
                    type="text"
                    id="mapEmbedUrl"
                    value={locationData.mapEmbedUrl}
                    onChange={(e) => handleLocationChange('mapEmbedUrl', e.target.value)}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                  <p className="text-sm text-gray-500 mt-1">
                    Get the embed URL from Google Maps by clicking "Share" and then "Embed a map"
                  </p>
                </div>

                <div className="mb-6">
                  <h3 className="text-lg font-medium text-navy mb-3">Map Preview</h3>
                  <div className="rounded-lg overflow-hidden shadow-md">
                    <iframe
                      src={locationData.mapEmbedUrl}
                      width="100%"
                      height="300"
                      style={{ border: 0 }}
                      allowFullScreen=""
                      loading="lazy"
                      referrerPolicy="no-referrer-when-downgrade"
                      title="Google Maps"
                    ></iframe>
                  </div>
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save Location Information
                </button>
              </form>
            </div>
          )}

          {/* Services Tab */}
          {activeTab === 'services' && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Service Form */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">
                  {serviceFormMode === 'add' ? 'Add Service' : 'Edit Service'}
                </h2>

                <form onSubmit={handleServiceSubmit}>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="serviceLabel">
                      Service Name
                    </label>
                    <input
                      type="text"
                      id="serviceLabel"
                      name="label"
                      value={serviceFormData.label}
                      onChange={handleServiceInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="serviceValue">
                      Service Value (ID)
                    </label>
                    <input
                      type="text"
                      id="serviceValue"
                      name="value"
                      value={serviceFormData.value}
                      onChange={handleServiceInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      placeholder="e.g. study-abroad"
                      required
                    />
                    <p className="text-sm text-gray-500 mt-1">
                      This is used as the value in the dropdown. Use lowercase with hyphens.
                    </p>
                  </div>

                  <div className="flex items-center justify-between">
                    <button
                      type="submit"
                      className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                      {serviceFormMode === 'add' ? 'Add Service' : 'Update Service'}
                    </button>

                    {serviceFormMode === 'edit' && (
                      <button
                        type="button"
                        onClick={resetServiceForm}
                        className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      >
                        Cancel
                      </button>
                    )}
                  </div>
                </form>
              </div>

              {/* Services List */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">Services List</h2>

                {services.length === 0 ? (
                  <p className="text-center py-4">No services found. Add your first service!</p>
                ) : (
                  <div className="space-y-4">
                    {services.map((service, index) => (
                      <div 
                        key={index} 
                        className="border rounded-lg p-4 flex items-center justify-between bg-gray-50 hover:bg-gray-100"
                      >
                        <div>
                          <h3 className="font-semibold">{service.label}</h3>
                          <p className="text-sm text-gray-600">Value: {service.value}</p>
                        </div>

                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => handleEditService(index)}
                            className="p-2 rounded-full text-blue-600 hover:bg-blue-100"
                            title="Edit"
                          >
                            <FaEdit />
                          </button>
                          <button
                            onClick={() => handleDeleteService(index)}
                            className="p-2 rounded-full text-red-600 hover:bg-red-100"
                            title="Delete"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ContactPageManagement;
