import React from 'react';
import { FaGraduationCap, FaHandshake, FaComments, FaEnvelope } from 'react-icons/fa';

const StatCard = ({ icon, title, value, bgColor }) => (
  <div className={`${bgColor} rounded-lg shadow-lg p-6 text-white`}>
    <div className="flex items-center">
      <div className="p-3 bg-white bg-opacity-30 rounded-full">
        {icon}
      </div>
      <div className="ml-5">
        <h4 className="text-xl font-semibold">{title}</h4>
        <p className="text-2xl font-bold mt-2">{value}</p>
      </div>
    </div>
  </div>
);

const Overview = ({ stats }) => {
  const statCards = [
    {
      icon: <FaGraduationCap className="w-8 h-8 text-white" />,
      title: 'Total Courses',
      value: stats.courses,
      bgColor: 'bg-blue-600'
    },
    {
      icon: <FaHandshake className="w-8 h-8 text-white" />,
      title: 'Services',
      value: stats.services,
      bgColor: 'bg-green-600'
    },
    {
      icon: <FaComments className="w-8 h-8 text-white" />,
      title: 'Testimonials',
      value: stats.testimonials,
      bgColor: 'bg-yellow-600'
    },
    {
      icon: <FaEnvelope className="w-8 h-8 text-white" />,
      title: 'Contact Requests',
      value: stats.contacts,
      bgColor: 'bg-purple-600'
    }
  ];

  return (
    <div>
      <h2 className="text-2xl font-semibold text-gray-900 mb-6">Dashboard Overview</h2>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {statCards.map((stat, index) => (
          <StatCard key={index} {...stat} />
        ))}
      </div>

      {/* Recent Activity */}
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h3 className="text-xl font-semibold text-gray-900 mb-4">Recent Activity</h3>
        <div className="space-y-4">
          <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
            <div className="flex items-center">
              <div className="bg-blue-100 p-2 rounded-full">
                <FaGraduationCap className="w-5 h-5 text-blue-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-900">New course added</p>
                <p className="text-sm text-gray-500">2 hours ago</p>
              </div>
            </div>
          </div>
          <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
            <div className="flex items-center">
              <div className="bg-green-100 p-2 rounded-full">
                <FaHandshake className="w-5 h-5 text-green-600" />
              </div>
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-900">Service updated</p>
                <p className="text-sm text-gray-500">5 hours ago</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Overview;
