import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { Layout } from './components/layout';
import { ROUTES } from './constants/routes';
import { motion, AnimatePresence } from 'framer-motion';

// Page imports
import Home from './pages/Home';
import ContactPage from './pages/Contact';
import AdmissionProcessPage from './pages/AdmissionProcess';
import CVJobsPortalPage from './pages/CVJobsPortal';

// Preloader Component
const Preloader = () => (
  <motion.div
    initial={{ opacity: 1 }}
    exit={{ 
      opacity: 0,
      transition: { duration: 0.4, ease: "easeOut" } 
    }}
    className="fixed inset-0 z-50 flex items-center justify-center bg-indigo-900"
  >
    <div className="text-center">
      <div className="flex justify-center items-center mb-8">
        <motion.div
          animate={{ 
            scale: [1, 1.2, 1],
            rotate: [0, 360]
          }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            ease: "easeInOut"
          }}
          className="w-16 h-16 border-4 border-indigo-500 border-t-transparent rounded-full"
        />
      </div>

      <motion.h2 
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.3, duration: 0.5 }}
        className="text-2xl font-bold text-white mb-2"
      >
        STEPS EDUCATION
      </motion.h2>

      <motion.div 
        initial={{ width: 0 }}
        animate={{ width: "100%" }}
        transition={{ duration: 1.5, ease: "easeInOut" }}
        className="h-1 bg-indigo-500 rounded-full w-40 mx-auto mt-4"
      />
    </div>
  </motion.div>
);

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time
    const timer = setTimeout(() => {
      setLoading(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      <AnimatePresence>
        {loading && <Preloader />}
      </AnimatePresence>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Home />} />
          <Route path={ROUTES.ADMISSION_PROCESS} element={<AdmissionProcessPage />} />
          <Route path={ROUTES.CV_JOBS} element={<CVJobsPortalPage />} />
          <Route path={ROUTES.CONTACT} element={<ContactPage />} />
        </Route>
      </Routes>
    </>
  );
}

export default App;
