import React, { useState } from 'react';
import { Outlet, Link, useLocation, useNavigate } from 'react-router-dom';
import { FaHome, FaBook, FaComments, FaEnvelope, FaCog, FaSignOutAlt, FaBars, FaTimes } from 'react-icons/fa';

const AdminLayout = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/admin/login');
  };

  const menuItems = [
    { title: 'Overview', path: '/admin/dashboard', icon: <FaHome className="text-xl" /> },
    { title: 'Courses', path: '/admin/dashboard/courses', icon: <FaBook className="text-xl" /> },
    { title: 'Testimonials', path: '/admin/dashboard/testimonials', icon: <FaComments className="text-xl" /> },
    { title: 'Contact Requests', path: '/admin/dashboard/contacts', icon: <FaEnvelope className="text-xl" /> },
    { title: 'Content Management', path: '/admin/dashboard/content', icon: <FaCog className="text-xl" /> },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={`${
          sidebarOpen ? 'w-64' : 'w-20'
        } bg-white shadow-lg transition-all duration-300 ease-in-out fixed h-full`}
      >
        <div className="flex items-center justify-between p-4 border-b">
          {sidebarOpen && <h2 className="text-xl font-bold text-gray-800">Admin Panel</h2>}
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="p-2 rounded-lg hover:bg-gray-100 text-gray-600"
          >
            {sidebarOpen ? <FaTimes /> : <FaBars />}
          </button>
        </div>

        <nav className="mt-6">
          {menuItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`flex items-center px-4 py-3 ${
                location.pathname === item.path
                  ? 'bg-blue-50 text-blue-600 border-r-4 border-blue-600'
                  : 'text-gray-600 hover:bg-gray-50'
              }`}
            >
              <span className="text-xl">{item.icon}</span>
              {sidebarOpen && <span className="ml-4">{item.title}</span>}
            </Link>
          ))}
          
          <button
            onClick={handleLogout}
            className="flex items-center w-full px-4 py-3 text-gray-600 hover:bg-gray-50 mt-auto"
          >
            <FaSignOutAlt className="text-xl" />
            {sidebarOpen && <span className="ml-4">Logout</span>}
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className={`flex-1 ${sidebarOpen ? 'ml-64' : 'ml-20'} transition-all duration-300`}>
        <div className="p-8">
          <Outlet />
        </div>
      </div>
    </div>
  );
};

export default AdminLayout;
