import React, { useState, useEffect } from 'react';
import LoadingSpinner from '../shared/LoadingSpinner';
import ErrorMessage from '../shared/ErrorMessage';

const CountriesWeOffer = () => {
  const [countries, setCountries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCountries = () => {
      try {
        setLoading(true);
        // Get countries from localStorage
        const storedCountries = JSON.parse(localStorage.getItem('countriesDatabase')) || [];

        if (storedCountries.length > 0) {
          // Sort by order if available
          const sortedCountries = [...storedCountries].sort((a, b) => (a.order || 0) - (b.order || 0));
          setCountries(sortedCountries);
        } else {
          // Default data if nothing is in localStorage
          setCountries([
            {
              _id: 'country-1',
              name: "United Kingdom",
              description: "World-class education in historic institutions",
              image: "https://images.unsplash.com/photo-1513635269975-59663e0ac1ad?ixlib=rb-4.0.3",
              flag: "ðŸ‡¬ðŸ‡§",
              order: 1
            },
            {
              _id: 'country-2',
              name: "United States",
              description: "Innovation and diverse academic opportunities",
              image: "https://images.unsplash.com/photo-1501466044931-62695aada8e9?ixlib=rb-4.0.3",
              flag: "ðŸ‡ºðŸ‡¸",
              order: 2
            },
            {
              _id: 'country-3',
              name: "Canada",
              description: "High quality education in a multicultural environment",
              image: "https://images.unsplash.com/photo-1503614472-8c93d56e92ce?ixlib=rb-4.0.3",
              flag: "ðŸ‡¨ðŸ‡¦",
              order: 3
            },
            {
              _id: 'country-4',
              name: "Australia",
              description: "Excellence in education with great lifestyle",
              image: "https://images.unsplash.com/photo-1523482580672-f109ba8cb9be?ixlib=rb-4.0.3",
              flag: "ðŸ‡¦ðŸ‡º",
              order: 4
            }
          ]);
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching countries:', err);
        setError('Failed to load countries');
        setLoading(false);
      }
    };

    // Initial fetch
    fetchCountries();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'countriesDatabase') {
        console.log('Countries database updated, refreshing...');
        fetchCountries();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Poll for changes every 2 seconds (as a backup for changes in the same tab)
    const intervalId = setInterval(fetchCountries, 2000);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      clearInterval(intervalId);
    };
  }, []);
  if (loading) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4 flex justify-center">
          <LoadingSpinner />
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4">
          <ErrorMessage message={error} />
        </div>
      </section>
    );
  }

  if (countries.length === 0) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Countries We Offer
          </h2>
          <p className="text-gray-600">No countries available at the moment.</p>
        </div>
      </section>
    );
  }

  return (
    <section className="py-20">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Countries We Offer
          </h2>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {countries.map((country) => (
            <div
              key={country._id}
              className="group relative overflow-hidden rounded-lg shadow-sm hover:shadow-lg transition-shadow duration-300"
            >
              <div className="aspect-w-16 aspect-h-9">
                <img
                  src={country.image}
                  alt={country.name}
                  className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-300"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = 'https://via.placeholder.com/600x400?text=Image+Not+Available';
                  }}
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent"></div>
              </div>
              <div className="absolute bottom-0 left-0 right-0 p-6">
                <h3 className="text-xl font-bold text-white mb-1">
                  {country.flag} {country.name}
                </h3>
                <p className="text-gray-200 text-sm">
                  {country.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default CountriesWeOffer;
