import React, { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import { CoursesService } from './AdminServices';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import ErrorMessage from '../../components/shared/ErrorMessage';

const CoursesManagement = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    university: '',
    description: '',
    duration: '',
    tuitionFee: '',
    requirements: '',
    image: '',
    featured: false
  });
  const [isEditing, setIsEditing] = useState(false);

  // Load all courses on component mount
  useEffect(() => {
    const loadCourses = async () => {
      try {
        setLoading(true);
        const data = await CoursesService.getAllCourses();
        setCourses(data);
        setLoading(false);
      } catch (err) {
        setError(err.message || 'Failed to load courses');
        setLoading(false);
      }
    };

    loadCourses();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      if (isEditing && selectedCourse) {
        // Update existing course
        const updatedCourse = await CoursesService.updateCourse(selectedCourse._id, formData);
        setCourses(courses.map(course => 
          course._id === updatedCourse._id ? updatedCourse : course
        ));
        setIsEditing(false);
        setSelectedCourse(null);
      } else {
        // Create new course
        const newCourse = await CoursesService.createCourse(formData);
        setCourses([...courses, newCourse]);
      }
      // Reset form
      setFormData({
        title: '',
        university: '',
        description: '',
        duration: '',
        tuitionFee: '',
        requirements: '',
        image: '',
        featured: false
      });
      setLoading(false);
    } catch (error) {
      toast.error(error.message || 'Something went wrong');
      setLoading(false);
    }
  };

  // Handle edit course
  const handleEdit = (course) => {
    setSelectedCourse(course);
    setFormData({
      title: course.title,
      university: course.university,
      description: course.description,
      duration: course.duration,
      tuitionFee: course.tuitionFee,
      requirements: course.requirements,
      image: course.image,
      featured: course.featured
    });
    setIsEditing(true);
  };

  // Handle delete course
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this course?')) {
      try {
        setLoading(true);
        await CoursesService.deleteCourse(id);
        setCourses(courses.filter(course => course._id !== id));
        setLoading(false);
      } catch (error) {
        toast.error(error.message || 'Failed to delete course');
        setLoading(false);
      }
    }
  };

  // Cancel editing
  const handleCancel = () => {
    setIsEditing(false);
    setSelectedCourse(null);
    setFormData({
      title: '',
      university: '',
      description: '',
      duration: '',
      tuitionFee: '',
      requirements: '',
      image: '',
      featured: false
    });
  };

  if (loading && courses.length === 0) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} />;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Courses Management</h1>

      {/* Course Form */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h2 className="text-xl font-semibold mb-4">
          {isEditing ? 'Edit Course' : 'Add New Course'}
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="title">
                Course Title
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="university">
                University
              </label>
              <input
                type="text"
                id="university"
                name="university"
                value={formData.university}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4 md:col-span-2">
              <label className="block text-gray-700 mb-2" htmlFor="description">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="4"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              ></textarea>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="duration">
                Duration
              </label>
              <input
                type="text"
                id="duration"
                name="duration"
                value={formData.duration}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="tuitionFee">
                Tuition Fee
              </label>
              <input
                type="text"
                id="tuitionFee"
                name="tuitionFee"
                value={formData.tuitionFee}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4 md:col-span-2">
              <label className="block text-gray-700 mb-2" htmlFor="requirements">
                Requirements
              </label>
              <textarea
                id="requirements"
                name="requirements"
                value={formData.requirements}
                onChange={handleChange}
                rows="3"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              ></textarea>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="image">
                Image URL
              </label>
              <input
                type="text"
                id="image"
                name="image"
                value={formData.image}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4 flex items-center">
              <input
                type="checkbox"
                id="featured"
                name="featured"
                checked={formData.featured}
                onChange={handleChange}
                className="mr-2"
              />
              <label className="text-gray-700" htmlFor="featured">
                Featured Course
              </label>
            </div>
          </div>
          <div className="flex justify-end mt-4">
            {isEditing && (
              <button
                type="button"
                onClick={handleCancel}
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md mr-2 hover:bg-gray-400"
              >
                Cancel
              </button>
            )}
            <button
              type="submit"
              className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              disabled={loading}
            >
              {loading ? 'Saving...' : isEditing ? 'Update Course' : 'Add Course'}
            </button>
          </div>
        </form>
      </div>

      {/* Courses List */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">All Courses</h2>
        {courses.length === 0 ? (
          <p className="text-gray-500">No courses found.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white">
              <thead>
                <tr className="bg-gray-100">
                  <th className="py-2 px-4 text-left">Title</th>
                  <th className="py-2 px-4 text-left">University</th>
                  <th className="py-2 px-4 text-left">Duration</th>
                  <th className="py-2 px-4 text-left">Featured</th>
                  <th className="py-2 px-4 text-left">Actions</th>
                </tr>
              </thead>
              <tbody>
                {courses.map((course) => (
                  <tr key={course._id} className="border-t">
                    <td className="py-2 px-4">{course.title}</td>
                    <td className="py-2 px-4">{course.university}</td>
                    <td className="py-2 px-4">{course.duration}</td>
                    <td className="py-2 px-4">
                      {course.featured ? 'Yes' : 'No'}
                    </td>
                    <td className="py-2 px-4">
                      <button
                        onClick={() => handleEdit(course)}
                        className="text-blue-500 hover:text-blue-700 mr-2"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(course._id)}
                        className="text-red-500 hover:text-red-700"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default CoursesManagement;
