import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
// Import local admin data service instead of Redux actions
import AdminDataService from './AdminDataService';
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import ErrorMessage from '../../components/shared/ErrorMessage';

const SECTION_TEMPLATES = {
  hero: {
    name: 'hero',
    fields: {
      title: '',
      subtitle: '',
      buttonText: '',
      buttonLink: '',
      backgroundImage: ''
    }
  },
  services: {
    name: 'services',
    isRepeatable: true,
    items: []
  },
  testimonials: {
    name: 'testimonials',
    isRepeatable: true,
    items: []
  }
};

const ContentManagement = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user } = useSelector((state) => state.auth);
  const { content, allContent, loading, error } = useSelector((state) => state.content);

  const [selectedPage, setSelectedPage] = useState('home');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    template: 'default',
    sections: [],
    meta: {
      title: '',
      description: '',
      keywords: []
    }
  });

  useEffect(() => {
    if (!user || !user.isAdmin) {
      navigate('/login');
    } else {
      dispatch(fetchAllContent());
    }
  }, [user, navigate, dispatch]);

  useEffect(() => {
    if (selectedPage) {
      dispatch(fetchContentBySlug(selectedPage));
    }
  }, [dispatch, selectedPage]);

  useEffect(() => {
    if (content) {
      setFormData({
        title: content.title || '',
        description: content.description || '',
        template: content.template || 'default',
        sections: content.sections || [],
        meta: content.meta || {
          title: '',
          description: '',
          keywords: []
        }
      });
    }
  }, [content]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (content) {
        await dispatch(updateContent({ id: content._id, contentData: formData })).unwrap();
        toast.success('Content updated successfully');
      } else {
        await dispatch(createContent({ ...formData, slug: selectedPage })).unwrap();
        toast.success('Content created successfully');
      }
    } catch (error) {
      toast.error(error.message || 'Something went wrong');
    }
  };

  const handleSectionChange = (sectionName, sectionData) => {
    setFormData(prev => ({
      ...prev,
      sections: prev.sections.map(section =>
        section.name === sectionName ? { ...section, ...sectionData } : section
      )
    }));
  };

  const handleAddSection = (templateName) => {
    const template = SECTION_TEMPLATES[templateName];
    if (!template) return;

    setFormData(prev => ({
      ...prev,
      sections: [...prev.sections, { ...template }]
    }));
  };

  const handleAddSectionItem = async (sectionName, itemData) => {
    try {
      await dispatch(addSectionItem({
        id: content._id,
        sectionName,
        itemData
      })).unwrap();
      toast.success('Item added successfully');
    } catch (error) {
      toast.error(error.message || 'Failed to add item');
    }
  };

  const handleUpdateSection = async (sectionName, sectionData) => {
    try {
      await dispatch(updateContentSection({
        id: content._id,
        sectionName,
        sectionData
      })).unwrap();
      toast.success('Section updated successfully');
    } catch (error) {
      toast.error(error.message || 'Failed to update section');
    }
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold">Content Management</h1>
        <button
          type="button"
          onClick={() => setFormData(prev => ({
            ...prev,
            sections: []
          }))}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          New Content
        </button>
      </div>

      <div className="grid grid-cols-4 gap-6">
        {/* Sidebar */}
        <div className="col-span-1 bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-4">Pages</h2>
          <ul className="space-y-2">
            {allContent?.map(item => (
              <li
                key={item._id}
                className={`cursor-pointer p-2 rounded ${selectedPage === item.slug ? 'bg-blue-100' : 'hover:bg-gray-100'}`}
                onClick={() => setSelectedPage(item.slug)}
              >
                {item.title}
              </li>
            ))}
          </ul>

          <h2 className="text-lg font-semibold mt-8 mb-4">Add Section</h2>
          <div className="space-y-2">
            {Object.keys(SECTION_TEMPLATES).map(templateName => (
              <button
                key={templateName}
                type="button"
                onClick={() => handleAddSection(templateName)}
                className="w-full px-3 py-2 text-left text-sm rounded hover:bg-gray-100"
              >
                {templateName.charAt(0).toUpperCase() + templateName.slice(1)}
              </button>
            ))}
          </div>
        </div>

        {/* Main Content */}
        <div className="col-span-3">
          <form onSubmit={handleSubmit} className="space-y-6 bg-white p-6 rounded shadow">
            <div className="grid grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Title
                </label>
                <input
                  type="text"
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  className="w-full p-2 border rounded"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Template
                </label>
                <select
                  value={formData.template}
                  onChange={(e) => setFormData({ ...formData, template: e.target.value })}
                  className="w-full p-2 border rounded"
                >
                  <option value="default">Default</option>
                  <option value="landing">Landing Page</option>
                  <option value="article">Article</option>
                </select>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="w-full p-2 border rounded"
                rows="3"
              />
            </div>

            <div className="border-t pt-6 mt-6">
              <h2 className="text-xl font-semibold mb-4">Sections</h2>

              {formData.sections.length === 0 ? (
                <p className="text-gray-500">No sections added yet. Add a section from the sidebar.</p>
              ) : (
                <div className="space-y-6">
                  {formData.sections.map((section, index) => (
                    <div key={`${section.name}-${index}`} className="border p-4 rounded">
                      <h3 className="font-semibold text-lg mb-3 capitalize">
                        {section.name} Section
                      </h3>

                      {section.isRepeatable && (
                        <div className="mb-4">
                          <button
                            type="button"
                            onClick={() => handleAddSectionItem(section.name, {})}
                            className="text-blue-600 text-sm hover:underline"
                          >
                            + Add Item
                          </button>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>

            <div className="border-t pt-6 mt-6">
              <h2 className="text-xl font-semibold mb-4">Meta Information</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Meta Title
                  </label>
                  <input
                    type="text"
                    value={formData.meta.title || ''}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      meta: { ...prev.meta, title: e.target.value }
                    }))}
                    className="w-full p-2 border rounded"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Meta Keywords
                  </label>
                  <input
                    type="text"
                    value={Array.isArray(formData.meta.keywords) ? formData.meta.keywords.join(', ') : formData.meta.keywords || ''}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      meta: { 
                        ...prev.meta, 
                        keywords: e.target.value.split(',').map(k => k.trim())
                      }
                    }))}
                    className="w-full p-2 border rounded"
                    placeholder="keyword1, keyword2, keyword3"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Meta Description
                  </label>
                  <textarea
                    value={formData.meta.description || ''}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      meta: { ...prev.meta, description: e.target.value }
                    }))}
                    className="w-full p-2 border rounded"
                    rows="3"
                  />
                </div>

                <button
                  type="submit"
                  className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
                >
                  {content ? 'Update Content' : 'Create Content'}
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ContentManagement;
