import React from 'react';

const countries = [
  {
    name: "United Kingdom",
    description: "World-class education in historic institutions",
    image: "https://images.unsplash.com/photo-1513635269975-59663e0ac1ad?ixlib=rb-4.0.3",
    flag: "ðŸ‡¬ðŸ‡§"
  },
  {
    name: "United States",
    description: "Innovation and diverse academic opportunities",
    image: "https://images.unsplash.com/photo-1501466044931-62695aada8e9?ixlib=rb-4.0.3",
    flag: "ðŸ‡ºðŸ‡¸"
  },
  {
    name: "Canada",
    description: "High quality education in a multicultural environment",
    image: "https://images.unsplash.com/photo-1503614472-8c93d56e92ce?ixlib=rb-4.0.3",
    flag: "ðŸ‡¨ðŸ‡¦"
  },
  {
    name: "Australia",
    description: "Excellence in education with great lifestyle",
    image: "https://images.unsplash.com/photo-1523482580672-f109ba8cb9be?ixlib=rb-4.0.3",
    flag: "ðŸ‡¦ðŸ‡º"
  }
];

const CountriesWeOffer = () => {
  return (
    <section className="py-20">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Countries We Offer
          </h2>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {countries.map((country, index) => (
            <div
              key={index}
              className="group relative overflow-hidden rounded-lg shadow-sm hover:shadow-lg transition-shadow duration-300"
            >
              <div className="aspect-w-16 aspect-h-9">
                <img
                  src={country.image}
                  alt={country.name}
                  className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 to-transparent"></div>
              </div>
              <div className="absolute bottom-0 left-0 right-0 p-6">
                <h3 className="text-xl font-bold text-white mb-1">
                  {country.flag} {country.name}
                </h3>
                <p className="text-gray-200 text-sm">
                  {country.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default CountriesWeOffer;
