import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchContentBySlug } from '../features/content/contentSlice';
import Hero from '../components/home/Hero';
import ServicesPreview from '../components/home/ServicesPreview';
import Testimonials from '../components/home/Testimonials';
import LoadingSpinner from '../components/shared/LoadingSpinner';
import ErrorMessage from '../components/shared/ErrorMessage';
import {
  AboutSection,
  AdmissionsPathway,
  PopularCourses,
  CountriesWeOffer
} from '../components/home';
import Services from '../components/services/Services';
import { ROUTES } from '../constants';
import Debug from '../components/Debug';

const Home = () => {
  const dispatch = useDispatch();
  const { content, loading, error } = useSelector((state) => state.content);

  useEffect(() => {
    dispatch(fetchContentBySlug('home'));
  }, [dispatch]);

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="min-h-screen">
      {content && (
        <>
          {content.sections?.map((section) => {
            switch (section.name) {
              case 'hero':
                return <Hero key="hero" content={section} />;
              case 'services':
                return <ServicesPreview key="services" content={section} />;
              case 'testimonials':
                return <Testimonials key="testimonials" content={section} />;
              default:
                return null;
            }
          })}
        </>
      )}
      <Services />
      <AboutSection />
      <AdmissionsPathway />
      <PopularCourses />
      <CountriesWeOffer />

      {/* Contact CTA */}
      <section className="py-20 bg-navy text-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl md:text-4xl font-bold mb-6 text-white">
            Start Your Educational Journey Today
          </h2>
          <p className="text-xl text-gray-200 mb-8 max-w-2xl mx-auto">
            Contact us for a free consultation and take the first step towards your academic success.
          </p>
          <a
            href={ROUTES.CONTACT}
            className="inline-block px-8 py-3 bg-navy text-white font-semibold rounded-md border-2 border-white hover:bg-white hover:text-navy transition-colors duration-300"
          >
            Get in Touch
          </a>
        </div>
      </section>
    </div>
  );
};

export default Home;
