import React, { useState, useEffect } from 'react';
import { getCourses } from '../../services/courseService';
import { toast } from 'react-toastify';
import LoadingSpinner from '../shared/LoadingSpinner';
import ErrorMessage from '../shared/ErrorMessage';
import { Link } from 'react-router-dom';

const PopularCourses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        const data = await getCourses();
        // Get top 6 courses based on some criteria (e.g., rating, enrollment)
        const popularCourses = data
          .sort((a, b) => b.rating - a.rating)
          .slice(0, 6);
        setCourses(popularCourses);
      } catch (err) {
        setError('Failed to fetch popular courses');
        toast.error('Failed to fetch popular courses');
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, []);

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;
  if (!courses.length) return null;

  return (
    <section className="py-20">
      <div className="container mx-auto px-4">
        <div className="max-w-3xl mx-auto text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold mb-6 text-navy">
            Popular Courses
          </h2>
          <p className="text-lg text-gray-600">
            Explore our most sought-after courses and start your journey today
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {courses.map((course) => (
            <div
              key={course._id}
              className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300"
            >
              <div className="p-6">
                <h3 className="text-xl font-semibold text-navy mb-2">
                  {course.title}
                </h3>
                <p className="text-gray-600 mb-4">
                  {course.description}
                </p>
                <div className="flex items-center justify-between mb-4">
                  <span className="text-navy font-semibold">
                    ${course.price}
                  </span>
                  <span className="text-gray-500">
                    {course.duration}
                  </span>
                </div>
                <div className="flex items-center mb-4">
                  <div className="flex text-yellow-400">
                    {[...Array(5)].map((_, i) => (
                      <span key={i}>
                        {i < Math.floor(course.rating) ? '★' : '☆'}
                      </span>
                    ))}
                  </div>
                  <span className="text-gray-500 ml-2">
                    ({course.rating})
                  </span>
                </div>
                <Link
                  to={`/courses/${course._id}`}
                  className="block w-full text-center px-4 py-2 bg-navy text-white rounded-md hover:bg-navy-light transition-colors"
                >
                  Learn More
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default PopularCourses;
