import React, { useState, useEffect } from 'react';
import { FaPlus, FaEdit, FaTrash, FaArrowUp, FaArrowDown, FaSave } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { AboutPageService } from './AdminDataServices';

const AboutPageManagement = () => {
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('hero'); // 'hero', 'mission', 'team', 'members', 'cta'

  // State for hero section
  const [heroData, setHeroData] = useState({
    title: '',
    description: ''
  });

  // State for mission section
  const [missionData, setMissionData] = useState({
    title: '',
    description1: '',
    description2: ''
  });

  // State for vision section
  const [visionData, setVisionData] = useState({
    title: '',
    description1: '',
    description2: ''
  });

  // State for team section
  const [teamSectionData, setTeamSectionData] = useState({
    title: '',
    description: ''
  });

  // State for team members
  const [teamMembers, setTeamMembers] = useState([]);
  const [selectedMember, setSelectedMember] = useState(null);
  const [memberFormMode, setMemberFormMode] = useState('add'); // 'add' or 'edit'
  const [memberFormData, setMemberFormData] = useState({
    name: '',
    role: '',
    image: '',
    bio: ''
  });

  // State for CTA section
  const [ctaData, setCtaData] = useState({
    title: '',
    description: '',
    buttonText: '',
    buttonLink: ''
  });

  // Fetch about page data on component mount
  useEffect(() => {
    fetchAboutPageData();
  }, []);

  // Fetch about page data
  const fetchAboutPageData = async () => {
    try {
      setLoading(true);
      const data = await AboutPageService.getAboutPage();

      // Set hero data
      setHeroData(data.hero);

      // Set mission data
      setMissionData(data.mission);

      // Set vision data
      setVisionData(data.vision);

      // Set team section data
      setTeamSectionData(data.team);

      // Set team members
      const sortedMembers = [...data.teamMembers].sort((a, b) => (a.order || 0) - (b.order || 0));
      setTeamMembers(sortedMembers);

      // Set CTA data
      setCtaData(data.cta);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching about page data:', error);
      toast.error('Failed to load about page data');
      setLoading(false);
    }
  };

  // Handle hero data input changes
  const handleHeroInputChange = (e) => {
    const { name, value } = e.target;
    setHeroData({
      ...heroData,
      [name]: value
    });
  };

  // Save hero data
  const handleSaveHero = async (e) => {
    e.preventDefault();

    try {
      await AboutPageService.updateHero(heroData);
      toast.success('Hero section updated successfully');
    } catch (error) {
      console.error('Error saving hero data:', error);
      toast.error('Failed to save hero data');
    }
  };

  // Handle mission data input changes
  const handleMissionInputChange = (e) => {
    const { name, value } = e.target;
    setMissionData({
      ...missionData,
      [name]: value
    });
  };

  // Save mission data
  const handleSaveMission = async (e) => {
    e.preventDefault();

    try {
      await AboutPageService.updateMission(missionData);
      toast.success('Mission section updated successfully');
    } catch (error) {
      console.error('Error saving mission data:', error);
      toast.error('Failed to save mission data');
    }
  };

  // Handle vision data input changes
  const handleVisionInputChange = (e) => {
    const { name, value } = e.target;
    setVisionData({
      ...visionData,
      [name]: value
    });
  };

  // Save vision data
  const handleSaveVision = async (e) => {
    e.preventDefault();

    try {
      await AboutPageService.updateVision(visionData);
      toast.success('Vision section updated successfully');
    } catch (error) {
      console.error('Error saving vision data:', error);
      toast.error('Failed to save vision data');
    }
  };

  // Handle team section data input changes
  const handleTeamSectionInputChange = (e) => {
    const { name, value } = e.target;
    setTeamSectionData({
      ...teamSectionData,
      [name]: value
    });
  };

  // Save team section data
  const handleSaveTeamSection = async (e) => {
    e.preventDefault();

    try {
      await AboutPageService.updateTeamSection(teamSectionData);
      toast.success('Team section updated successfully');
    } catch (error) {
      console.error('Error saving team section data:', error);
      toast.error('Failed to save team section data');
    }
  };

  // Handle member form input changes
  const handleMemberInputChange = (e) => {
    const { name, value } = e.target;
    setMemberFormData({
      ...memberFormData,
      [name]: value
    });
  };

  // Reset member form
  const resetMemberForm = () => {
    setMemberFormData({
      name: '',
      role: '',
      image: '',
      bio: ''
    });
    setSelectedMember(null);
    setMemberFormMode('add');
  };

  // Handle member form submission
  const handleMemberSubmit = async (e) => {
    e.preventDefault();

    try {
      if (memberFormMode === 'add') {
        await AboutPageService.addTeamMember(memberFormData);
      } else {
        await AboutPageService.updateTeamMember(selectedMember._id, memberFormData);
      }

      resetMemberForm();
      fetchAboutPageData();
    } catch (error) {
      console.error('Error saving team member:', error);
      toast.error('Failed to save team member');
    }
  };

  // Edit team member
  const handleEditMember = (member) => {
    setSelectedMember(member);
    setMemberFormData({
      name: member.name || '',
      role: member.role || '',
      image: member.image || '',
      bio: member.bio || ''
    });
    setMemberFormMode('edit');
  };

  // Delete team member
  const handleDeleteMember = async (id) => {
    if (window.confirm('Are you sure you want to delete this team member?')) {
      try {
        await AboutPageService.deleteTeamMember(id);
        fetchAboutPageData();
      } catch (error) {
        console.error('Error deleting team member:', error);
        toast.error('Failed to delete team member');
      }
    }
  };

  // Handle CTA data input changes
  const handleCtaInputChange = (e) => {
    const { name, value } = e.target;
    setCtaData({
      ...ctaData,
      [name]: value
    });
  };

  // Save CTA data
  const handleSaveCta = async (e) => {
    e.preventDefault();

    try {
      await AboutPageService.updateCta(ctaData);
      toast.success('CTA section updated successfully');
    } catch (error) {
      console.error('Error saving CTA data:', error);
      toast.error('Failed to save CTA data');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-navy mb-8">About Page Management</h1>

      {/* Tabs */}
      <div className="flex border-b mb-8 overflow-x-auto">
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'hero' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('hero')}
        >
          Hero Section
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'mission' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('mission')}
        >
          Mission & Vision
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'team' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('team')}
        >
          Team Section
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'members' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('members')}
        >
          Team Members
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'cta' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('cta')}
        >
          CTA Section
        </button>
      </div>

      {loading ? (
        <div className="text-center py-8">
          <p>Loading...</p>
        </div>
      ) : (
        <>
          {/* Hero Section Tab */}
          {activeTab === 'hero' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Hero Section</h2>

              <form onSubmit={handleSaveHero}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="heroTitle">
                    Title
                  </label>
                  <input
                    type="text"
                    id="heroTitle"
                    name="title"
                    value={heroData.title}
                    onChange={handleHeroInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="heroDescription">
                    Description
                  </label>
                  <textarea
                    id="heroDescription"
                    name="description"
                    value={heroData.description}
                    onChange={handleHeroInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    rows="3"
                    required
                  />
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save Hero Section
                </button>
              </form>
            </div>
          )}

          {/* Mission & Vision Tab */}
          {activeTab === 'mission' && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              {/* Mission Section */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">Mission</h2>

                <form onSubmit={handleSaveMission}>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="missionTitle">
                      Title
                    </label>
                    <input
                      type="text"
                      id="missionTitle"
                      name="title"
                      value={missionData.title}
                      onChange={handleMissionInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="missionDescription1">
                      Description 1
                    </label>
                    <textarea
                      id="missionDescription1"
                      name="description1"
                      value={missionData.description1}
                      onChange={handleMissionInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      rows="3"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="missionDescription2">
                      Description 2
                    </label>
                    <textarea
                      id="missionDescription2"
                      name="description2"
                      value={missionData.description2}
                      onChange={handleMissionInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      rows="3"
                      required
                    />
                  </div>

                  <button
                    type="submit"
                    className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  >
                    <FaSave className="inline mr-2" />
                    Save Mission
                  </button>
                </form>
              </div>

              {/* Vision Section */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">Vision</h2>

                <form onSubmit={handleSaveVision}>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="visionTitle">
                      Title
                    </label>
                    <input
                      type="text"
                      id="visionTitle"
                      name="title"
                      value={visionData.title}
                      onChange={handleVisionInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="visionDescription1">
                      Description 1
                    </label>
                    <textarea
                      id="visionDescription1"
                      name="description1"
                      value={visionData.description1}
                      onChange={handleVisionInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      rows="3"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="visionDescription2">
                      Description 2
                    </label>
                    <textarea
                      id="visionDescription2"
                      name="description2"
                      value={visionData.description2}
                      onChange={handleVisionInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      rows="3"
                      required
                    />
                  </div>

                  <button
                    type="submit"
                    className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  >
                    <FaSave className="inline mr-2" />
                    Save Vision
                  </button>
                </form>
              </div>
            </div>
          )}

          {/* Team Section Tab */}
          {activeTab === 'team' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Team Section</h2>

              <form onSubmit={handleSaveTeamSection}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="teamTitle">
                    Title
                  </label>
                  <input
                    type="text"
                    id="teamTitle"
                    name="title"
                    value={teamSectionData.title}
                    onChange={handleTeamSectionInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="teamDescription">
                    Description
                  </label>
                  <textarea
                    id="teamDescription"
                    name="description"
                    value={teamSectionData.description}
                    onChange={handleTeamSectionInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    rows="3"
                    required
                  />
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save Team Section
                </button>
              </form>
            </div>
          )}

          {/* Team Members Tab */}
          {activeTab === 'members' && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Team Member Form */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">
                  {memberFormMode === 'add' ? 'Add Team Member' : 'Edit Team Member'}
                </h2>

                <form onSubmit={handleMemberSubmit}>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="memberName">
                      Name
                    </label>
                    <input
                      type="text"
                      id="memberName"
                      name="name"
                      value={memberFormData.name}
                      onChange={handleMemberInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="memberRole">
                      Role
                    </label>
                    <input
                      type="text"
                      id="memberRole"
                      name="role"
                      value={memberFormData.role}
                      onChange={handleMemberInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="memberImage">
                      Image URL
                    </label>
                    <input
                      type="url"
                      id="memberImage"
                      name="image"  
                      value={memberFormData.image}
                      onChange={handleMemberInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="memberBio">
                      Bio
                    </label>
                    <textarea
                      id="memberBio"
                      name="bio"
                      value={memberFormData.bio}
                      onChange={handleMemberInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      rows="3"
                      required
                    />
                  </div>

                  {memberFormData.image && (
                    <div className="mb-4">
                      <p className="block text-gray-700 text-sm font-bold mb-2">Image Preview</p>
                      <div className="w-32 h-32 rounded-full overflow-hidden mx-auto">
                        <img 
                          src={memberFormData.image} 
                          alt="Preview" 
                          className="w-full h-full object-cover"
                          onError={(e) => {
                            e.target.onerror = null;
                            e.target.src = 'https://via.placeholder.com/128?text=No+Image';
                          }}
                        />
                      </div>
                    </div>
                  )}

                  <div className="flex items-center justify-between">
                    <button
                      type="submit"
                      className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                      {memberFormMode === 'add' ? 'Add Member' : 'Update Member'}
                    </button>

                    {memberFormMode === 'edit' && (
                      <button
                        type="button"
                        onClick={resetMemberForm}
                        className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      >
                        Cancel
                      </button>
                    )}
                  </div>
                </form>
              </div>

              {/* Team Members List */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">Team Members</h2>

                {teamMembers.length === 0 ? (
                  <p className="text-center py-4">No team members found. Add your first team member!</p>
                ) : (
                  <div className="space-y-4">
                    {teamMembers.map((member, index) => (
                      <div 
                        key={member._id} 
                        className="border rounded-lg p-4 flex items-center justify-between bg-gray-50 hover:bg-gray-100"
                      >
                        <div className="flex items-center space-x-4">
                          <div className="w-12 h-12 rounded-full overflow-hidden bg-gray-200 flex-shrink-0">
                            <img 
                              src={member.image} 
                              alt={member.name} 
                              className="w-full h-full object-cover"
                              onError={(e) => {
                                e.target.onerror = null;
                                e.target.src = 'https://via.placeholder.com/100?text=No+Image';
                              }}
                            />
                          </div>
                          <div>
                            <h3 className="font-semibold">{member.name}</h3>
                            <p className="text-sm text-gray-600">{member.role}</p>
                          </div>
                        </div>

                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => handleEditMember(member)}
                            className="p-2 rounded-full text-blue-600 hover:bg-blue-100"
                            title="Edit"
                          >
                            <FaEdit />
                          </button>
                          <button
                            onClick={() => handleDeleteMember(member._id)}
                            className="p-2 rounded-full text-red-600 hover:bg-red-100"
                            title="Delete"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}

          {/* CTA Section Tab */}
          {activeTab === 'cta' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">CTA Section</h2>

              <form onSubmit={handleSaveCta}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaTitle">
                    Title
                  </label>
                  <input
                    type="text"
                    id="ctaTitle"
                    name="title"
                    value={ctaData.title}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaDescription">
                    Description
                  </label>
                  <textarea
                    id="ctaDescription"
                    name="description"
                    value={ctaData.description}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    rows="3"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaButtonText">
                    Button Text
                  </label>
                  <input
                    type="text"
                    id="ctaButtonText"
                    name="buttonText"
                    value={ctaData.buttonText}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaButtonLink">
                    Button Link
                  </label>
                  <input
                    type="text"
                    id="ctaButtonLink"
                    name="buttonLink"
                    value={ctaData.buttonLink}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    placeholder="e.g. /contact"
                    required
                  />
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save CTA Section
                </button>
              </form>

              {/* Preview */}
              <div className="mt-8">
                <h3 className="text-lg font-semibold mb-2">Preview</h3>
                <div className="bg-navy text-white p-6 rounded-lg text-center">
                  <h2 className="text-xl font-bold mb-2">{ctaData.title}</h2>
                  <p className="mb-4">{ctaData.description}</p>
                  <button className="bg-white text-navy font-bold py-2 px-4 rounded">
                    {ctaData.buttonText}
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default AboutPageManagement;
