import React, { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import { TestimonialsService } from './AdminServices';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import ErrorMessage from '../../components/shared/ErrorMessage';

const TestimonialsManagement = () => {
  const [testimonials, setTestimonials] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedTestimonial, setSelectedTestimonial] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    university: '',
    text: '',
    image: '',
    rating: 5,
    featured: false
  });
  const [isEditing, setIsEditing] = useState(false);

  // Load all testimonials on component mount
  useEffect(() => {
    const loadTestimonials = async () => {
      try {
        setLoading(true);
        const data = await TestimonialsService.getAllTestimonials();
        setTestimonials(data);
        setLoading(false);
      } catch (err) {
        setError(err.message || 'Failed to load testimonials');
        setLoading(false);
      }
    };

    loadTestimonials();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : type === 'number' ? parseInt(value) : value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      if (isEditing && selectedTestimonial) {
        // Update existing testimonial
        const updatedTestimonial = await TestimonialsService.updateTestimonial(selectedTestimonial._id, formData);
        setTestimonials(testimonials.map(testimonial => 
          testimonial._id === updatedTestimonial._id ? updatedTestimonial : testimonial
        ));
        setIsEditing(false);
        setSelectedTestimonial(null);
      } else {
        // Create new testimonial
        const newTestimonial = await TestimonialsService.createTestimonial(formData);
        setTestimonials([...testimonials, newTestimonial]);
      }
      // Reset form
      setFormData({
        name: '',
        university: '',
        text: '',
        image: '',
        rating: 5,
        featured: false
      });
      setLoading(false);
    } catch (error) {
      toast.error(error.message || 'Something went wrong');
      setLoading(false);
    }
  };

  // Handle edit testimonial
  const handleEdit = (testimonial) => {
    setSelectedTestimonial(testimonial);
    setFormData({
      name: testimonial.name,
      university: testimonial.university,
      text: testimonial.text,
      image: testimonial.image,
      rating: testimonial.rating,
      featured: testimonial.featured
    });
    setIsEditing(true);
  };

  // Handle delete testimonial
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this testimonial?')) {
      try {
        setLoading(true);
        await TestimonialsService.deleteTestimonial(id);
        setTestimonials(testimonials.filter(testimonial => testimonial._id !== id));
        setLoading(false);
      } catch (error) {
        toast.error(error.message || 'Failed to delete testimonial');
        setLoading(false);
      }
    }
  };

  // Cancel editing
  const handleCancel = () => {
    setIsEditing(false);
    setSelectedTestimonial(null);
    setFormData({
      name: '',
      university: '',
      text: '',
      image: '',
      rating: 5,
      featured: false
    });
  };

  if (loading && testimonials.length === 0) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} />;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Testimonials Management</h1>

      {/* Testimonial Form */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h2 className="text-xl font-semibold mb-4">
          {isEditing ? 'Edit Testimonial' : 'Add New Testimonial'}
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="name">
                Name
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="university">
                University
              </label>
              <input
                type="text"
                id="university"
                name="university"
                value={formData.university}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4 md:col-span-2">
              <label className="block text-gray-700 mb-2" htmlFor="text">
                Testimonial Text
              </label>
              <textarea
                id="text"
                name="text"
                value={formData.text}
                onChange={handleChange}
                rows="4"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              ></textarea>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="image">
                Image URL
              </label>
              <input
                type="text"
                id="image"
                name="image"
                value={formData.image}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="rating">
                Rating (1-5)
              </label>
              <input
                type="number"
                id="rating"
                name="rating"
                min="1"
                max="5"
                value={formData.rating}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4 flex items-center">
              <input
                type="checkbox"
                id="featured"
                name="featured"
                checked={formData.featured}
                onChange={handleChange}
                className="mr-2"
              />
              <label className="text-gray-700" htmlFor="featured">
                Featured Testimonial
              </label>
            </div>
          </div>
          <div className="flex justify-end mt-4">
            {isEditing && (
              <button
                type="button"
                onClick={handleCancel}
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md mr-2 hover:bg-gray-400"
              >
                Cancel
              </button>
            )}
            <button
              type="submit"
              className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              disabled={loading}
            >
              {loading ? 'Saving...' : isEditing ? 'Update Testimonial' : 'Add Testimonial'}
            </button>
          </div>
        </form>
      </div>

      {/* Testimonials List */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">All Testimonials</h2>
        {testimonials.length === 0 ? (
          <p className="text-gray-500">No testimonials found.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white">
              <thead>
                <tr className="bg-gray-100">
                  <th className="py-2 px-4 text-left">Name</th>
                  <th className="py-2 px-4 text-left">University</th>
                  <th className="py-2 px-4 text-left">Rating</th>
                  <th className="py-2 px-4 text-left">Featured</th>
                  <th className="py-2 px-4 text-left">Actions</th>
                </tr>
              </thead>
              <tbody>
                {testimonials.map((testimonial) => (
                  <tr key={testimonial._id} className="border-t">
                    <td className="py-2 px-4">{testimonial.name}</td>
                    <td className="py-2 px-4">{testimonial.university}</td>
                    <td className="py-2 px-4">{testimonial.rating}</td>
                    <td className="py-2 px-4">
                      {testimonial.featured ? 'Yes' : 'No'}
                    </td>
                    <td className="py-2 px-4">
                      <button
                        onClick={() => handleEdit(testimonial)}
                        className="text-blue-500 hover:text-blue-700 mr-2"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(testimonial._id)}
                        className="text-red-500 hover:text-red-700"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default TestimonialsManagement;
