import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import contentService from '../../services/contentService';

// Async thunks
export const fetchContentBySlug = createAsyncThunk(
  'content/fetchBySlug',
  async (slug, { rejectWithValue }) => {
    try {
      const response = await contentService.getContentBySlug(slug);
      return response;
    } catch (error) {
      return rejectWithValue(error.response?.data || error.message);
    }
  }
);

export const fetchAllContent = createAsyncThunk(
  'content/fetchAll',
  async (filters, { rejectWithValue }) => {
    try {
      const response = await contentService.getAllContent(filters);
      return response;
    } catch (error) {
      return rejectWithValue(error.response?.data || error.message);
    }
  }
);

export const createContent = createAsyncThunk(
  'content/create',
  async (contentData, { getState, rejectWithValue }) => {
    try {
      const { user } = getState().auth;
      return await contentService.createContent(contentData, user.token);
    } catch (error) {
      return rejectWithValue(error.response?.data || error.message);
    }
  }
);

export const updateContent = createAsyncThunk(
  'content/update',
  async ({ id, contentData }, { getState, rejectWithValue }) => {
    try {
      const { user } = getState().auth;
      return await contentService.updateContent(id, contentData, user.token);
    } catch (error) {
      return rejectWithValue(error.response?.data || error.message);
    }
  }
);

export const updateContentSection = createAsyncThunk(
  'content/updateSection',
  async ({ id, sectionName, sectionData }, { getState, rejectWithValue }) => {
    try {
      const { user } = getState().auth;
      return await contentService.updateContentSection(id, sectionName, sectionData, user.token);
    } catch (error) {
      return rejectWithValue(error.response?.data || error.message);
    }
  }
);

export const addSectionItem = createAsyncThunk(
  'content/addSectionItem',
  async ({ id, sectionName, itemData }, { getState, rejectWithValue }) => {
    try {
      const { user } = getState().auth;
      return await contentService.addSectionItem(id, sectionName, itemData, user.token);
    } catch (error) {
      return rejectWithValue(error.response?.data || error.message);
    }
  }
);

const initialState = {
  content: null,
  allContent: [],
  loading: false,
  error: null,
  success: false,
  lastUpdated: null,
};

const contentSlice = createSlice({
  name: 'content',
  initialState,
  reducers: {
    resetContent(state) {
      state.content = null;
      state.loading = false;
      state.error = null;
      state.success = false;
      state.lastUpdated = null;
    },
    resetError(state) {
      state.error = null;
    },
    resetSuccess(state) {
      state.success = false;
    },
  },
  extraReducers(builder) {
    builder
      // Fetch content by slug
      .addCase(fetchContentBySlug.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchContentBySlug.fulfilled, (state, action) => {
        state.loading = false;
        state.content = action.payload;
        state.lastUpdated = new Date().toISOString();
      })
      .addCase(fetchContentBySlug.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Fetch all content
      .addCase(fetchAllContent.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchAllContent.fulfilled, (state, action) => {
        state.loading = false;
        state.allContent = action.payload;
        state.lastUpdated = new Date().toISOString();
      })
      .addCase(fetchAllContent.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Create content
      .addCase(createContent.pending, (state) => {
        state.loading = true;
        state.error = null;
        state.success = false;
      })
      .addCase(createContent.fulfilled, (state, action) => {
        state.loading = false;
        state.success = true;
        state.content = action.payload;
        state.allContent.push(action.payload);
        state.lastUpdated = new Date().toISOString();
      })
      .addCase(createContent.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Update content
      .addCase(updateContent.pending, (state) => {
        state.loading = true;
        state.error = null;
        state.success = false;
      })
      .addCase(updateContent.fulfilled, (state, action) => {
        state.loading = false;
        state.success = true;
        state.content = action.payload;
        state.allContent = state.allContent.map(content =>
          content._id === action.payload._id ? action.payload : content
        );
        state.lastUpdated = new Date().toISOString();
      })
      .addCase(updateContent.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Update section
      .addCase(updateContentSection.pending, (state) => {
        state.loading = true;
        state.error = null;
        state.success = false;
      })
      .addCase(updateContentSection.fulfilled, (state, action) => {
        state.loading = false;
        state.success = true;
        state.content = action.payload;
        state.allContent = state.allContent.map(content =>
          content._id === action.payload._id ? action.payload : content
        );
        state.lastUpdated = new Date().toISOString();
      })
      .addCase(updateContentSection.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Add section item
      .addCase(addSectionItem.pending, (state) => {
        state.loading = true;
        state.error = null;
        state.success = false;
      })
      .addCase(addSectionItem.fulfilled, (state, action) => {
        state.loading = false;
        state.success = true;
        state.content = action.payload;
        state.allContent = state.allContent.map(content =>
          content._id === action.payload._id ? action.payload : content
        );
        state.lastUpdated = new Date().toISOString();
      })
      .addCase(addSectionItem.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export const { resetContent, resetError, resetSuccess } = contentSlice.actions;
export default contentSlice.reducer;
